INSERT INTO `products` VALUES ('1', 'SONATA 2017', '2017_Sonata.png', 'Huyndai Sonata 2017 thêm tính năng, giá rẻ hơn', '<p><a href=\"http://hyundai-thanhcong.vn/san-pham/Sonata\"><img alt=\"http://hyundai-thanhcong.vn/tempfiles/uploads/products/logo/SONATA_logo.png\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/products/logo/SONATA_logo.png\" style=\"height:9.75pt; width:120pt\" /> </a></p>\r\n\r\n<p><img alt=\"http://hyundai-thanhcong.vn/Uploads/Anhdaidien/SONATA.png\" src=\"http://hyundai-thanhcong.vn/Uploads/Anhdaidien/SONATA.png\" style=\"height:195pt; width:439.5pt\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Sự h&agrave;i h&ograve;a giữa vẻ đẹp b&ecirc;n ngo&agrave;i</strong><br />\r\n<strong>v&agrave; sức mạnh b&ecirc;n trong</strong></h2>\r\n\r\n<p><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat2.png\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat2.png\" style=\"height:396.75pt; width:753pt\" /></p>\r\n\r\n<h2><strong>Cụm đ&egrave;n hậu dạng LED</strong></h2>\r\n\r\n<p>Giảm thiểu ti&ecirc;u thụ năng lượng, cụm đ&egrave;n hậu l&agrave; sự kết hợp h&agrave;i h&ograve;a giữa đơn giản v&agrave; hiện đại. Đ&egrave;n hậu dạng LED mang đến tầm nh&igrave;n tốt hơn v&agrave; giảm khả năng va chạm từ ph&iacute;a sau.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat10.jpg\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat10.jpg\" style=\"height:99.75pt; width:229.5pt\" /><br />\r\n	Tay nắm cửa mạ cr&ocirc;m<br />\r\n	Tay nắm cửa mạ cr&ocirc;m mang đến vẻ sang trọng v&agrave; tăng cường độ b&aacute;m.</li>\r\n	<li><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat11.jpg\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat11.jpg\" style=\"height:99.75pt; width:229.5pt\" /><br />\r\n	Xi nhan t&iacute;ch hợp tr&ecirc;n gương chiếu hậu<br />\r\n	Xi nhan t&iacute;ch hợp tr&ecirc;n gương chiếu hậu đảm bảo an to&agrave;n khi chuyển hướng đồng thời mang đến sự sang trọng cho Sonata.</li>\r\n	<li><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat12.jpg\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat12.jpg\" style=\"height:99.75pt; width:229.5pt\" /><br />\r\n	Đ&egrave;n soi bậc l&ecirc;n xuống<br />\r\n	L&agrave; chiếc xe th&acirc;n thiện với người sử dụng, đ&egrave;n soi bậc l&ecirc;n xuống được thiết kế gi&uacute;p chiếu s&aacute;ng cho h&agrave;nh kh&aacute;ch khi bước l&ecirc;n xe.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Ghế l&aacute;i trang bị ti&ecirc;n tiến</strong></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"http://hyundai-thanhcong.vn/Uploads/Anhdaidien/SONATA.png\" src=\"http://hyundai-thanhcong.vn/Uploads/Anhdaidien/SONATA.png\" style=\"height:195pt; width:439.5pt\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Sự h&agrave;i h&ograve;a giữa vẻ đẹp b&ecirc;n ngo&agrave;i</strong><br />\r\n<strong><strong>v&agrave; sức mạnh b&ecirc;n trong</strong></strong></h2>\r\n\r\n<p><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat2.png\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat2.png\" style=\"height:396.75pt; width:753pt\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Cụm đ&egrave;n hậu dạng LED</strong></h2>\r\n\r\n<p>Giảm thiểu ti&ecirc;u thụ năng lượng, cụm đ&egrave;n hậu l&agrave; sự kết hợp h&agrave;i h&ograve;a giữa đơn giản v&agrave; hiện đại. Đ&egrave;n hậu dạng LED mang đến tầm nh&igrave;n tốt hơn v&agrave; giảm khả năng va chạm từ ph&iacute;a sau.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat10.jpg\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat10.jpg\" style=\"height:99.75pt; width:229.5pt\" /><br />\r\n	Tay nắm cửa mạ cr&ocirc;m<br />\r\n	Tay nắm cửa mạ cr&ocirc;m mang đến vẻ sang trọng v&agrave; tăng cường độ b&aacute;m.</li>\r\n	<li><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat11.jpg\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat11.jpg\" style=\"height:99.75pt; width:229.5pt\" /><br />\r\n	Xi nhan t&iacute;ch hợp tr&ecirc;n gương chiếu hậu<br />\r\n	Xi nhan t&iacute;ch hợp tr&ecirc;n gương chiếu hậu đảm bảo an to&agrave;n khi chuyển hướng đồng thời mang đến sự sang trọng cho Sonata.</li>\r\n	<li><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat12.jpg\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/ngoaithat/ngoaithat12.jpg\" style=\"height:99.75pt; width:229.5pt\" /><br />\r\n	Đ&egrave;n soi bậc l&ecirc;n xuống<br />\r\n	L&agrave; chiếc xe th&acirc;n thiện với người sử dụng, đ&egrave;n soi bậc l&ecirc;n xuống được thiết kế gi&uacute;p chiếu s&aacute;ng cho h&agrave;nh kh&aacute;ch khi bước l&ecirc;n xe.</li>\r\n</ul>\r\n\r\n<h2><strong>Ghế l&aacute;i trang bị ti&ecirc;n tiến</strong></h2>\r\n\r\n<p>Hyundai tập trung nghi&ecirc;n cứu về sự ph&acirc;n bổ lực to&agrave;n th&acirc;n của ghế l&aacute;i, qua đ&oacute; cải thiện thiết kế, chất liệu v&agrave; sản xuất, nhằm mang đến cho người l&aacute;i sự thoải m&aacute;i v&agrave; giảm những mệt mỏi tr&ecirc;n những qu&atilde;ng đường d&agrave;i .</p>\r\n\r\n<ul>\r\n	<li><strong>Tựa đầu : Thiết kế thấp v&agrave; đ&agrave;n hồi</strong></li>\r\n	<li><strong>Tựa vai : &Ecirc;m &aacute;i</strong></li>\r\n	<li><strong>Th&agrave;nh ghế : Thiết kế s&acirc;u hơn</strong></li>\r\n	<li><strong>Tựa lưng : Chắc chắn</strong></li>\r\n	<li><strong>Đệm ngồi : Tăng độ d&agrave;y v&agrave; &ecirc;m &aacute;i</strong></li>\r\n</ul>\r\n\r\n<p><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/noithat/noithat2.png\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/noithat/noithat2.png\" style=\"height:450pt; width:753pt\" /></p>\r\n\r\n<ul>\r\n	<li><strong>THIẾT KẾ V&Ocirc; LĂNG</strong>V&ocirc; lăng c&oacute; sự thay đổi về chiều rộng, mang đến sự thoải m&aacute;i cho người l&aacute;i, cải thiện độ b&aacute;m khi cầm v&agrave; vẻ thể thao.</li>\r\n	<li><strong>ĐIỀU KHIỂN &Acirc;M THANH TR&Ecirc;N V&Ocirc; LĂNG</strong>Hệ thống điều khiển &acirc;m thanh được t&iacute;ch hợp tr&ecirc;n v&ocirc; lăng, tiện lợi cho người sử dụng, n&acirc;ng cao t&iacute;nh an to&agrave;n khi l&aacute;i xe.</li>\r\n	<li><strong>ĐIỀU KHIỂN H&Agrave;NH TR&Igrave;NH</strong>Giống như hệ thống điều khiển &acirc;m thanh, c&aacute;c n&uacute;t bấm điều khiển h&agrave;nh tr&igrave;nh được bố tr&iacute; thuận tiện, chỉ với việc di chuyển 1 ng&oacute;n tay.</li>\r\n</ul>\r\n\r\n<p><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/noithat/noithat3.png\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sonata1/noithat/noithat3.png\" style=\"height:450pt; width:753pt\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '1', '1', '6', '41', 'Hàn Quốc', '800000000');
INSERT INTO `products` VALUES ('2', 'ACCENT 5 CỬA 2016', 'ACCENT-5DR.png', '1 PHONG CÁCH', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<a href=\"http://hyundai-thanhcong.vn/san-pham/accent-5-cua\"><img alt=\"http://hyundai-thanhcong.vn/tempfiles/uploads/products/logo/ACCENT-5-CUA_logo.png\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/products/logo/ACCENT-5-CUA_logo.png\" style=\"height:11.25pt; width:157.5pt\" /></a></p>\r\n\r\n<h2><strong>Thiết kế trẻ trung v&agrave; năng động</strong></h2>\r\n\r\n<p>Kế thừa sự th&agrave;nh c&ocirc;ng của mẫu Accent, phi&ecirc;n bản mới được cộng th&ecirc;m phong c&aacute;ch hiện đại của d&ograve;ng xe hatchback. Ứng dụng triết l&yacute; &quot;Đi&ecirc;u khắc d&ograve;ng chảy&quot;, Accent được thiết kế với k&iacute;ch thước d&agrave;i rộng hơn c&ugrave;ng c&aacute;c đường n&eacute;t g&oacute;c cạnh đầy c&aacute; t&iacute;nh. Accent mới l&agrave; sự kết hợp ho&agrave;n hảo giữa thiết kế trẻ trung v&agrave; sự năng động của xe hatchback.</p>\r\n\r\n<p><img alt=\"http://worldwide.hyundai.com/wcm/idc/groups/sgvehiclecontent/@hmc/documents/sitecontent/mdaw/mdgw/~edisp/hw087071.png\" src=\"http://worldwide.hyundai.com/wcm/idc/groups/sgvehiclecontent/@hmc/documents/sitecontent/mdaw/mdgw/~edisp/hw087071.png\" style=\"height:328px; width:867px\" /></p>\r\n\r\n<h2><strong>Đ&egrave;n pha sang trọng</strong><br />\r\nChiếu s&aacute;ng tối đa v&agrave;o ban đ&ecirc;m đồng thời mang đến vẻ sang trọng v&agrave;o ban ng&agrave;y.</h2>\r\n\r\n<p><img alt=\"http://worldwide.hyundai.com/wcm/idc/groups/sgvehiclecontent/@hmc/documents/sitecontent/mdaw/mdcy/~edisp/hw079381.jpg\" src=\"http://worldwide.hyundai.com/wcm/idc/groups/sgvehiclecontent/@hmc/documents/sitecontent/mdaw/mdcy/~edisp/hw079381.jpg\" style=\"height:250px; width:940px\" /><br />\r\n&nbsp;</p>\r\n\r\n<h2><strong>Xe nhỏ, tiện &iacute;ch lớn</strong></h2>\r\n\r\n<p>Hiện đại trong từng chi tiết. Thiết kế với c&aacute;c đường n&eacute;t mềm mại,<br />\r\ncản sau c&ugrave;ng đ&egrave;n chiếu hậu mang đến vẻ ngo&agrave;i sang trọng v&agrave; h&agrave;i h&ograve;a cho Accent.</p>\r\n\r\n<h2><img alt=\"http://worldwide.hyundai.com/wcm/idc/groups/sgvehiclecontent/@hmc/documents/sitecontent/mdaw/mdgw/~edisp/hw087046.png\" src=\"http://worldwide.hyundai.com/wcm/idc/groups/sgvehiclecontent/@hmc/documents/sitecontent/mdaw/mdgw/~edisp/hw087046.png\" style=\"height:407px; width:1004px\" /><strong>....Cụm đèn hậu</strong><br />\r\nPh&iacute;a sau xe nổi bật với đ&egrave;n hậu lớn, ph&ugrave; hợp với thiết kế xe, mang đến &aacute;nh s&aacute;ng tối đa v&agrave;o ban đ&ecirc;m.</h2>\r\n\r\n<p><img alt=\"http://worldwide.hyundai.com/wcm/idc/groups/sgvehiclecontent/@hmc/documents/sitecontent/mdaw/mdcy/~edisp/hw079383.jpg\" src=\"http://worldwide.hyundai.com/wcm/idc/groups/sgvehiclecontent/@hmc/documents/sitecontent/mdaw/mdcy/~edisp/hw079383.jpg\" style=\"height:323px; width:491px\" /></p>\r\n\r\n<h2><strong>Thiết kế thể thao</strong></h2>\r\n\r\n<p>Accent mang đến cảm gi&aacute;c của 1 chiếc coupe với đường m&aacute;i thấp v&agrave; c&aacute;c đường g&acirc;n mạnh mẽ dập nổi dọc th&acirc;n xe.</p>\r\n\r\n<p><img alt=\"http://worldwide.hyundai.com/wcm/idc/groups/sgvehiclecontent/@hmc/documents/sitecontent/mdaw/mdgw/~edisp/hw087047.png\" src=\"http://worldwide.hyundai.com/wcm/idc/groups/sgvehiclecontent/@hmc/documents/sitecontent/mdaw/mdgw/~edisp/hw087047.png\" style=\"height:379px; width:1005px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"http://worldwide.hyundai.com/wcm/idc/groups/sgvehiclecontent/@hmc/documents/sitecontent/mdaw/mdm5/~edisp/hw035090.jpg\" src=\"http://worldwide.hyundai.com/wcm/idc/groups/sgvehiclecontent/@hmc/documents/sitecontent/mdaw/mdm5/~edisp/hw035090.jpg\" style=\"height:340px; width:460px\" /></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Hộp số tự động v&ocirc; cấp</strong><br />\r\n			&Aacute;p suất dầu biến đổi gi&uacute;p việc chuyển số &ecirc;m &aacute;i hơn. Hộp số tự động v&ocirc; cấp mang đến phản ứng nhanh, mạnh mẽ v&agrave; gia tốc lớn.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Hệ thống chống<br />\r\nb&oacute; cứng phanh ABS</strong></h2>\r\n\r\n<p>Khi di chuyển tr&ecirc;n bề mặt trơn trượt, bất kỳ 1 sự chệch hướng nhỏ n&agrave;o cũng sẽ<br />\r\nđược ph&aacute;t hiện bởi cảm biến ABS. Hệ thống sẽ ngay lập tức được k&iacute;ch hoạt,<br />\r\ngiữ cho b&aacute;nh xe kh&ocirc;ng bị kh&oacute;a v&agrave; duy tr&igrave; sự kiểm so&aacute;t</p>\r\n\r\n<p><img alt=\"http://worldwide.hyundai.com/wcm/idc/groups/sgvehiclecontent/@hmc/documents/sitecontent/mdaw/mdgw/~edisp/hw087108.jpg\" src=\"http://worldwide.hyundai.com/wcm/idc/groups/sgvehiclecontent/@hmc/documents/sitecontent/mdaw/mdgw/~edisp/hw087108.jpg\" style=\"height:628px; width:1004px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n', '1', '2', '6', '43', 'Hàn Quốc', '556000000');
INSERT INTO `products` VALUES ('3', 'Grand i10 sedan', 'Hyundai-Grand-i10.png', 'NHẤN NÚT KHỞI ĐỘNG CUỘC  SỐNG MỚI', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<a href=\"http://hyundai-thanhcong.vn/san-pham/grand-i10-sedan\"><img alt=\"http://hyundai-thanhcong.vn/tempfiles/uploads/products/logo/GRAND-I10-SEDAN_logo.png\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/products/logo/GRAND-I10-SEDAN_logo.png\" style=\"height:18pt; width:146.25pt\" /></a></p>\r\n\r\n<h2><strong>Hyundai Grand i10 sedan</strong> sở hữu ngoại h&igrave;nh được n&acirc;ng cấp tinh tế hơn, dựa theo cảm hứng từ <strong>ng&ocirc;n ngữ thiết kế đi&ecirc;u khắc d&ograve;ng chảy</strong> của Hyundai. Chiếc xe thực sự l&agrave; niềm tự h&agrave;o với <strong>phong c&aacute;ch</strong>,<strong> </strong><strong>chất lượng</strong>, <strong>kh&ocirc;ng gian</strong>, <strong>sự tiện nghi</strong>,<strong> </strong><strong>an to&agrave;n</strong><strong> </strong>v&agrave; <strong>thuận tiện</strong>.</h2>\r\n\r\n<h2><strong>Hiện đại v&agrave; ti&ecirc;n tiến</strong></h2>\r\n\r\n<h3><strong><em>Hyundai Grand i10 sedan</em></strong> thu h&uacute;t &aacute;nh mắt của bạn ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n với một sự <em>tinh tế</em> kh&ocirc;ng k&eacute;m phần <em>quyến rũ</em>, hấp dẫn tất cả c&aacute;c xung quanh.</h3>\r\n\r\n<p><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/ngoaithat/ngoaithat1.png\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/ngoaithat/ngoaithat1.png\" style=\"height:592px; width:1160px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Nội thất sang trọng, cao cấp</strong></h2>\r\n\r\n<h3><em><strong>Nội thất của Grand i10 sedan mang phong c&aacute;ch sang trọng nhưng đầy t&iacute;nh thực tế. Khoang l&aacute;i s&aacute;ng v&agrave; rộng r&atilde;i với chất lượng ho&agrave;n thiện cao tạo cho bạn niềm cảm hứng phi&ecirc;u lưu c&ugrave;ng chiếc xe.</strong></em></h3>\r\n\r\n<p><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/noithat/noithat1.jpg\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/noithat/noithat1.jpg\" style=\"height:600px; width:1280px\" /></p>\r\n\r\n<ul>\r\n	<li><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/noithat/noithat2.png\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/noithat/noithat2.png\" style=\"height:133px; width:306px\" /><br />\r\n	<strong>Bảng đồng hồ hiển thị LCD</strong><br />\r\n	Bảng đồng hồ của Grand i10 sedan c&oacute; khả năng th&ocirc;ng b&aacute;o đồng thời nhiều th&ocirc;ng tin vận h&agrave;nh, được thiết kế mang t&iacute;nh thẩm mỹ cao với &aacute;nh s&aacute;ng hiển thị m&agrave;u xanh v&agrave; trắng dịu d&agrave;ng cho mắt.</li>\r\n	<li><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/noithat/noithat3.jpg\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/noithat/noithat3.jpg\" style=\"height:133px; width:306px\" /><br />\r\n	<strong>Hệ thống &acirc;m thanh</strong><br />\r\n	Xe được trang bị hệ thống &acirc;m thanh giải tr&iacute; hỗ trợ Radio/CD/Mp3 c&ugrave;ng kết nối Bluetooth với 1GB bộ nhớ trong đem lại trải nghiệm &acirc;m nhạc v&ocirc; tận.</li>\r\n	<li><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/noithat/noithat4.jpg\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/noithat/noithat4.jpg\" style=\"height:133px; width:306px\" /><br />\r\n	<strong>Tay l&aacute;i trợ lực điều chỉnh g&oacute;c l&aacute;i</strong><br />\r\n	Grand i10 sedan được trang bị tay l&aacute;i trợ lực, c&oacute; thể điều chỉnh được g&oacute;c l&aacute;i n&ecirc;n n&oacute; ph&ugrave; hợp với tất cả mọi người với v&oacute;c d&aacute;ng kh&aacute;c nhau.</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/noithat/noithat5.jpg\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/noithat/noithat5.jpg\" style=\"height:133px; width:306px\" /><br />\r\n	<strong>Đ&egrave;n trần</strong><br />\r\n	Đ&egrave;n trần được tự động k&iacute;ch hoạt khi mở cửa trong điều kiện trời tối, đem đến &aacute;nh s&aacute;ng th&iacute;ch hợp gi&uacute;p bạn c&oacute; thể nh&igrave;n r&otilde; mọi thứ tr&ecirc;n xe.</li>\r\n	<li><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/noithat/noithat6.jpg\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/noithat/noithat6.jpg\" style=\"height:133px; width:306px\" /><br />\r\n	<strong>Hộc để đồ trung t&acirc;m</strong><br />\r\n	Hộc để đồ trung t&acirc;m với k&iacute;ch thước vừa vặn c&ugrave;ng nắp đậy l&agrave; nơi lưu trữ l&iacute; tưởng cho v&iacute;, điện thoại hoặc c&aacute;c thiết bị cầm tay.</li>\r\n	<li><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/noithat/noithat7.jpg\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/noithat/noithat7.jpg\" style=\"height:133px; width:306px\" /><br />\r\n	<strong>T&uacute;i t&agrave;i liệu sau ghế</strong><br />\r\n	T&uacute;i t&agrave;i liệu được thiết kế liền mạch tại mặt lưng ghế h&agrave;ng ghế trước, gi&uacute;p bạn c&oacute; thể để những cuốn s&aacute;ch v&agrave; tạp ch&iacute; y&ecirc;u th&iacute;ch.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Mạnh mẽ m&agrave; vẫn hiệu quả trong ti&ecirc;u hao nhi&ecirc;n liệu</strong></h2>\r\n\r\n<h3>Với <strong><em>Grand i10 sedan</em></strong>, bạn sẽ cảm nhận được sự pha trộn th&uacute; vị của sức mạnh v&agrave; hiệu quả, khả năng xử l&iacute; nhạy b&eacute;n, sự tĩnh lặng trong khoang l&aacute;i c&ugrave;ng với sự tối ưu về mặt kh&iacute; động học. Tất cả đều nằm trong 1 chiếc xe.</h3>\r\n\r\n<table cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/vanhanh/vanhanh1.jpg\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/vanhanh/vanhanh1.jpg\" style=\"height:384px; width:892px\" /></p>\r\n\r\n			<p><strong>(C&ocirc;ng suất tối đa : 83 PS, Lượng kh&iacute; thải : 11.6 kg-m)</strong></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><strong>C&ocirc;ng nghệ an to&agrave;n chủ động</strong></h2>\r\n\r\n<h3><em><strong>Bạn ho&agrave;n to&agrave;n c&oacute; thể đặt niềm tin với khả năng đảm bảo an to&agrave;n của Grand i10 sedan. Hệ thống t&uacute;i kh&iacute; sẽ bảo vệ người l&aacute;i v&agrave; h&agrave;nh kh&aacute;ch khỏi những chấn thương do va chạm</strong></em>.</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p><img alt=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/antoan/antoan1.jpg\" src=\"http://hyundai-thanhcong.vn/Uploads/SanPham/sedan/antoan/antoan1.jpg\" style=\"height:496px; width:1102px\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;\r\n<p>&nbsp;</p>\r\n</p>\r\n', '1', '1', '6', '42', 'Hàn Quốc', '390000000');
INSERT INTO `products` VALUES ('4', 'Audi A3', 'Audi A3 sedan.png', 'Audi A3 mang đến một phong cách tinh tế, thanh lịch', '<p><img alt=\"Tham gia thị trường Việt Nam, Audi A3 chỉ có duy nhất bản sedan (4 cửa),\r\n và thiếu vắng bản coupe (đuôi cụt, 5 cửa), vốn dĩ luôn xuất hiện song \r\nsong ở nhiều thị trường khác trên thế giới - Ảnh: Bobi.&lt;br&gt;\" src=\"https://vneconomy2.vcmedia.vn/thumb_w/660/nCuznW7ccccccccccccJrl8HI36ph/Image/2014/03/a3/3-7cd9c.jpg\" style=\"margin:0px; width:660pxpx\" /></p>\r\n\r\n<h2><strong>Tham gia thị trường Việt Nam, Audi A3 chỉ c&oacute; duy nhất bản sedan (4 cửa), v&agrave; thiếu vắng bản coupe (đu&ocirc;i cụt, 5 cửa), vốn dĩ lu&ocirc;n xuất hiện song song ở nhiều thị trường kh&aacute;c tr&ecirc;n thế giới</strong></h2>\r\n\r\n<p><img alt=\"Audi A3 sedan nhập khẩu và phân phối trong nước có khá nhiều tùy chọn về\r\n công năng sử dụng và trang bị tiên nghi. Tuy nhiên, chiếc sedan này chỉ có một lựa chọn động cơ loại TFSI tăng áp 4 xi-lanh,\r\n phun nhiên liệu trực tiếp, dung tích 1.8 lít công suất 180 mã lực và \r\nmô-men xoắn cực đại 250 Nm - Ảnh: Bobi. \" src=\"https://vneconomy2.vcmedia.vn/thumb_w/660/nCuznW7ccccccccccccJrl8HI36ph/Image/2014/03/a3/2-7cd9c.jpg\" style=\"margin:0px; width:660pxpx\" /></p>\r\n\r\n<h2><strong>Audi A3 sedan nhập khẩu v&agrave; ph&acirc;n phối trong nước c&oacute; kh&aacute; nhiều t&ugrave;y chọn về c&ocirc;ng năng sử dụng v&agrave; trang bị ti&ecirc;n nghi. Tuy nhi&ecirc;n, chiếc sedan n&agrave;y chỉ c&oacute; một lựa chọn động cơ loại TFSI tăng &aacute;p 4 xi-lanh, phun nhi&ecirc;n liệu trực tiếp, dung t&iacute;ch 1.8 l&iacute;t c&ocirc;ng suất 180 m&atilde; lực v&agrave; m&ocirc;-men xoắn cực đại 250 Nm</strong></h2>\r\n\r\n<p><img alt=\"Mặc dù được xếp vào phân khúc xe sedan, nhưng kiểu dáng của Audi A3 lại \r\nkhông giống một chiếc sedan truyền thống - Ảnh: Bobi.&lt;br&gt;\" src=\"https://vneconomy2.vcmedia.vn/thumb_w/660/nCuznW7ccccccccccccJrl8HI36ph/Image/2014/03/a3/1-7cd9c.jpg\" style=\"margin:0px; width:660pxpx\" /></p>\r\n\r\n<h2><strong>Mặc d&ugrave; được xếp v&agrave;o ph&acirc;n kh&uacute;c xe sedan, nhưng kiểu d&aacute;ng của Audi A3 lại kh&ocirc;ng giống một chiếc sedan truyền thống</strong></h2>\r\n\r\n<p><img alt=\"Phía trước A3 sedan giữ nguyên  thiết kế thường thấy ở các \r\ndòng xe Audi khác, với đặc điểm nhận dạng là lưới tản nhiệt hình thang \r\nngược cỡ lớn và cặp đèn pha công nghệ dải LED là thế mạnh của hãng xe \r\nĐức - Ảnh: Bobi. \" src=\"https://vneconomy2.vcmedia.vn/thumb_w/660/nCuznW7ccccccccccccJrl8HI36ph/Image/2014/03/a3/4-7cd9c.jpg\" style=\"margin:0px; width:660pxpx\" /></p>\r\n\r\n<h2><strong>Ph&iacute;a trước A3 sedan giữ nguy&ecirc;n thiết kế thường thấy ở c&aacute;c d&ograve;ng xe Audi kh&aacute;c, với đặc điểm nhận dạng l&agrave; lưới tản nhiệt h&igrave;nh thang ngược cỡ lớn v&agrave; cặp đ&egrave;n pha c&ocirc;ng nghệ dải LED l&agrave; thế mạnh của h&atilde;ng xe Đức</strong></h2>\r\n\r\n<p><img alt=\"Bên trong A3 sedan, các chi tiết được thiết kế tối giản cho phù hợp với \r\nkhông gian của một chiếc xe cỡ nhỏ. Chất liệu nội thất chủ yếu là da và \r\nnhựa tổng hợp cao cấp. Chi tiết mới xuất hiện trên A3 sedan là cửa gió \r\nđiều hòa hình tròn - Ảnh: Bobi.\" src=\"https://vneconomy2.vcmedia.vn/thumb_w/660/nCuznW7ccccccccccccJrl8HI36ph/Image/2014/03/a3/11-7cd9c.jpg\" style=\"margin:0px; width:660pxpx\" /></p>\r\n\r\n<h2><strong>B&ecirc;n trong A3 sedan, c&aacute;c chi tiết được thiết kế tối giản cho ph&ugrave; hợp với kh&ocirc;ng gian của một chiếc xe cỡ nhỏ. Chất liệu nội thất chủ yếu l&agrave; da v&agrave; nhựa tổng hợp cao cấp. Chi tiết mới xuất hiện tr&ecirc;n A3 sedan l&agrave; cửa gi&oacute; điều h&ograve;a h&igrave;nh tr&ograve;n</strong></h2>\r\n\r\n<p><img alt=\"Audi A3 sedan tiêu chuẩn nhập về Việt Nam đều có trang bị giao diện \r\nMulti Media Interface (MMI) điều khiển qua màn hình thu gập bằng điện ở \r\nchính giữa táp-lô, giao diện âm nhạc Audi Music Interface kết nối bằng \r\ndây với iPod, đầu đọc DVD, điều hòa tự động hai vùng khí, 14 loa công \r\nsuất 705 W và hàng ghế trước chỉnh điện.&lt;br&gt;&lt;br&gt;Tuy nhiên, xe không được trang bị cửa sổ trời và vẫn tiếp tục sử dụng chức năng khởi động bằng chìa khóa - Ảnh: Bobi.\" src=\"https://vneconomy2.vcmedia.vn/thumb_w/660/nCuznW7ccccccccccccJrl8HI36ph/Image/2014/03/a3/10-7cd9c.jpg\" style=\"margin:0px; width:660pxpx\" /></p>\r\n\r\n<h2>Audi A3 sedan ti&ecirc;u chuẩn nhập về Việt Nam đều c&oacute; trang bị giao diện Multi Media Interface (MMI) điều khiển qua m&agrave;n h&igrave;nh thu gập bằng điện ở ch&iacute;nh giữa t&aacute;p-l&ocirc;, giao diện &acirc;m nhạc Audi Music Interface kết nối bằng d&acirc;y với iPod, đầu đọc DVD, điều h&ograve;a tự động hai v&ugrave;ng kh&iacute;, 14 loa c&ocirc;ng suất 705 W v&agrave; h&agrave;ng ghế trước chỉnh điện.<br />\r\n<br />\r\nTuy nhi&ecirc;n, xe kh&ocirc;ng được trang bị cửa sổ trời v&agrave; vẫn tiếp tục sử dụng chức năng khởi động bằng ch&igrave;a kh&oacute;a</h2>\r\n\r\n<p><img alt=\"Hệ thống an toàn tiêu chuẩn gồm 10 túi khí bảo vệ cho toàn bộ hành \r\nkhách, cảnh báo áp suất lốp điện tử và phanh đỗ cơ điện, kiểm soát cân \r\nbằng và khóa vi sai điện tử, phanh ABS với đĩa phanh trước được thiết kế\r\n các khe tản nhiệt bên trong - Ảnh: Bobi.&lt;br&gt;\" src=\"https://vneconomy2.vcmedia.vn/thumb_w/660/nCuznW7ccccccccccccJrl8HI36ph/Image/2014/03/a3/9-7cd9c.jpg\" style=\"margin:0px; width:660pxpx\" /></p>\r\n\r\n<h2><strong>Hệ thống an to&agrave;n ti&ecirc;u chuẩn gồm 10 t&uacute;i kh&iacute; bảo vệ cho to&agrave;n bộ h&agrave;nh kh&aacute;ch, cảnh b&aacute;o &aacute;p suất lốp điện tử v&agrave; phanh đỗ cơ điện, kiểm so&aacute;t c&acirc;n bằng v&agrave; kh&oacute;a vi sai điện tử, phanh ABS với đĩa phanh trước được thiết kế c&aacute;c khe tản nhiệt b&ecirc;n trong&nbsp;</strong></h2>\r\n\r\n<p><img alt=\"Cũng giống A4 và Q3, Audi A3 không sử dụng hệ dẫn động 4 bánh Quatro, mà\r\n chỉ được trang bị hệ dẫn động cầu trước - Ảnh: Bobi. &lt;br&gt;\" src=\"https://vneconomy2.vcmedia.vn/thumb_w/660/nCuznW7ccccccccccccJrl8HI36ph/Image/2014/03/a3/8-7cd9c.jpg\" style=\"margin:0px; width:660pxpx\" /></p>\r\n\r\n<h2><strong>Cũng giống A4 v&agrave; Q3, Audi A3 kh&ocirc;ng sử dụng hệ dẫn động 4 b&aacute;nh Quatro, m&agrave; chỉ được trang bị hệ dẫn động cầu trước</strong></h2>\r\n\r\n<p><img alt=\"Audi A3 sedan có thể vận hành ở tốc độ tối đa 235 km/giờ và mất 7,3 giây\r\n để tăng tốc từ 0 lên 100 km/giờ. Mức tiêu hao nhiên liệu trung bình được công bố là 5,6\r\n lít/100 km - Ảnh: Bobi. \" src=\"https://vneconomy2.vcmedia.vn/thumb_w/660/nCuznW7ccccccccccccJrl8HI36ph/Image/2014/03/a3/7-7cd9c.jpg\" style=\"margin:0px; width:660pxpx\" /></p>\r\n\r\n<h2><strong>Audi A3 sedan c&oacute; thể vận h&agrave;nh ở tốc độ tối đa 235 km/giờ v&agrave; mất 7,3 gi&acirc;y để tăng tốc từ 0 l&ecirc;n 100 km/giờ. Mức ti&ecirc;u hao nhi&ecirc;n liệu trung b&igrave;nh được c&ocirc;ng bố l&agrave; 5,6 l&iacute;t/100 km</strong></h2>\r\n\r\n<p><img alt=\"Xe có chiều dài tổng thể 4.460 mm, rộng 1.800 mm và cao 1.420 mm. Trục \r\ncơ sở dài 2.640 mm và thể tích khoang hành lý 425 lít có thể tùy biến \r\nbằng cách gập tựa lưng hàng ghế sau - Ảnh: Bobi.&lt;br&gt;\" src=\"https://vneconomy2.vcmedia.vn/thumb_w/660/nCuznW7ccccccccccccJrl8HI36ph/Image/2014/03/a3/5-7cd9c.jpg\" style=\"margin:0px; width:660pxpx\" /></p>\r\n\r\n<h2><strong>Xe c&oacute; chiều d&agrave;i tổng thể 4.460 mm, rộng 1.800 mm v&agrave; cao 1.420 mm. Trục cơ sở d&agrave;i 2.640 mm v&agrave; thể t&iacute;ch khoang h&agrave;nh l&yacute; 425 l&iacute;t c&oacute; thể t&ugrave;y biến bằng c&aacute;ch gập tựa lưng h&agrave;ng ghế sau</strong></h2>\r\n\r\n<p><img alt=\"Tại thị trường Việt Nam, Audi A3 sedan có giá bán sau thuế là 1,395 tỷ \r\nđồng cùng thời gian bảo hành 3 năm không giới hạn km - Ảnh: Bobi.\" src=\"https://vneconomy2.vcmedia.vn/thumb_w/660/nCuznW7ccccccccccccJrl8HI36ph/Image/2014/03/a3/6-7cd9c.jpg\" style=\"margin:0px; width:660pxpx\" /></p>\r\n\r\n<h2><strong>Tại thị trường Việt Nam, Audi A3 sedan c&oacute; gi&aacute; b&aacute;n sau thuế l&agrave; 1,395 tỷ đồng c&ugrave;ng thời gian bảo h&agrave;nh 3 năm kh&ocirc;ng giới hạn km</strong></h2>\r\n', '1', '1', '1', '37', 'Đức', '1395000000');
INSERT INTO `products` VALUES ('5', 'Audi Q7 2016', 'Audi Q7 2016.png', '           Nội thất sang trọng đẳng cấp và rộng rãi nhất trong phân khúc, hệ thống lái năng động và tiết kiệm nhiên liệu hiệu quả, chiếc SUV thể thao Audi Q7 thế hệ mới thiết lập nên những tiêu chuẩn mới.', '<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>\r\n	<h3><strong>Audi Q7 2.0 TFSI: gi&aacute; khởi điểm chưa bao gồm t&ugrave;y chọn: 2.999.000.000 VNĐ</strong></h3>\r\n	</li>\r\n	<li>\r\n	<h3><strong>Audi Q7 3.0 TFSI: gi&aacute; khởi điểm chưa bao gồm t&ugrave;y chọn: 3.450.000.000 VNĐ</strong></h3>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201542/1_6100.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201542/1_6100.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p>Nhờ tiết kiệm 240kg v&agrave; giảm 28% mức ti&ecirc;u thụ nhi&ecirc;n liệu so với thế hệ tiền nhiệm, Audi Q7 thể thao mới ch&iacute;nh l&agrave; minh chứng cho phương ch&acirc;m Ti&ecirc;n phong trong c&ocirc;ng nghệ bởi sự thể hiện xuất sắc tr&ecirc;n con đường dẫn tới th&agrave;nh c&ocirc;ng&ldquo;, &ocirc;ng Laurent Genet &ndash; Tổng gi&aacute;m đốc nhập khẩu Audi tại Việt Nam chia sẻ. Với trọng lượng nhẹ, phần th&acirc;n xe đa vật liệu v&agrave; hệ thống khung gầm ho&agrave;n to&agrave;n mới gi&uacute;p mang đến cảm gi&aacute;c thoải m&aacute;i tuyệt đối trong khi vẫn đảm bảo khả năng vận h&agrave;nh của một chiếc Audi thể thao với mức kh&iacute; thải CO2 thấp nhất.</p>\r\n\r\n<p>Audi Q7 mới sở hữu k&iacute;ch thước tổng thể d&agrave;i x rộng x cao tương ứng 5.050 x 2.100 x 1.740 (mm) chiều d&agrave;i cơ sở của xe đạt 2.990 mm, chiếc SUV ngắn hơn 37mm nhưng vẫn giữ chiều cao so với phi&ecirc;n bản cũ. Tỉ lệ n&agrave;y của chiếc Audi Q7 mới h&agrave;i h&ograve;a hơn, với c&aacute;c trụ gi&uacute;p tăng kh&ocirc;ng gian v&agrave; độ c&acirc;n bằng. C&aacute;c đường g&acirc;n quanh th&acirc;n xe sắc n&eacute;t to&aacute;t l&ecirc;n sự tinh tế, thể thao, mặc d&ugrave; to&agrave;n bộ vỏ xe đều l&agrave;m từ hợp kim nh&ocirc;m</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201541/audi-q7-2016_7909.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201541/audi-q7-2016_7909.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p>Thiết kế của Audi Q7 được l&agrave;m mới nhờ lưới tản nhiệt khung đơn nổi bật với c&aacute;c thanh ngang bằng hợp kim nh&ocirc;m s&aacute;ng b&oacute;ng được thiết kế rộng hơn v&agrave; nằm thấp hơn so với phi&ecirc;n bản trước với phần th&acirc;n nổi 3D nổi bật phần mũi xe; Đ&egrave;n pha được li&ecirc;n kết với lưới tản nhiệt khung đơn tạo ấn tượng mạnh mẽ v&agrave; chiều s&acirc;u. Dải đ&egrave;n chạy chiếu s&aacute;ng ban ng&agrave;y t&iacute;ch hợp đ&egrave;n xi nhan dạng mũi t&ecirc;n k&eacute;p tạo n&ecirc;n điểm nhấn mới v&agrave; độc đ&aacute;o ri&ecirc;ng cho mẫu xe trong đại gia đ&igrave;nh Audi. Audi cũng trang bị đ&egrave;n xi-nhan sau dạng tia &ndash; c&aacute;c th&agrave;nh phần trong dải tia n&agrave;y được k&iacute;ch hoạt s&aacute;ng lần lượt rất nhanh từ trong ra ngo&agrave;i</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201541/audi-q7-2016-12_9802.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201541/audi-q7-2016-12_9802.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p>Audi cho ph&eacute;p chiếc Q7 mới 11 m&agrave;u tuỳ chọn với 5 m&agrave;u ho&agrave;n to&agrave;n mới. C&aacute;c m&agrave;u đơn sắc gồm trắng Carrara v&agrave; đen Night Black, c&aacute;c m&agrave;u &aacute;nh kim gồm c&oacute; n&acirc;u Argus, be Carat, bạc Floret, trắng Glacier, x&aacute;m Graphite, đen Orca, xanh Ink v&agrave; trắng Tofana. G&oacute;i ngoại thất S line c&oacute; th&ecirc;m m&agrave;u x&aacute;m Daytona. Audi cũng cho ph&eacute;p kh&aacute;ch h&agrave;ng c&oacute; thể tự do tuỳ chọn v&agrave; đặt những m&agrave;u y&ecirc;u th&iacute;ch kh&aacute;c cho việc c&aacute; nh&acirc;n ho&aacute; chiếc xe của m&igrave;nh.</p>\r\n\r\n<p>Với tuỳ chọn g&oacute;i ngoại thất S line, c&aacute;c cản xe, viền cửa v&agrave; c&aacute;nh gi&oacute; sẽ được nhấn mạnh. C&aacute;c hốc gi&oacute; ph&iacute;a trước được nối liền với nhau, ph&iacute;a sau, ốp dưới sẽ c&oacute; dạng lưới. c&aacute;c thanh ngang lưới tản nhiệt v&agrave; tr&ecirc;n hốc gi&oacute; trước được ho&agrave;n thiện b&oacute;ng với m&agrave;u x&aacute;m đậm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201541/audi-q7-2016-45_198.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201541/audi-q7-2016-45_198.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Q7 thế hệ mới được trang bị cả phi&ecirc;n bản động cơ 2.0L v&agrave; 3.0L. Trong đ&oacute;, phi&ecirc;n bản động cơ V6 3.0 TFSI mới với nhiều cải tiến mang đến khả năng vận h&agrave;nh v&agrave; tiết kiệm nhi&ecirc;n liệu tốt hơn. Khối động cơ n&agrave;y sản sinh c&ocirc;ng suất 333 m&atilde; lực gi&uacute;p chiếc Audi Q7 mới chỉ mất 6,3 gi&acirc;y để tăng tốc từ 0 tới 100km/h, nhanh hơn 0,6 gi&acirc;y so với phi&ecirc;n bản Q7 trước. Tốc độ tối đa của xe đạt 250km/h. Động cơ 3.0 TFSI chỉ ti&ecirc;u thụ 7.9 l&iacute;t/100km &ndash; mức ti&ecirc;u thụ nhi&ecirc;n liệu giảm 28% so với Q7 thế hệ trước &ndash; v&agrave; chỉ thải 179 gram CO2/km.</p>\r\n\r\n<p>Audi Q7 thế hệ mới tạo ấn tượng với hệ dẫn động bốn b&aacute;nh quattro của Audi c&oacute; khả năng đ&aacute;nh l&aacute;i cả tr&ecirc;n 4 b&aacute;nh xe. Hệ thống l&aacute;i bốn b&aacute;nh khiến cho mẫu SUV full-size c&oacute; b&aacute;n k&iacute;nh v&ograve;ng quay nhỏ hơn khi ở tốc độ thấp từ 8 tới 10 km/h, dễ d&agrave;ng v&agrave;o cua hơn ở tốc độ cao. C&ocirc;ng nghệ quattro thế hệ mới nhất cũng được trang bị tr&ecirc;n Audi Q7 mới. Audi l&agrave; thương hiệu xe sang dẫn đầu về hệ dẫn động bốn b&aacute;nh tr&ecirc;n to&agrave;n cầu với 30% c&aacute;c mẫu xe Audi được trang bị quattro. Tại Việt Nam, 80% xe Audi được b&aacute;n ra c&oacute; trang bị quattro. Với phi&ecirc;n bản Audi Q7 mới, Việt Nam ch&iacute;nh thức trở th&agrave;nh v&ugrave;ng đất của thế hệ quattro đỉnh cao.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201541/audi-q7-2016-213_2431.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201541/audi-q7-2016-213_2431.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p>Nội thất Audi Q7 mới đem đến một kh&ocirc;ng gian rộng mở, kho&aacute;ng đạt như một ph&ograve;ng kh&aacute;ch hạng sang. Thiết kế mới đ&atilde; gi&uacute;p mẫu xe n&agrave;y ghi điểm với hội đồng thiết kế Đức tại Triển l&atilde;m IAA 2015 (Frankfurt Motor Show 2015) gi&uacute;p xe đạt giải thưởng &ldquo;Best of the Best&rdquo; trong hạng mục n&ocirc;i thất xe sang &ldquo;Interior Premium Brand&rdquo; cho thiết kế khoang l&aacute;i. Đ&acirc;y cũng l&agrave; chiếc SUV rộng nhất trong c&ugrave;ng ph&acirc;n kh&uacute;c. Kh&ocirc;ng gian để ch&acirc;n, vai v&agrave; đầu (th&ecirc;m 41mm cho h&agrave;ng ghế trước) rộng hơn kh&aacute; nhiều so với phi&ecirc;n bản trước.</p>\r\n\r\n<p>Q7 được trang bị ghế ngồi thiết kế mới ho&agrave;n to&agrave;n với c&aacute;c ghế trước chỉnh điện, hỗ trợ tựa lưng kết hợp tựa tay, tuỳ chọn ghế l&aacute;i nhớ vị tr&iacute;. C&aacute;c tựa đầu c&oacute; thể điều chỉnh kh&ocirc;ng chỉ theo chiều dọc, m&agrave; c&ograve;n c&oacute; thể điều chỉnh khoảng c&aacute;ch với đầu. H&agrave;ng ghế thứ hai cũng thuộc hạng thoải m&aacute;i v&agrave; tiện nghi nhất trong c&ugrave;ng ph&acirc;n kh&uacute;c. Tựa lưng ba phần được chia theo tỉ lệ 35:30:35 c&oacute; thể thay đổi độ nghi&ecirc;ng 16 độ. H&agrave;ng ghế thứ ba với tỉ lệ 50:50, cho ph&eacute;p điều chỉnh dựng thẳng lưng ghế hoặc gập hết xuống s&agrave;n khoang chứa đồ.</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201542/audi-q7-2016-65453_5198.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201542/audi-q7-2016-65453_5198.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p>Những trang bị đ&aacute;ng ch&uacute; &yacute; tr&ecirc;n Q7 thế hệ mới bao gồm:Hệ thống giải tr&iacute; đa phương tiện MMI radio plus gồm hệ thống &acirc;m thanh Audi, ra lệnh giọng n&oacute;i, kết nối &acirc;m nhạc Audi với 2 cổng USB v&agrave; kết nối Bluetooth. M&agrave;n h&igrave;nh MMI 7 inch th&ograve; ra thụt v&agrave;o từ mặt tap-l&ocirc;. N&uacute;t điều khiển to xoay/ấn được thiết kế ở trung t&acirc;m của bảng điều khiển MMI, 8 n&uacute;t ri&ecirc;ng biệt ph&iacute;a tr&ecirc;n b&agrave;n cảm ứng để c&agrave;i đặt c&aacute;c chương tr&igrave;nh radio ưa th&iacute;ch. Trang bị hệ thống sạc kh&ocirc;ng d&acirc;y theo ti&ecirc;u chuẩn Qi. T&ugrave;y chọn hệ thống &acirc;m thanh Bose v&agrave; Bang &amp; Olufsen nổi bật d&agrave;n &acirc;m thanh 3D mới. Phần tựa lưng của h&agrave;ng ghế trước được thiết kế gắn một m&agrave;n h&igrave;nh Audi với c&aacute;c chức năng linh hoạt như một hệ thống giải tr&iacute; cho h&agrave;ng ghế sau...</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201541/audi-q7-2016-678_5864.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201541/audi-q7-2016-678_5864.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p>Audi Q7 mới được trang bị nhiều t&iacute;nh năng an to&agrave;n hiện đại đ&aacute;p ứng y&ecirc;u cầu khắc khe của người ti&ecirc;u d&ugrave;ng. Trong đ&oacute; đ&aacute;ng ch&uacute; &yacute; l&agrave; hệ thống an to&agrave;n tiền chẩn đo&aacute;n Audi gi&uacute;p ngăn ngừa những t&igrave;nh huống nguy hiểm xảy ra; Hệ thống kiểm so&aacute;t h&agrave;nh tr&igrave;nh chủ động (ACC) gi&uacute;p Audi Q7 mới duy tr&igrave; khoảng c&aacute;ch an to&agrave;n với c&aacute;c phương tiện ph&iacute;a trước, hệ thống sử dụng hai cảm biến rada, hoạt động với dải vận tốc của xe từ 0 đến 250 km/h. Chức năng Stop&amp;Go cho ph&eacute;p chiếc Q7 dừng ho&agrave;n to&agrave;n v&agrave; lăn b&aacute;nh trở lại một c&aacute;ch tự động theo y&ecirc;u cầu của người l&aacute;i. Nếu hệ thống ACC kh&ocirc;ng được k&iacute;ch hoạt, từ tốc độ 60 km/h trở l&ecirc;n, xe sẽ th&ocirc;ng b&aacute;o khoảng c&aacute;ch tới xe ph&iacute;a trước v&agrave; cảnh b&aacute;o va chạm. Người điều khiển c&oacute; thể thiết lập gi&aacute; trị cho khoảng c&aacute;ch n&agrave;y...</p>\r\n\r\n<p>Cũng như tất cả c&aacute;c mẫu xe Audi ph&acirc;n phối ch&iacute;nh thức tại Việt Nam, Audi Q7 mới được bảo h&agrave;nh 3 năm kh&ocirc;ng giới hạn km. Audi Q7 mới sẽ được ph&acirc;n phối tại Audi Đ&agrave; Nẵng, Audi H&agrave; Nội v&agrave; Audi Hồ Ch&iacute; Minh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '1', '3', '1', '38', 'Đức', '2999000000');
INSERT INTO `products` VALUES ('6', 'BMW M3 Sedan', '2015-bmw-m3-sedan-White.jpg', 'Thế hệ thứ 5 của M3 với động cơ 3 lít đạt công suất tối đa 431 mã lực.', '<h1>&nbsp;</h1>\r\n\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong></p>\r\n\r\n<p><a href=\"http://giaxe.bmw.vn/media/posts/car_gallery/1423045939.png\"><img src=\"http://giaxe.bmw.vn/media/posts/car_gallery/1423045939.png\" style=\"height:auto; margin:0px\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"BMW M3 Sedan hoàn toàn mới\" src=\"http://giaxe.bmw.vn/media/posts/car_info/1423044433.jpg\" style=\"float:right; height:auto; margin:0px 0px 5px 60px; width:420px\" /></p>\r\n\r\n<h2><strong>BMW M3 Sedan ho&agrave;n to&agrave;n mới</strong></h2>\r\n\r\n<p>Nh&igrave;n từ ph&iacute;a trước, M3 sở hữu ngoại h&igrave;nh hầm hố với thiết kế nan lưới tản nhiệt h&igrave;nh thận k&eacute;p đặc trưng. Ca-p&ocirc; với thiết kế hơi nh&ocirc; l&ecirc;n ngay phần động cơ như cảnh b&aacute;o sức mạnh tiềm ẩn, ca-p&ocirc; xe được cấu th&agrave;nh từ chất liệu aluminium gi&uacute;p tiết kiệm trọng lượng đến 8kg! Hốc tho&aacute;t gi&oacute; (Air Curtain) tr&ecirc;n cản trước được thiết kế dẫn luồng gi&oacute; từ trước v&agrave; giải ph&oacute;ng ra sau ở Khe tho&aacute;t gi&oacute; (Air Breather) gi&uacute;p tăng cường t&iacute;nh kh&iacute; động học.</p>\r\n\r\n<p>Bằng việc sử dụng c&aacute;c vật liệu trọng lượng nhẹ, như nhựa tổng hợp gia cố sợi carbon (CFRP), v&agrave; nh&ocirc;m aluminium cho c&aacute;c th&agrave;nh phần của khung gầm, th&acirc;n xe. Mui xe Carbon được trang bị ti&ecirc;u chuẩn. Nhờ thế gi&uacute;p tiết kiệm trọng lượng tổng thể cho M3 đến 80kg!</p>\r\n\r\n<p><img alt=\"Khả năng vận hành hoàn hảo\" src=\"http://giaxe.bmw.vn/media/posts/car_info/1423044557.jpg\" style=\"float:left; height:auto; margin:0px 60px 5px 0px; width:420px\" /></p>\r\n\r\n<p><strong>Khả năng vận h&agrave;nh ho&agrave;n hảo</strong></p>\r\n\r\n<p>Một trong những mục ti&ecirc;u quan trọng nhất khi ph&aacute;t triển BMW M3 đ&oacute; ch&iacute;nh l&agrave; việc đảm bảo cho chiếc xe c&oacute; khả năng vận h&agrave;nh ho&agrave;n hảo tr&ecirc;n đường đua. Ch&iacute;nh v&igrave; thế, hệ thống l&agrave;m m&aacute;t (cooling system) chuy&ecirc;n dụng được tối ưu nhằm chắc chắn nhiệt độ của động cơ, turbo tăng &aacute;p v&agrave; hộp số lu&ocirc;n được c&acirc;n bằng ở mức tối ưu.</p>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; TH&Ocirc;NG SỐ KỸ THUẬT</strong></h2>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"technical\" src=\"http://giaxe.bmw.vn/media/posts/car_info/1423044652.jpg\" style=\"height:auto; margin:0px; width:480px\" /></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Động cơ/Dung t&iacute;ch (cc) 2.979cc</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;C&ocirc;ng suất cực đại (kw(hp)/tại v&ograve;ng ph&uacute;t) 431 hp tại 5.500 &ndash; 7.300</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thời gian tăng tốc 0-100 km/h (gi&acirc;y) 4,3</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Vận tốc tối đa (Km/h) 250</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Ti&ecirc;u hao nhi&ecirc;n liệu (l/100km) 8,3 l&iacute;t/ 100km</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; K&iacute;ch thước (D&agrave;i x Rộng x Cao) (mm) 4671x1877x1430</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; NGOẠI THẤT</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</h2>\r\n\r\n<h2><a href=\"http://giaxe.bmw.vn/media/posts/car_info/1423045508669887.jpg\">&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"HỘP SỐ LY HỢP KÉP DCT 7-CẤP\" src=\"http://giaxe.bmw.vn/media/posts/car_info/1423045508669887.jpg\" style=\"height:auto; margin:0px; width:960px\" /></a></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; HỘP SỐ LY HỢP K&Eacute;P DCT 7-CẤP</strong></h2>\r\n\r\n<p>Ngay khi bạn nhấn ga tiến về ph&iacute;a trước, th&igrave; hộp số đ&atilde; lựa chọn cấp số tiếp theo. Nếu bạn muốn trải nghiệm cảm gi&aacute;c xe đua thực thụ th&igrave; Hộp số ly hợp k&eacute;p DCT sẽ mang đến đường đua một c&aacute;ch nhanh nhất. Hộp số n&agrave;y cho ph&eacute;p bạn c&oacute; thể chuyển số m&agrave; vẫn giữ ch&acirc;n ga. V&agrave; khả năng chuyển số nhanh đến mức bạn kh&ocirc;ng thể cảm nhận thấy bất k&igrave; độ trễ n&agrave;o. Đ&acirc;y cũng l&agrave; c&ocirc;ng nghệ đang được &aacute;p dụng cho d&ograve;ng xe đua F1</p>\r\n\r\n<h2><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; NỘI THẤT</strong></h2>\r\n\r\n<p><a href=\"http://giaxe.bmw.vn/media/posts/car_info/1423045184457990.jpg\">&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"TRANG BỊ NỘI THẤT ẤN TƯỢNG\" src=\"http://giaxe.bmw.vn/media/posts/car_info/1423045184457990.jpg\" style=\"height:auto; margin:0px; width:960px\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;TRANG BỊ NỘI THẤT ẤN TƯỢNG</strong></h2>\r\n\r\n<p>Ngo&agrave;i ra, BMW M3 được trang bị bộ m&acirc;m M 19&rdquo; hợp kim thể thao kiểu nan k&eacute;p. Nội thất m&agrave;u cam Sakhir tạo sự tương phản ấn tượng với ngoại thất, được bọc da Merino cao cấp, ghế ngồi thể thao với thiết kế &ocirc;m s&aacute;t, c&aacute;c chi tiết nội thất được ốp carbon, với c&aacute;c đường viền Chrome đen b&oacute;ng l&agrave;m điểm nhấn. Kh&aacute;ch h&agrave;ng s&agrave;nh trải nghiệm c&oacute; thể ho&agrave;n to&agrave;n h&agrave;i l&ograve;ng với Hệ thống &acirc;m thanh 16-loa Harman Kardon.</p>\r\n', '1', '1', '2', '40', 'Đức', '3798000000');
INSERT INTO `products` VALUES ('7', 'BMW 218i GT', 'BWM 218i Gran Tourer.png', 'BMW 2-Series Gran Tourer - MPV 7 chỗ hạng sang cho gia đình', '<table align=\"center\" cellspacing=\"0\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"background-color:transparent\"><img alt=\"Chi tiet xe gia dinh 7 cho gia mem BMW vua ban o VN hinh anh 1\" src=\"http://znews-photo.d.za.zdn.vn/w660/Uploaded/mfssa/2016_04_04/bmwg2_1.jpg\" style=\"height:auto; margin:0px; width:660px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:transparent\">\r\n			<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Th&acirc;n xe thiết kế d&agrave;i hơn phi&ecirc;n bản Active Tourer đ&atilde; ra mắt trước đ&oacute;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" cellspacing=\"0\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"background-color:transparent\"><img alt=\"Chi tiet xe gia dinh 7 cho gia mem BMW vua ban o VN hinh anh 2\" src=\"http://znews-photo.d.za.zdn.vn/w660/Uploaded/mfssa/2016_04_04/bmwg2_3.jpg\" style=\"height:auto; margin:0px; width:660px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:transparent\">\r\n			<p>Chiếc MPV c&oacute; k&iacute;ch thước d&agrave;i x rộng x cao lần lượt l&agrave; 4.556 x 1.800 x 1.647 mm. Ở ph&iacute;a trước, 3 biểu tượng đặc trưng BMW l&agrave; lưới tản nhiệt k&eacute;p với thiết kế truyền thống, đ&egrave;n pha LEP k&eacute;p v&agrave; logo thương hiệu BMW li&ecirc;n kết một c&aacute;ch r&otilde; r&agrave;ng.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" cellspacing=\"0\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"background-color:transparent\"><img alt=\"Chi tiet xe gia dinh 7 cho gia mem BMW vua ban o VN hinh anh 3\" src=\"http://znews-photo.d.za.zdn.vn/w660/Uploaded/mfssa/2016_04_04/bmwg2_5.jpg\" style=\"height:auto; margin:0px; width:660px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:transparent\">\r\n			<p>&nbsp; &nbsp; &nbsp; &nbsp; Đ&egrave;n pha Full LED cho khả năng chiếu xe 300m được trang bị sẵn tr&ecirc;n BMW - 128i Gran Tourer 7 chỗ</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" cellspacing=\"0\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"background-color:transparent\"><img alt=\"Chi tiet xe gia dinh 7 cho gia mem BMW vua ban o VN hinh anh 4\" src=\"http://znews-photo.d.za.zdn.vn/w660/Uploaded/mfssa/2016_04_04/bmwg2_2.jpg\" style=\"height:auto; margin:0px; width:660px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:transparent\">\r\n			<p>Ở ph&iacute;a sau, đ&egrave;n đu&ocirc;i c&oacute; k&iacute;ch thước lớn mở rộng ra 2 b&ecirc;n với nhiều chi tiết đ&egrave;n LED dạng ống được thiết kế c&aacute;ch điệu dạng chữ L.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" cellspacing=\"0\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"background-color:transparent\"><img alt=\"Chi tiet xe gia dinh 7 cho gia mem BMW vua ban o VN hinh anh 5\" src=\"http://znews-photo.d.za.zdn.vn/w660/Uploaded/mfssa/2016_04_04/bmwg2_4.jpg\" style=\"height:auto; margin:0px; width:660px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:transparent\">\r\n			<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;BMW Series 2 Gran Tourer được trang bị bộ m&acirc;m hợp kim nan k&eacute;p k&iacute;ch thước 16 inch.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" cellspacing=\"0\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"background-color:transparent\"><img alt=\"Chi tiet xe gia dinh 7 cho gia mem BMW vua ban o VN hinh anh 6\" src=\"http://znews-photo.d.za.zdn.vn/w660/Uploaded/mfssa/2016_04_04/bmwg2_7.jpg\" style=\"height:auto; margin:0px; width:660px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:transparent\">\r\n			<p>Nội thất tổng quan của Series 2 Gran Tourer thiết kế kiểu truyền thống của BMW, m&ocirc; phỏng d&ograve;ng th&aacute;c đổ v&agrave; hướng đến người l&aacute;i. C&aacute;c điểm nhấn như m&agrave;n h&igrave;nh trung t&acirc;m 6,5 inch nh&ocirc; l&ecirc;n khỏi khu vực điều khiển, ghế ngồi bọc da cao cấp,ốp trang tr&iacute; nh&ocirc;m &aacute;nh bạc viền đen b&oacute;ng.</p>\r\n\r\n			<p>V&ocirc;-lăng đa chức năng thể thao, bọc da, c&ugrave;ng viền chrome mờ.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" cellspacing=\"0\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"background-color:transparent\"><img alt=\"Chi tiet xe gia dinh 7 cho gia mem BMW vua ban o VN hinh anh 7\" src=\"http://znews-photo.d.za.zdn.vn/w660/Uploaded/mfssa/2016_04_04/bmwg2_8.jpg\" style=\"height:auto; margin:0px; width:660px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:transparent\">\r\n			<p>Khoang h&agrave;nh l&yacute; c&oacute; thể được mở rộng từ 560 đến 805 l&iacute;t. H&agrave;ng ghế thứ 3 gập xuống gi&uacute;p tăng kh&ocirc;ng gian tối đa l&ecirc;n đến 1.820 l&iacute;t. H&agrave;ng ghế thứ hai c&ograve;n được mở rộng bằng c&aacute;ch trượt ghế ph&iacute;a trước hoặc ph&iacute;a sau 130 mm.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" cellspacing=\"0\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"background-color:transparent\"><img alt=\"Chi tiet xe gia dinh 7 cho gia mem BMW vua ban o VN hinh anh 8\" src=\"http://znews-photo.d.za.zdn.vn/w660/Uploaded/mfssa/2016_04_04/bmwg2_9.jpg\" style=\"height:auto; margin:0px; width:660px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:transparent\">\r\n			<p>Mẫu xe 7 chỗ cũng hỗ trợ c&aacute;c t&iacute;nh năng chuyển đổi chế độ vận h&agrave;nh với c&aacute;c chế độ thể thao, comfort v&agrave; eco pro. Người l&aacute;i c&oacute; thể thao t&aacute;c bằng c&aacute;ch bấm v&agrave;o c&aacute;c n&uacute;t được bố tr&iacute; cạnh cần số.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" cellspacing=\"0\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"background-color:transparent\"><img alt=\"Chi tiet xe gia dinh 7 cho gia mem BMW vua ban o VN hinh anh 9\" src=\"http://znews-photo.d.za.zdn.vn/w660/Uploaded/mfssa/2016_04_04/bmwg2_10.jpg\" style=\"height:auto; margin:0px; width:660px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:transparent\">\r\n			<p>Tr&aacute;i tim của chiếc xe l&agrave; khối động cơ 3 xi-lanh thẳng h&agrave;ng, c&ocirc;ng nghệ BMW TwinPower Turbo, dung t&iacute;ch 1.5L, c&ocirc;ng suất 136 m&atilde; lực tại 4.000 v&ograve;ng/ph&uacute;t v&agrave; m&ocirc;-men xoắn cực đại 220 Nm tại 1.250 v&ograve;ng/ph&uacute;t. Khả năng tăng tốc 0-100 km/h trong 9,8 gi&acirc;y, tốc độ tối đa 202 km/h.</p>\r\n\r\n			<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Th&ocirc;ng số kỹ thuật chi tiết tr&ecirc;n BMW 218i Gran Tourer:</strong></p>\r\n\r\n			<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:100%\">\r\n				<tbody>\r\n					<tr>\r\n						<td>Dung t&iacute;ch động cơ</td>\r\n						<td>1499</td>\r\n					</tr>\r\n					<tr>\r\n						<td>C&ocirc;ng suất cực đại :</td>\r\n						<td>100 (136) / 4400</td>\r\n					</tr>\r\n					<tr>\r\n						<td>Thời gian tăng tốc từ 0 - 100km/h :</td>\r\n						<td>9,8</td>\r\n					</tr>\r\n					<tr>\r\n						<td>M&ocirc;men xoắn cực đại : Nm</td>\r\n						<td>220/1250</td>\r\n					</tr>\r\n					<tr>\r\n						<td>Vận tốc tối đa : km/h</td>\r\n						<td>202</td>\r\n					</tr>\r\n					<tr>\r\n						<td>Ti&ecirc;u thụ nhi&ecirc;n liệu</td>\r\n						<td>5,5</td>\r\n					</tr>\r\n					<tr>\r\n						<td>K&iacute;ch thước (d&agrave;i x rộng x cao) :</td>\r\n						<td>4556/2038/1647</td>\r\n					</tr>\r\n					<tr>\r\n						<td>Chiều d&agrave;i cơ sở :</td>\r\n						<td>2780</td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '1', '3', '2', '8', 'Đức', '1455000000');
INSERT INTO `products` VALUES ('8', 'BMW X1', '2016 new bmw x1.jpg', 'BMW X1 2016 đã có mặt tại Việt Nam với phiên bản X1 Sdrive 20i Xline nổi bật đầy cá tính', '<p><strong><img alt=\"\" src=\"http://giaxebmw.com/upload//X1_2016_00003.jpg\" style=\"width:100%px\" /></strong></p>\r\n\r\n<p>BMW X1 ho&agrave;n to&agrave;n mới mang đến một n&eacute;t đặc trưng của những chiếc SAV đương đại. Với thiết kế thể thao v&agrave; mạnh mẽ, chiều d&agrave;i cơ sở d&agrave;i c&ugrave;ng cản trước cản sau ngắn mang lại sự năng động phấn kh&iacute;ch. D&ugrave; trong nội th&agrave;nh hay tr&ecirc;n địa h&igrave;nh, chiếc xe vẫn mang đến sự gợi cảm bởi thiết kế sắc n&eacute;t v&agrave; hiện đại. Kết hợp những vật liệu mới v&agrave; những c&ocirc;ng nghệ hiện đại, <a href=\"http://giaxebmw.com/\">BMW</a> X1 ho&agrave;n to&agrave;n mới sẽ lại một lần nữa tạo những cột mốc đ&aacute;ng mong đợi.</p>\r\n\r\n<p><strong>KH&Aacute;M PH&Aacute; BMW X1 HO&Agrave;N TO&Agrave;N MỚI</strong></p>\r\n\r\n<p><img alt=\"\" src=\"http://giaxebmw.com/upload//X1-2016%20(7).jpeg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p>D&ugrave; cho xuất hiện thanh lịch h&agrave;ng ng&agrave;y tr&ecirc;n những con phố nhỏ hay để kh&aacute;m ph&aacute; những v&ugrave;ng l&atilde;nh thổ mới lạ xa x&ocirc;i, BMW X1 l&agrave; người bạn đồng h&agrave;nh cho mọi hoạt động. N&oacute; cung cấp sự linh hoạt đ&aacute;ng kinh ngạc v&agrave; mang đến nguồn cảm hứng mạnh mẽ.</p>\r\n\r\n<p><img alt=\"\" src=\"http://giaxebmw.com/upload//X1-2016%20(3).jpeg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p>H&igrave;nh ảnh của n&oacute; mang đến tinh thần kh&aacute;m ph&aacute;: BMW X1 đ&atilde; tiệm cận hơn với những chiếc SUV thị th&agrave;nh v&agrave; người ta gọi n&oacute; l&agrave; những chiếc SAV. Kh&aacute;i niệm diễn tả sự linh hoạt của những chiếc xe đ&aacute;p ứng nhiều nhu cầu kh&aacute;c nhau của chủ nh&acirc;n. Bạn c&oacute; thể dễ d&agrave;ng nhận ra chiếc X1 ngay trong b&oacute;ng tối bởi thiết kế truyền thống 6 đ&egrave;n pha ph&iacute;a trước cho họ X của thương hiệu BMW. Cản trước ngắn, cản sau ngắn, gầm cao v&agrave; một nổi thất rộng lu&ocirc;n cho ph&eacute;p những chuyến du lịch cho cả gia đ&igrave;nh bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"http://giaxebmw.com/upload//X1-2016%20(2).jpeg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p>L&ecirc;n kế hoạch cho sự ph&aacute; c&aacute;ch mỗi ng&agrave;y. Tận hưởng điều nhỏ nhất theo phong c&aacute;ch ri&ecirc;ng. Ph&aacute; vỡ những nguy&ecirc;n tắc rập khu&ocirc;n. Nh&igrave;n ngắm thế giới qua lăng k&iacute;nh kh&aacute;c biệt. Tiếp tục h&agrave;nh tr&igrave;nh ngay khi bạn mới dừng b&aacute;nh. Dừng lại khi người kh&aacute;c bắt đầu đi. Chọn đường v&ograve;ng thay v&igrave; lộ tr&igrave;nh ngắn. Tận hưởng tự do với tất cả gi&aacute;c quan. Niềm vui l&agrave; BMW X1, người bằng hữu ở bất kể cung đường n&agrave;o &ndash; th&agrave;nh thị, rừng n&uacute;i v&agrave; hơn thế nữa.</p>\r\n\r\n<p><strong>Th&ocirc;ng số kỹ thuật cơ bản của chiếc BMW X1 sDrive18i:</strong></p>\r\n\r\n<table cellspacing=\"0\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff\">Thiết kế động cơ</td>\r\n			<td style=\"background-color:#ffffff\">In-line/3/4</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff\">Dung t&iacute;ch động cơ</td>\r\n			<td style=\"background-color:#ffffff\">1499 cm3</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff\">C&ocirc;ng suất cực đại</td>\r\n			<td style=\"background-color:#ffffff\">100 (136)/4400 kw(hp)/rpm</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff\">M&ocirc;men xoắn cực đại</td>\r\n			<td style=\"background-color:#ffffff\">220/1250 Nm</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff\">Thời gian tăng tốc từ 0 - 100km/h</td>\r\n			<td style=\"background-color:#ffffff\">9,7 s</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff\">Vận tốc tối đa</td>\r\n			<td style=\"background-color:#ffffff\">200 km/h</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff\">Ti&ecirc;u hao nhi&ecirc;n liệu</td>\r\n			<td style=\"background-color:#ffffff\">6,7/4,9/5,6 l/100km</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff\">K&iacute;ch thước (d&agrave;i x rộng x cao)</td>\r\n			<td style=\"background-color:#ffffff\">4439/1821/1598</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff\">Chiều d&agrave;i cơ sở</td>\r\n			<td style=\"background-color:#ffffff\">2670 mm</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><img alt=\"\" src=\"http://giaxebmw.com/upload//x1-2016-tskt.jpg\" style=\"width:100%px\" />Với kiểu d&aacute;ng xe SAV (Sports Avtivity Vehicle), một biến thể của d&ograve;ng xe SUV truyền thống khi kết hợp t&iacute;nh sang trọng của những chiếc sedan truyền thống v&agrave; sự thực dụng của xe SUV. Sự xuất hiện của X1 một lần nữa khẳng định vị thế ti&ecirc;n phong trong ph&acirc;n kh&uacute;c SAV của BMW m&agrave; <a href=\"http://giaxebmw.com/bmw-x-series/bmw-x5.php\">X5</a> l&agrave; kẻ ti&ecirc;n phong.<br />\r\n<img alt=\"\" src=\"http://giaxebmw.com/upload//x1-2016-noi-that.jpeg\" style=\"height:480px; width:640px\" /><br />\r\nNội thất sang trọng v&agrave; mạnh mẽ</p>\r\n', '1', '4', '2', '15', 'Đức', '1600000000');
INSERT INTO `products` VALUES ('9', 'Toyota Yaris', 'yaris2017.png', 'Toyota Yaris 2017 dòng xe số 1 trong đô thị', '<p>&nbsp;</p>\r\n\r\n<p><img alt=\"phiên bản toyota yaris 2017\" src=\"http://toyota-giatot.vn/DBImages/toyota-yaris-nhap-khau-Thai-lan-2017-1.jpg\" style=\"width:725px\" /></p>\r\n\r\n<p><strong>Ngoại Thất</strong></p>\r\n\r\n<p>Toyota Yaris 2017: Về tổng thể th&igrave; Yaris 2017 d&agrave;i hơn v&agrave; thấp hơn phi&ecirc;n bản tiền nhiệm, n&ecirc;n ngay &aacute;nh mắt đầu ti&ecirc;n đ&atilde; thấy rằng Yaris 2016 rất &ldquo;chườm&rdquo; xe. K&iacute;ch thước tổng thể của xe D&agrave;i x Rộng x Cao : 4115 x 1700 x 1475 (mm).</p>\r\n\r\n<p>Phần đầu xe nổi bật trong từng đường n&eacute;t, Yaris 2017 lu&ocirc;n thu h&uacute;t mọi &aacute;nh nh&igrave;n với lưới tản nhiệt h&igrave;nh thang c&acirc;n đối. Hơn nữa sự kết hợp h&agrave;i h&ograve;a cụm đ&egrave;n trước sắc n&eacute;t v&agrave; thiết kế hốc h&uacute;t gi&oacute; theo đ&uacute;ng nguy&ecirc;n l&yacute; kh&iacute; động học, gi&uacute;p chủ sở hữu tự tin khẳng định c&aacute; t&iacute;nh v&agrave; sự lịch l&atilde;m của m&igrave;nh.</p>\r\n\r\n<p>Cụm đ&egrave;n pha cực kỳ sắc sảo, n&oacute; thể hiện &aacute;nh mắt sắc sảo của chủ nh&acirc;n chiếc xe. Cụm đ&egrave;n pha của<strong><em>Yaris 2017</em></strong> thon gọn vuốt d&agrave;i sang hai b&ecirc;n đầu xe kết hợp c&ugrave;ng cụm đ&egrave;n sương m&ugrave; tinh tế nổi bật n&eacute;t lịch l&atilde;m v&agrave; hiện đại trong thiết kế tổng thể. Phi&ecirc;n bản G trang bị đ&egrave;n pha dạng b&oacute;ng chiếu đảm bảo hiệu quả s&aacute;ng tối ưu, đem đến sự an t&acirc;m cho người l&aacute;i.</p>\r\n\r\n<p><img alt=\"Cụm đèn pha cực kỳ sắc sảo yaris 2017\" src=\"http://toyota-giatot.vn/DBImages/toyota-yaris-nhap-khau-Thai-lan-2017-3.jpg\" style=\"width:725px\" /></p>\r\n\r\n<p>B&ecirc;n dưới đ&egrave;n hậu l&agrave; đ&egrave;n sương m&ugrave; ph&iacute;a sau được bố tr&iacute; dưới thấp, tăng khả năng quan s&aacute;t cho c&aacute;c xe ph&iacute;a sau trong điều kiện thời tiết xấu.</p>\r\n\r\n<p>La-zang b&aacute;nh xe ở cả hai phi&ecirc;n bản G v&agrave; E đều được trang bị m&acirc;m đ&uacute;c 8 chấu đơn với thiết kế g&oacute;c cạnh mang đậm phong c&aacute;ch thể thao, tạo n&eacute;t l&ocirc;i cuốn ngay cả khi đang di chuyển. K&iacute;ch thước lốp xe l&agrave; 185/60R15.</p>\r\n\r\n<p><img alt=\"vóc dáng thể thao toyota yaris 2017\" src=\"http://toyota-giatot.vn/DBImages/toyota-yaris-nhap-khau-Thai-lan-2017-4.jpg\" style=\"width:725px\" /></p>\r\n\r\n<p>Những nh&agrave; thiết kế của Toyota đ&atilde; mang đến một Yaris 2017 với diện mạo thể thao v&agrave; năng động.</p>\r\n\r\n<p>Gương chiếu hậu phi&ecirc;n bản Yaris G trang bị chỉnh l&ograve;ng gương bằng điện, t&iacute;ch hợp chức năng gập điện đem đến sự tiện lợi cho chủ nh&acirc;n trong c&aacute;ch sử dụng. B&ecirc;n ngo&agrave;i được thiết kế c&ugrave;ng m&agrave;u th&acirc;n xe v&agrave; t&iacute;ch hợp đ&egrave;n b&aacute;o rẽ, tăng t&iacute;nh thẩm mỹ v&agrave; an to&agrave;n hơn khi điều khiển xe.</p>\r\n\r\n<p>Cụm đ&egrave;n hậu được thiết kế với h&igrave;nh chữ L ph&aacute; c&aacute;ch &ocirc;m trọn phần đu&ocirc;i xe, to&aacute;t l&ecirc;n phong c&aacute;ch năng động v&agrave; hiện đại của chủ nh&acirc;n chiếc xe. Đặc điểm nổi bật của d&ograve;ng xe hatchback được thể hiện r&otilde; nhất phần đu&ocirc;i xe. H&agrave;i h&ograve;a trong một tổng thể đồng nhất, đu&ocirc;i xe tạo ấn tượng mạnh với thiết kế g&oacute;c cạnh v&agrave; những đường g&acirc;n dập nổi. Cụm đ&egrave;n sau c&aacute; t&iacute;nh c&ugrave;ng nẹp trang tr&iacute; biển số mạ chrome l&agrave; điểm nhấn ho&agrave;n hảo trong thiết kế hiện đại.</p>\r\n\r\n<p><img alt=\"cụm đèn hậu yaris 2017\" src=\"http://toyota-giatot.vn/DBImages/toyota-yaris-nhap-khau-Thai-lan-2017-5.jpg\" style=\"width:725px\" /></p>\r\n\r\n<p>Điểm nhấn ở đu&ocirc;i xe l&agrave; nẹp trang tr&iacute; biển số mạ chrome tạo điểm nhấn nổi bật của tổng thể thiết kế đu&ocirc;i xe.</p>\r\n\r\n<p><strong>Nội thất</strong></p>\r\n\r\n<p>Trong xe được thiết kế nhiều c&aacute;c ngăn để đựng c&aacute;c vật dụng, được thiết kế ph&ugrave; hợp, đem lại tiện &iacute;ch khi sử dụng xe v&agrave; dễ d&agrave;ng cất giữ, thao t&aacute;c trong tầm với.</p>\r\n\r\n<p>Ốp cửa xe thiết kế c&aacute;ch điệu theo cấu tr&uacute;c nhấp nh&ocirc; v&agrave; những đường chỉ tinh tế t&ocirc;n th&ecirc;m n&eacute;t sang trọng v&agrave; rộng r&atilde;i cho kh&ocirc;ng gian nội thất.</p>\r\n\r\n<p>Ghế của <strong><em>Toyota Yaris 2017</em></strong> c&oacute; k&iacute;ch thước lớn, mạng lại sự thoải m&aacute;i tối đa cho h&agrave;nh kh&aacute;ch. Sự tỉ mỉ thể hiện trong từng đường chỉ nổi tr&ecirc;n bề mặt, kết hợp c&ugrave;ng t&ocirc;ng m&agrave;u đối lập tương phản, khắc họa phong c&aacute;ch thể thao, sự kh&aacute;c biệt v&agrave; c&aacute;c t&iacute;nh.</p>\r\n\r\n<p>Với mong muốn người l&aacute;i sẽ dễ d&agrave;ng v&agrave; c&oacute; cảm hứng th&iacute;ch th&uacute; khi l&aacute;i xe, Yaris 2017 được thiết kế v&ocirc; lăng 3 chấu vừa vặn theo vị tr&iacute; đặt tay, Phi&ecirc;n bản G l&agrave; v&ocirc; lăng bọc da, mạ bạc tăng th&ecirc;m vẻ sang trọng v&agrave; tinh tế của kh&ocirc;ng gian nội thất. Việc t&iacute;ch hợp c&aacute;c ph&iacute;m điều khiển &acirc;m thanh tr&ecirc;n v&ocirc; lăng tối ưu h&oacute;a sự tiện lợi khi sử dụng xe.</p>\r\n\r\n<p><img alt=\"Yaris 2017 được thiết kế vô lăng 3 chấu\" src=\"http://toyota-giatot.vn/DBImages/vo-lang-3-chau-yaris-2017.jpg\" style=\"width:725px\" /></p>\r\n\r\n<p>Bảng đồng hồ tap l&ocirc; thiết kế 3D với t&ocirc;ng m&agrave;u cam chủ đạo v&agrave; đường viền mạ chrome l&agrave;m nổi bật vẻ hiện đại cho nội thất xe. M&agrave;n h&igrave;nh đa th&ocirc;ng tin (phi&ecirc;n bản G) được t&iacute;ch hợp trong bảng đồng hồ gi&uacute;p người l&aacute;i dễ d&agrave;ng nắm bắt c&aacute;c th&ocirc;ng số của xe khi vận h&agrave;nh.</p>\r\n\r\n<p>Hệ thống mở kh&oacute;a th&ocirc;ng minh v&agrave; khởi động bằng n&uacute;t bấm được trang bị tr&ecirc;n phi&ecirc;n bản G tạo sự tiện lợi khi ra/v&agrave;o xe, đồng thời gi&uacute;p việc khởi động tắt/mở m&aacute;y trở n&ecirc;n dễ d&agrave;ng v&agrave; &iacute;t thao t&aacute;c hơn với ch&igrave;a kh&oacute;a lu&ocirc;n b&ecirc;n m&igrave;nh.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh DVD 6 loa được trang bị tr&ecirc;n phi&ecirc;n bản Yaris G v&agrave; hệ thống CD 1 đĩa 4 loa t&iacute;ch hợp tr&ecirc;n Yaris E, t&iacute;ch hợp AM/FM c&oacute; thể ph&aacute;t nhạc dưới định dạng MP3/WMA/AAC. Ngo&agrave;i ra, nhờ khả năng kết nối Bluetooth v&agrave; USB, chủ sở hữu sẽ dễ d&agrave;ng thưởng thức c&aacute;c giai điệu y&ecirc;u th&iacute;ch mọi l&uacute;c, mọi nơi. Chuyến đi sẽ trở n&ecirc;n thoải m&aacute;i hơn nhờ hệ thống giải tr&iacute; đầy đủ như đang trong một ng&ocirc;i nh&agrave; di động.</p>\r\n\r\n<p>Hệ thống điều h&ograve;a tự động (phi&ecirc;n bản G) v&agrave; chỉnh tay (phi&ecirc;n bản E) được trang bị bộ lọc gi&oacute; gi&uacute;p kh&ocirc;ng kh&iacute; trong xe lu&ocirc;n sạch m&aacute;t, trong l&agrave;nh, tạo cảm gi&aacute;c dễ chịu cho suốt h&agrave;nh tr&igrave;nh.</p>\r\n\r\n<p><strong>Động cơ v&agrave; hệ thống truyền động:</strong></p>\r\n\r\n<p><img alt=\"vóc dáng thể thao yaris 2017\" src=\"http://toyota-giatot.vn/DBImages/toyota-yaris-nhap-khau-Thai-lan-2017-6.jpg\" style=\"width:725px\" /></p>\r\n\r\n<p>Động cơ 1.5 l&iacute;t ở phi&ecirc;n bản tiền nhiệm nay đươc thay bằng động cơ 1.3 l&iacute;t tiết kiệm hơn, nhưng vẫn đảm bảo c&ocirc;ng suất đủ lớn để ngo&agrave;i việc chạy trong phố, th&igrave; xe vẫn c&oacute; thể lướt &ecirc;m tr&ecirc;n những cung đường cao tốc, hoặc vượt qua những cung đường kh&oacute; khăn. Cả hai phi&ecirc;n bản G &amp; E đều được trang bị hộp số tự động.</p>\r\n', '1', '2', '3', '19', 'Nhật Bản', '636000000');
INSERT INTO `products` VALUES ('10', 'Fortuner 2017', 'Fortuner 2017.png', 'Xe Toyota Fortuner 2017 có gì mới?', '<h2>&nbsp; &nbsp; &nbsp; &nbsp;FORTUNER 2017</h2>\r\n\r\n<p>Thế hệ mới nhất của Fortuner cho tới thời điểm hiện tại. Những th&ocirc;ng tin ban đầu về chiếc xe đ&atilde; nhận được rất nhiều phản hồi t&iacute;ch cực v&agrave; được cho l&agrave; ph&oacute;ng kho&aacute;ng v&agrave; mạnh mẽ hơn phi&ecirc;n bản tiền nhiệm</p>\r\n\r\n<p><img alt=\"Gia xe oto toyota fortuner 2017 là bao nhiêu?\" src=\"http://giatoyota.vn/wp-content/uploads/2016/07/Gia-xe-oto-toyota-fortuner-7.jpg\" style=\"height:533px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<h3><strong>Ngoại thất</strong></h3>\r\n\r\n<p>Toyota Fortuner 2017 sở hữu một ngoại h&igrave;nh thể thao việt d&atilde; kh&aacute; ngầu v&agrave; thời thượng. K&iacute;ch thước d&agrave;i x rộng x cao được thiết kế đạt độ ho&agrave;n hảo cho một chiếc SUV tại Việt Nam cụ thể l&agrave; 4.795 x 1.855 x 1.853 (mm), chiều d&agrave;i cơ sở kh&ocirc;ng thay đổi đạt 2.750mm, khoảng s&aacute;ng gầm xe đạt 225mm tạo cho xe một phong th&aacute;i uy nghi, th&aacute;ch thức mọi nẻo đường thuộc mọi dạng địa h&igrave;nh</p>\r\n\r\n<p><img alt=\"Toyota fortuner 2017 đánh giá xe\" src=\"http://giatoyota.vn/wp-content/uploads/2016/07/Gia-xe-oto-toyota-fortuner-4.jpg\" style=\"height:533px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p>Cụm đ&egrave;n pha s&aacute;ng gi&aacute; với h&igrave;nh d&aacute;ng như 2 lưỡi gươm sắc lạnh, được coi l&agrave; s&aacute;t thủ b&oacute;ng đ&ecirc;m v&agrave; thu h&uacute;t bởi sự lạnh l&ugrave;ng đầy ki&ecirc;u h&atilde;nh. C&ocirc;ng nghệ Led được trang bị cho cụm đ&egrave;n n&agrave;y khiến ch&uacute;ng trở n&ecirc;n s&aacute;ng v&agrave; tiết kiệm năng lượng hơn so với một v&agrave;i phi&ecirc;n bản cũ đ&atilde; từng ra mắt trước đ&oacute;</p>\r\n\r\n<p><img alt=\"Gia xe oto toyota fortuner mới tại Việt Nam\" src=\"http://giatoyota.vn/wp-content/uploads/2016/07/Gia-xe-oto-toyota-fortuner-3.jpg\" style=\"height:533px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p>Lưới tản nhiệt h&igrave;nh thang c&acirc;n đối gồm nhiều thanh ngang cỡ lớn được mạ chrome sang trọng v&agrave; v&ocirc; c&ugrave;ng tinh tế</p>\r\n\r\n<p>Cụm đ&egrave;n sương m&ugrave; h&igrave;nh tam gi&aacute;c c&aacute;ch điệu rất lạ mắt, hỗ trợ tối đa cho cụm đ&egrave;n pha đặc biệt khi thời tiết xấu hoặc tại những khu vực thiếu &aacute;nh s&aacute;ng</p>\r\n\r\n<p>Toyota Fortuner 2017 c&ograve;n sở hữu một cột C v&ocirc; c&ugrave;ng tinh tế, điểm nhấn tiếp theo l&agrave; bộ la zăng 18 inch 12 chấu thể thao, trẻ trung v&agrave; thời thượng.</p>\r\n\r\n<p>Điểm nổi bật ở ph&iacute;a đu&ocirc;i xe l&agrave; cụm đ&egrave;n hậu thanh tho&aacute;t, mỏng nhẹ được vuốt d&agrave;i một c&aacute;ch đầy nghệ thuật. Phi&ecirc;n bản cao cấp c&ograve;n được trang bị c&ocirc;ng nghệ Led t&acirc;n tiến đầy thu h&uacute;t</p>\r\n\r\n<h3><strong>Nội thất</strong></h3>\r\n\r\n<p>Toyota Fortuner 2017 c&oacute; một nội thất xa hoa với nhiều s&aacute;ng tạo mang t&iacute;nh đột ph&aacute; so với c&aacute;c d&ograve;ng xe c&ugrave;ng ph&acirc;n kh&uacute;c</p>\r\n\r\n<p><img alt=\"Gia xe oto toyota fortuner 2017 All New\" src=\"http://giatoyota.vn/wp-content/uploads/2016/07/Gia-xe-oto-toyota-fortuner-1.jpg\" style=\"height:595px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p>Vừa mở cửa xe, h&agrave;nh kh&aacute;ch như bước v&agrave;o một kh&ocirc;ng gian rộng lớn chứ kh&ocirc;ng chỉ trong tầm một chiếc SUV. Kh&ocirc;ng gian đa chiều c&ugrave;ng sự xuất hiện dồi d&agrave;o của &aacute;nh s&aacute;ng tự nhi&ecirc;n khiến nội thất xe được y&ecirc;u ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n</p>\r\n\r\n<p>V&ocirc; lăng 3 chấu thay cho 4 chấu như phi&ecirc;n bản cũ cực thể thao v&agrave; ra d&aacute;ng. Bảng điều khiển trung t&acirc;m được thiết kế tập trung, gọn g&agrave;ng hơn hẳn so với phi&ecirc;n bản tiền nhiệm. Hệ thống ghế ngồi đa dụng, được bố tr&iacute; gọn nhưng vẫn c&oacute; vẻ bề thế chứ kh&ocirc;ng hề nhỏ b&eacute; so với kh&ocirc;ng gian xe. Khoảng c&aacute;ch giữa c&aacute;c h&agrave;ng ghế đủ rộng để di chuyển v&agrave; sinh hoạt khi cần thiết</p>\r\n\r\n<p>Hệ thống giải tr&iacute; được trang bị đầy đủ bao gồm m&agrave;n h&igrave;nh 7 inch th&ocirc;ng minh, ổ đĩa DVD đa c&ocirc;ng dụng, c&oacute; kết nối USB/AUX. Điểm cộng cho hệ thống giải tr&iacute; l&agrave; hệ thống &acirc;m thanh gồm 6 loa chất lượng cực đỉnh, c&oacute; khả năng ph&aacute;t ra &acirc;m thanh lan đều khắp kh&ocirc;ng gian xe</p>\r\n\r\n<p>Toyota Fortunner 2017 c&ograve;n c&oacute; nhiều điểm cộng bởi ch&igrave;a kh&oacute;a th&ocirc;ng minh, t&iacute;nh năng mở cốp điện tử t&acirc;n tiến, t&iacute;nh năng n&uacute;t khởi động Start/ Stop độc v&agrave; lạ</p>\r\n\r\n<h3><strong>Động cơ</strong></h3>\r\n\r\n<p>3 bản động cơ cơ động v&agrave; bền bỉ được ra mắt c&ugrave;ng với 5 phi&ecirc;n bản xe được cho rằng sẽ tiết kiệm tối đa nhi&ecirc;n liệu v&agrave; gi&uacute;p chủ nh&acirc;n tiết kiệm t&agrave;i ch&iacute;nh ở mức kh&aacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"http://toyota-tancang.weebly.com/uploads/2/1/2/6/21261020/toyota-fortuner-2017-dong-co_orig.jpg\" /></p>\r\n\r\n<p>Fortuner 2017 bản 2.8l 4 xylanh m&aacute;y dầu tự tin di chuyển với hộp số tự động 6cấp. Động cơ cho c&ocirc;ng suất cực đại 177HP tại 3.400 v&ograve;ng/ph&uacute;t, momen xoắn lớn nhất đạt 450Nm tại 1.600- 2.000 v&ograve;ng/ph&uacute;t</p>\r\n\r\n<p>Fortuner 2017 bản 2.7L 4 xy lanh tự động m&aacute;y xăng với hộp số tự động 6 cấp. C&ocirc;ng suất lớn nhất đạt 166HP tại 5.200 v&ograve;ng/ ph&uacute;t.Momen xoắn lớn nhất 245Nm tại 4.000 v&ograve;ng/ ph&uacute;t</p>\r\n\r\n<p>Fortunner 2017 2.4L 4 xy lanh m&aacute;y dầu được tiết kiệm nhi&ecirc;n liệu với hộp số tự động 6 cấp, tự động v&agrave; tay số. C&ocirc;ng suất cực đại của động cơ đạt 1.500HP tại 3.400 v&ograve;ng/ph&uacute;t. Momen xoắn lớn nhất 4.000Nm tại 1.600- 2.000 v&ograve;ng/ ph&uacute;t</p>\r\n\r\n<h3><strong>An to&agrave;n</strong></h3>\r\n\r\n<p>Đề cao độ ho&agrave;n hảo của đứa con cứng, Toyota chăm ch&uacute;t hệ thống an to&agrave;n cho Toyota Fortuner 2017 một c&aacute;ch tương ho&agrave;n thiện</p>\r\n\r\n<p><img alt=\"\" src=\"http://toyota-tancang.weebly.com/uploads/2/1/2/6/21261020/toyota-fortuner-2017-can-bang-dien-tu-vsc_orig.jpg\" style=\"height:390px; width:940px\" /></p>\r\n\r\n<p>Trước hết phải kể đến hệ thống b&oacute; cứng phanh ABS gi&uacute;p b&aacute;nh xe kh&ocirc;ng c&ograve;n bị b&oacute; cứng khi phanh đặc biệt với trường hợp phanh gấp khi b&aacute;nh xe bị trượt tr&ecirc;n mặt đường</p>\r\n\r\n<p>Hệ thống ph&acirc;n bổ lực ph&acirc;n điện tử v&agrave; hỗ trợ phanh BA, hệ thống n&agrave;y c&oacute; vai tr&ograve; kh&ocirc;ng k&eacute;m g&igrave; ABS, hạn chế tối đa sự đổ khi xe bị lệch do nhiều t&aacute;c nh&acirc;n t&aacute;c động</p>\r\n\r\n<p>Ngo&agrave;i ra, hệ thống &aacute;n to&agrave;n c&ograve;n c&oacute; sự g&oacute;p mặt của hệ thống kiểm so&aacute;t ổn định xe th&ocirc;ng minh, hỗ trợ đổ đ&egrave;o, ngang dốc,..</p>\r\n', '1', '3', '3', '1', 'Nhật Bản', '1050000000');
INSERT INTO `products` VALUES ('11', 'Toyota Hilux 2016', '2016-Toyota-Hilux.png', 'Toyota Hilux 2016 – Tiện nghi và An toàn hơn', '<h3>&nbsp;</h3>\r\n\r\n<p>Những cải tiến quan trọng tr&ecirc;n <strong>&nbsp;</strong><strong>Toyota Hilux 2016 &nbsp;</strong>được tập trung v&agrave;o phần khung sườn v&agrave; hệ thống treo, cứng c&aacute;p v&agrave; an to&agrave;n hơn, cải thiện t&iacute;nh năng vận h&agrave;nh offroad v&agrave; cả độ tiện nghi &ecirc;m &aacute;i th&ocirc;ng qua hệ thống treo v&agrave; giảm x&oacute;c.</p>\r\n\r\n<p><img alt=\"Hilux mới giá bao nhiêu\" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/09/1972728-kiem-tra-do-an-toan-cua-l-2.jpg\" style=\"height:467px; margin:4px auto 7px; width:700px\" /></p>\r\n\r\n<p>HIlux mới c&oacute; tới 3 kiểu hệ thống treo kh&aacute;c nhau th&iacute;ch ứng với c&aacute;c thị trường kh&aacute;c nhau: kiểu ti&ecirc;u chuẩn ph&ugrave; hợp với điều kiện chung; kiểu tải nặng &ldquo;heave duty&rdquo; d&agrave;nh cho kh&aacute;ch h&agrave;ng cần tải nặng v&agrave; kiểu tiện nghi &ldquo;Comfort&rdquo; như trang bị tr&ecirc;n mẫu xe ra mắt ở Th&aacute;i Lan, th&iacute;ch hợp cho vận h&agrave;nh chủ yếu tr&ecirc;n đường bằng phẳng.</p>\r\n\r\n<h3><strong>Toyota Hilux 2016 &ndash; Động cơ v&agrave; Vận h&agrave;nh</strong></h3>\r\n\r\n<p>Động cơ 1KD-FTV 3.0L, I4, DOHC, được trang bị những c&ocirc;ng nghệ tối t&acirc;n: Hệ thống phun diesel điện tử Common Rail; Turbo tăng &aacute;p kết hợp Intercooler. Sử dụng bộ Turbo tăng &aacute;p loại VNT cho c&ocirc;ng suất cao, momen xoắn lớn v&agrave; khả năng tiết kiệm nhi&ecirc;n liệu vượt trội. Chủ nh&acirc;n sẽ lu&ocirc;n h&agrave;i l&ograve;ng khi sở hữu chiếc b&aacute;n tải vận h&agrave;nh mạnh mẽ nhưng vẫn tiết kiệm nhi&ecirc;n liệu tối ưu.</p>\r\n\r\n<p><img alt=\"Toyota Hilux 2016 - Động cơ\" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/10/hilux_performance_dong_co.jpg\" style=\"height:339px; margin:4px auto 7px; width:700px\" /></p>\r\n\r\n<p>Toyota Hilux 2016 &nbsp;Phi&ecirc;n bản 3.0G AT được trang bị hộp số tự động 5 cấp với chế độ sang số thể thao cho chủ nh&acirc;n sự thuận tiện v&agrave; phấn kh&iacute;ch khi l&aacute;i xe. Ngo&agrave;i ra, hộp số mới kết hợp với hệ thống g&agrave;i cầu điện tử ti&ecirc;n tiến c&ugrave;ng kh&oacute;a vi sai cầu sau sẽ mang lại cảm gi&aacute;c l&aacute;i v&ocirc; c&ugrave;ng mạnh mẽ, đ&aacute;nh thức đam m&ecirc; chinh phục mọi địa h&igrave;nh của người cầm l&aacute;i.</p>\r\n\r\n<p><img alt=\"Toyota Hilux 2016 - Hộp số \" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/10/hilux_performance_hop_so.jpg\" style=\"height:339px; margin:4px auto 7px; width:700px\" /></p>\r\n\r\n<p>Phi&ecirc;n bản <strong>Hilux 3.0G Số tự động 2016</strong> được trang bị hệ thống g&agrave;i cầu điện tử gi&uacute;p thao t&aacute;c l&aacute;i c&agrave;ng th&ecirc;m thuận tiện. Hệ thống kh&oacute;a vi sai cầu sau kết hợp với hệ thống g&agrave;i cầu cho ph&eacute;p xe c&oacute; thể vượt qua được những địa h&igrave;nh gồ ghề, phức tạp.</p>\r\n\r\n<p><img alt=\"hilux_performance_gai_cau_tu_dong\" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/10/hilux_performance_gai_cau_tu_dong.jpg\" style=\"height:339px; margin:4px auto 7px; width:700px\" /></p>\r\n\r\n<h3><strong>Hệ thống An to&agrave;n</strong></h3>\r\n\r\n<p>T&uacute;i kh&iacute;: 3 t&uacute;i kh&iacute; (người l&aacute;i, h&agrave;nh kh&aacute;ch ph&iacute;a trước &amp; t&uacute;i kh&iacute; đầu gối), ri&ecirc;ng bản 3.0G số tự động c&oacute; đến 7 t&uacute;i kh&iacute;</p>\r\n\r\n<p><img alt=\"An toan Hilux toyota - túi khí bảo vệ xe\" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/10/An-toan-Hilux-toyota.jpg\" style=\"height:339px; margin:4px auto 7px; width:700px\" /></p>\r\n\r\n<p>Cột l&aacute;i v&agrave; b&agrave;n đạp phanh tự đổ khi c&oacute; va chạm&hellip;</p>\r\n\r\n<p>Khung xe TOP<img alt=\"hilux_safety_abs\" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/10/hilux_safety_abs.jpg\" style=\"height:339px; margin:4px auto 7px; width:700px\" /></p>\r\n\r\n<p><strong><em>Hệ thống chống b&oacute; cứng phanh ABS</em></strong></p>\r\n\r\n<p><img alt=\"An toan Hilux toyota - Hệ thống phân bổ lực phanh điện tử EBD\" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/10/An-toan-Hilux-toyota-2.jpg\" style=\"height:337px; margin:4px auto 7px; width:700px\" /></p>\r\n\r\n<p><em>Hệ thống ph&acirc;n bổ lực phanh điện tử EBD, Hệ thống hỗ trợ lực phanh khẩn cấp BA</em></p>\r\n\r\n<p><img alt=\"Hệ thống an toàn trên Hilux - Hệ thống kiểm soát lực kéo TSC \" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/10/Hilux-TSC.jpg\" style=\"height:336px; margin:4px auto 7px; width:700px\" /></p>\r\n\r\n<p><strong><em>Hệ thống c&acirc;n bằng điện tử VSC, Hệ thống kiểm so&aacute;t lực k&eacute;o TRC</em></strong></p>\r\n\r\n<table align=\"center\" cellspacing=\"0\" style=\"width:825px\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\"><strong>Chiều d&agrave;i</strong></td>\r\n			<td>5.335 mm</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\"><strong>Chiều rộng</strong></td>\r\n			<td>1.855 mm</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\"><strong>Chiều cao</strong></td>\r\n			<td>1.820 mm</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\"><strong>Chiều d&agrave;i cơ sở</strong></td>\r\n			<td>3.085 mm</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"4\"><strong>Động cơ</strong></td>\r\n			<td><strong>Kiểu</strong></td>\r\n			<td>1GD (diesel)</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Dung t&iacute;ch</strong></td>\r\n			<td>2.8 L&iacute;t</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>C&ocirc;ng suất</strong></td>\r\n			<td>130 kW [177 PS] / 3.400 rpm</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>M&ocirc;-men</strong></td>\r\n			<td>450 Nm / 2.400 rpm</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\"><strong>Hộp số</strong></td>\r\n			<td>Tự động 6 cấp</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\"><strong>Dẫn động</strong></td>\r\n			<td>4 b&aacute;nh to&agrave;n thời gian</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\"><strong>K&iacute;ch thước la-zang</strong></td>\r\n			<td>17 inch<br />\r\n			&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '1', '5', '3', '22', 'Nhật Bản', '697000000');
INSERT INTO `products` VALUES ('12', 'Toyota Sienna', 'Toyota Sienna 2017.png', 'Phiên bản cải tiến 2016 của mẫu MPV đa dụng Toyota Sienna được một showroom tư nhân tại Hà Nội đưa về và phân phối với giá bán hơn 3,2 tỷ đồng.', '<p><img alt=\"toyota Siena ngoai that (7).JPG\" src=\"http://cdn2.autodaily.vn/du-lieu/2016/02/02/toyota%20Siena%20ngoai%20that%20(7).JPG\" style=\"height:361px; margin:0px; width:645px\" /></p>\r\n\r\n<p><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Toyota SIenna Limited 2016 c&oacute; mặt tại H&agrave; Nội.</em></p>\r\n\r\n<p>Về thiết kế, Sienna 2016 kh&ocirc;ng c&oacute; nhiều sự kh&aacute;c biệt so với phi&ecirc;n bản tiền nhiệm. Mặt trước xe nổi bật với kiểu d&aacute;ng thiết kế theo h&igrave;nh chữ V. Lưới tản nhiệt l&agrave;m từ kim loại mạ chrome s&aacute;ng, đ&egrave;n pha thiết kế liền khối với lưới tản nhiệt kết hợp c&ugrave;ng dải đ&egrave;n LED chạy ng&agrave;y sắc sảo.</p>\r\n\r\n<p><img alt=\"toyota Siena ngoai that (11).jpg\" src=\"http://cdn1.autodaily.vn/du-lieu/2016/02/02/toyota%20Siena%20ngoai%20that%20(11).jpg\" style=\"height:361px; margin:0px; width:645px\" /></p>\r\n\r\n<p>Sienna 2016 sở hữu k&iacute;ch thước tổng thể d&agrave;i x rộng x cao lần lượt l&agrave; 5.085 x 1.986 x 1.750 mm, chiều d&agrave;i cơ sở đạt mức 3.030 mm. Cửa xe được thiết kế kiểu mở trượt bằng điện đặc trưng của d&ograve;ng MPV. Toyota Sienna 2016 được trang bị m&acirc;m 18 inch 10 chấu, lốp trước v&agrave; sau c&oacute; chung k&iacute;ch thước P235/55. Ph&iacute;a sau xe nổi bật với lưới đ&egrave;n hậu lớn, thanh kim loại mạ chrome với d&ograve;ng chữ Sienna dập nổi được ốp ch&iacute;nh giữa đu&ocirc;i xe.</p>\r\n\r\n<p><img alt=\"toyota Siena ngoai that (15).jpg\" src=\"http://cdn1.autodaily.vn/du-lieu/2016/02/02/toyota%20Siena%20ngoai%20that%20(15).jpg\" style=\"height:361px; margin:0px; width:645px\" /></p>\r\n\r\n<p>Kh&ocirc;ng gian nội thất b&ecirc;n trong xe nổi bật với chất kiệu da bọc cao cấp, một số chi tiết được ốp kim loại v&agrave; gỗ sang trọng. Trung t&acirc;m bảng điều khiển l&agrave; m&agrave;n h&igrave;nh cảm ứng 6,1 inch trang bị ti&ecirc;u chuẩn, ph&iacute;a dưới l&agrave; c&aacute;c ph&iacute;m chức năng điều chỉnh điều h&ograve;a, cần gạt chế độ dừng đỗ, di chuyển, ... V&ocirc;-lăng 3 chấu t&iacute;ch hợp nhiều ph&iacute;m chức năng, 2 m&agrave;n h&igrave;nh Analog ph&iacute;a sau, h&agrave;ng ghế đầu điều chỉnh điện.</p>\r\n\r\n<p><img alt=\"toyota Siena noi that (10).JPG\" src=\"http://cdn2.autodaily.vn/du-lieu/2016/02/02/toyota%20Siena%20noi%20that%20(10).JPG\" style=\"height:361px; margin:0px; width:645px\" /></p>\r\n\r\n<p>H&agrave;ng ghế thứ 2 của xe được thiết kế t&aacute;ch rời, điều chỉnh cơ l&ecirc;n, xuống, gập lại c&ugrave;ng gi&aacute; đỡ ch&acirc;n tiện dụng. H&agrave;ng ghế thứ 3 c&oacute; thiết kế 60 &ndash; 40 điều chỉnh điện ho&agrave;n to&agrave;n để gập lại hoặc hạ xuống tạo th&agrave;nh 1 mặt s&agrave;n rộng lớn cho khoang chứa đồ ph&iacute;a sau. H&agrave;nh kh&aacute;ch tại 2 h&agrave;ng ghế sau được trang bị 1 m&agrave;n h&igrave;nh 16,4 inch, đọc thẻ SD, kết nối HDMI, 2 đường điện ra 120V v&agrave; tai nghe kh&ocirc;ng d&acirc;y.<img alt=\"\" src=\"http://cdn2.autodaily.vn/du-lieu/2016/02/02/toyota%20Siena%20noi%20that%20(20).jpg\" style=\"height:559px; margin:0px auto; width:680px\" /></p>\r\n\r\n<p>Toyota Sienna Limited 2016 được trang bị khối động cơ V6 3.5L sản sinh c&ocirc;ng suất tối đa 266 m&atilde; lực, m&ocirc;-men xoắn cực đại 332 Nm, hệ dẫn động AWD, hộp số tự động 6 cấp đi k&egrave;m t&iacute;nh năng kiểm so&aacute;t điện tử th&ocirc;ng minh. Xe c&oacute; mức ti&ecirc;u thụ nhi&ecirc;n liệu trung b&igrave;nh 14,7L tại đ&ocirc; thị v&agrave; 12,38L tr&ecirc;n cao tốc.</p>\r\n\r\n<p><img alt=\"toyota Siena ngoai that (3).JPG\" src=\"http://cdn3.autodaily.vn/du-lieu/2016/02/02/toyota%20Siena%20ngoai%20that%20(3).JPG\" style=\"height:361px; margin:0px; width:645px\" /></p>\r\n\r\n<p>Một số trang bị an to&agrave;n nổi bật tr&ecirc;n xe như hệ thống ổn định xe VSC, chống trượt (TRAC), chống b&oacute; cứng ABS, ph&acirc;n bổ lực phanh EBD, phanh khẩn cấp BA, tạm dừng th&ocirc;ng minh SST (Smart Stop Technology). M&agrave;n h&igrave;nh gi&aacute;m s&aacute;t điểm m&ugrave;, cảnh b&aacute;o va chạm ph&iacute;a sau, hỗ trợ khởi h&agrave;nh ngang dốc.</p>\r\n', '1', '6', '3', '1', 'Nhật Bản', '3200000000');
INSERT INTO `products` VALUES ('13', 'Ford Fiesta 1.5L AT Sport', 'Ford 1.5LAT Sport.png', 'Ngắm mãi không chán', '<h3>&nbsp;</h3>\r\n\r\n<p>C&oacute; rất nhiều điểm để ch&uacute;ng ta n&oacute;i về thiết kế v&agrave; m&agrave;u sắc mới của Ford Fiesta. Lưới tản nhiệt mới. Đ&egrave;n pha mới.Diện mạo ho&agrave;n to&agrave;n mới. C&oacute; mẫu 4 cửa hay 5 cửa. H&atilde;y c&ugrave;ng kh&aacute;m ph&aacute; n&agrave;o.</p>\r\n\r\n<p><img alt=\"Xe Ford Fiesta 1.5L AT Sport 5 cửa - Hatchback\" src=\"http://driver.gianhangvn.com/image/xe-ford-fiesta-titanium-4-cua-583175j22691.jpg\" /></p>\r\n\r\n<h2><strong>C&Ocirc;NG NGHỆ</strong></h2>\r\n\r\n<h3><strong>Những t&iacute;nh năng thực sự tuyệt vời</strong></h3>\r\n\r\n<p>Đ&atilde; trải nghiệm rồi th&igrave; bạn sẽ cảm thấy ch&uacute;ng hữu &iacute;ch v&agrave; tuyệt vời như thế n&agrave;o. Một khi bạn l&aacute;i xe với c&aacute;c t&iacute;nh năng n&agrave;y, bạn sẽ lu&ocirc;n muốn tiếp tục sử dụng ch&uacute;ng.</p>\r\n\r\n<p><img alt=\"Xe Ford Fiesta 1.5L AT Sport 5 cửa - Hatchback\" src=\"http://driver.gianhangvn.com/image/xe-ford-fiesta-titanium-4-cua-mau-1-583176j22691.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>AN TO&Agrave;N</strong></h2>\r\n\r\n<h3><strong>Đ&acirc;y l&agrave; d&ograve;ng xe m&agrave; thế hệ bố mẹ bạn c&ograve;n th&iacute;ch</strong></h3>\r\n\r\n<p>Thật y&ecirc;n t&acirc;m khi biết rằng Fiesta lu&ocirc;n đặt sự an to&agrave;n của bạn l&ecirc;n tr&ecirc;n hết. Nhưng ch&iacute;nh thế hệ bố mẹ bạn mới l&agrave; những người thực sự th&iacute;ch những t&iacute;nh năng đặc sắc tr&igrave;nh b&agrave;y tại đ&acirc;y.</p>\r\n\r\n<p><img alt=\"Xe Ford Fiesta 1.5L AT Sport 5 cửa - Hatchback\" src=\"http://driver.gianhangvn.com/image/xe-ford-fiesta-titanium-4-cua-mau-14-583191j22691.jpg\" /></p>\r\n\r\n<h3><strong>An t&acirc;m với t&uacute;i kh&iacute;</strong></h3>\r\n\r\n<p>Fiesta được trang bị 7 t&uacute;i kh&iacute;. T&uacute;i kh&iacute; d&agrave;nh cho người l&aacute;i v&agrave; h&agrave;nh kh&aacute;ch ph&iacute;a trước, t&uacute;i kh&iacute; b&ecirc;n cho h&agrave;nh kh&aacute;ch ph&iacute;a trước, t&uacute;i kh&iacute; r&egrave;m dọc hai b&ecirc;n trần xe v&agrave; t&uacute;i kh&iacute; bảo vệ đầu gối người l&aacute;i. Mỗi t&uacute;i kh&iacute; đều mang lại cảm gi&aacute;c an t&acirc;m hơn.</p>\r\n\r\n<p><img alt=\"Xe Ford Fiesta 1.5L AT Sport 5 cửa - Hatchback\" src=\"http://driver.gianhangvn.com/image/xe-ford-fiesta-titanium-4-cua-mau-15-583192j22691.jpg\" /></p>\r\n\r\n<hr />\r\n<h2><strong>TIẾT KIỆM NHI&Ecirc;N LIỆU</strong></h2>\r\n\r\n<h3><strong>Lợi &iacute;ch l&acirc;u d&agrave;i</strong></h3>\r\n\r\n<p>Tiết kiệm&#39; kh&ocirc;ng phải l&agrave; kh&aacute;i niệm thường đi liền với sự thoải m&aacute;i. Nhưng khi đề cập đến tiết kiệm cho c&aacute; nh&acirc;n bạn th&igrave; đ&oacute; lại l&agrave; chuyện ho&agrave;n to&agrave;n kh&aacute;c.</p>\r\n\r\n<p><img alt=\"Xe Ford Fiesta 1.5L AT Sport 5 cửa - Hatchback\" src=\"http://driver.gianhangvn.com/image/xe-ford-fiesta-titanium-4-cua-mau-19-583196j22691.jpg\" /></p>\r\n\r\n<h3><strong>C&ocirc;ng nghệ ti&ecirc;n tiến</strong></h3>\r\n\r\n<p>C&aacute;c kỹ sư của Ford đ&atilde; đưa cả ba c&ocirc;ng nghệ chủ chốt v&agrave;o động cơ EcoBoost nhằm giảm mức ti&ecirc;u hao nhi&ecirc;n liệu v&agrave; mang lại sức mạnh vượt trội: phun nhi&ecirc;n liệu trực tiếp, trục cam biến thi&ecirc;n v&agrave; turbo tăng &aacute;p. Bộ điều khiển trung t&acirc;m đảm bảo cho động cơ vận h&agrave;nh &ecirc;m &aacute;i.</p>\r\n\r\n<p><img alt=\"Xe Ford Fiesta 1.5L AT Sport 5 cửa - Hatchback\" src=\"http://driver.gianhangvn.com/image/xe-ford-fiesta-titanium-4-cua-mau-21-583198j22691.jpg\" /></p>\r\n\r\n<p><strong>Phun nhi&ecirc;n liệu trực tiếp</strong></p>\r\n\r\n<p>Tiết kiệm nhi&ecirc;n liệu hơn v&agrave; tối ưu h&oacute;a c&ocirc;ng suất hoạt động.</p>\r\n\r\n<p><img alt=\"Xe Ford Fiesta 1.5L AT Sport 5 cửa - Hatchback\" src=\"http://driver.gianhangvn.com/image/xe-ford-fiesta-titanium-4-cua-mau-22-583199j22691.jpg\" /></p>\r\n\r\n<p><strong>Trục cam biến thi&ecirc;n</strong></p>\r\n\r\n<p>Gia tăng c&ocirc;ng suất động cơ v&agrave; tiết kiệm nhi&ecirc;n liệu</p>\r\n\r\n<p><img alt=\"Xe Ford Fiesta 1.5L AT Sport 5 cửa - Hatchback\" src=\"http://driver.gianhangvn.com/image/xe-ford-fiesta-titanium-4-cua-mau-23-583201j22691.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Phun nhi&ecirc;n liệu trực tiếp</strong></p>\r\n\r\n<p>Hệ thống phun nhi&ecirc;n liệu cao &aacute;p sẽ phun nhi&ecirc;n liệu trực tiếp v&agrave;o buồng đốt với lượng nhi&ecirc;n liệu được t&iacute;nh to&aacute;n ch&iacute;nh x&aacute;c theo nhu cầu của động cơ v&agrave;o mỗi thời điểm th&iacute;ch hợp, đồng thời hệ thống cũng gi&uacute;p l&agrave;m m&aacute;t kh&iacute; nạp cho ph&eacute;p tỷ lệ n&eacute;n cao hơn. So với động cơ phun nhi&ecirc;n liệu gi&aacute;n tiếp,...</p>\r\n\r\n<p><img alt=\"Xe Ford Fiesta 1.5L AT Sport 5 cửa - Hatchback\" src=\"http://driver.gianhangvn.com/image/xe-ford-fiesta-titanium-4-cua-mau-26-583204j22691.jpg\" /></p>\r\n\r\n<p><strong>Trục cam biến thi&ecirc;n</strong></p>\r\n\r\n<p>Động cơ EcoBoost kiểm so&aacute;t ch&iacute;nh x&aacute;c thời điểm đ&oacute;ng mở của van nạp v&agrave; van xả sao cho ph&ugrave; hợp với tốc độ v&agrave; cường độ hoạt động của động cơ. Nhờ vậy, qu&aacute; tr&igrave;nh tiết kiệm nhi&ecirc;n liệu được cải thiện, giảm bớt kh&iacute; thải, đồng thời tăng c&ocirc;ng suất của động cơ.</p>\r\n\r\n<p><img alt=\"Xe Ford Fiesta 1.5L AT Sport 5 cửa - Hatchback\" src=\"http://driver.gianhangvn.com/image/xe-ford-fiesta-titanium-4-cua-mau-27-583205j22691.jpg\" /></p>\r\n\r\n<p><strong>K&iacute;ch thước nhỏ gọn</strong></p>\r\n\r\n<p>Thiết kế nhỏ v&agrave; nhẹ của EcoBoost gi&uacute;p tăng khả năng tiết kiệm nhi&ecirc;n liệu, trong khi số lượng xy lanh &iacute;t hơn sẽ giảm ma s&aacute;t v&agrave; bớt ti&ecirc;u hao nhi&ecirc;n liệu. Ch&iacute;nh nhờ turbo tăng &aacute;p v&agrave; c&ocirc;ng nghệ phun nhi&ecirc;n liệu trực tiếp, EcoBoost c&oacute; khả năng sản sinh c&ocirc;ng suất mạnh mẽ tương đương với c&aacute;c động cơ lớn kh&aacute;c.</p>\r\n\r\n<h3><strong>Nhỏ nhưng mạnh mẽ</strong></h3>\r\n\r\n<p><img alt=\"Xe Ford Fiesta 1.5L AT Sport 5 cửa - Hatchback\" src=\"http://driver.gianhangvn.com/image/xe-ford-fiesta-titanium-4-cua-mau-28-583207j22691.jpg\" /></p>\r\n\r\n<h3><strong>HỘP SỐ POWERSHIFT 6 CẤP</strong></h3>\r\n\r\n<p>Khi nhắc đến bộ truyền động, thường chia th&agrave;nh hai nh&oacute;m: nh&oacute;m th&iacute;ch số s&agrave;n hoặc nh&oacute;m th&iacute;ch số tự động. Nhưng l&agrave;m thế n&agrave;o để thuyết phục cả hai nh&oacute;m đ&oacute; chỉ với một loại hộp số? Cảm gi&aacute;c l&aacute;i thể thao v&agrave; t&iacute;nh hiệu quả của một hộp số s&agrave;n.kết hợp với việc dễ sử dụng của một hộp số tự động.</p>\r\n\r\n<p><img alt=\"Xe Ford Fiesta 1.5L AT Sport 5 cửa - Hatchback\" src=\"http://driver.gianhangvn.com/image/xe-ford-fiesta-titanium-4-cua-mau-34-583224j22691.jpg\" /></p>\r\n\r\n<hr />\r\n<h2><strong>TH&Ocirc;NG SỐ KỸ THUẬT CỦA FORD FIESTA</strong></h2>\r\n\r\n<h3><strong>Động cơ</strong></h3>\r\n\r\n<ul>\r\n	<li>Dung t&iacute;ch xi lanh (cc) : 1,498</li>\r\n	<li>Kiểu động cơ : Xăng 1.5L Duratec 16 Van</li>\r\n	<li>M&ocirc; men xoắn cực đại (Nm/v&ograve;ng/ph&uacute;t) : 140 @ 4400</li>\r\n	<li>C&ocirc;ng suất cực đại (PS/v&ograve;ng/ph&uacute;t) : 112 @ 6300</li>\r\n	<li>Hệ thống nhi&ecirc;n liệu : Phun nhi&ecirc;n liệu điện tử đa điểm</li>\r\n	<li>Dung t&iacute;ch th&ugrave;ng xăng : 43</li>\r\n</ul>\r\n\r\n<h3><strong>Hộp số</strong></h3>\r\n\r\n<ul>\r\n	<li>Hộp số : Hộp số tự động 6 cấp ly hợp k&eacute;p</li>\r\n</ul>\r\n\r\n<h3><strong>Hệ thống phanh</strong></h3>\r\n\r\n<ul>\r\n	<li>B&aacute;nh xe : V&agrave;nh (m&acirc;m) đ&uacute;c hợp kim 16&quot;</li>\r\n	<li>Hệ thống phanh Trước- Sau : Đĩa - Tang trống</li>\r\n	<li>Lốp : 195/50 R16</li>\r\n</ul>\r\n\r\n<h3><strong>Hệ thống giảm x&oacute;c</strong></h3>\r\n\r\n<ul>\r\n	<li>Trước : Kiểu Macpherson với thanh c&acirc;n bằng</li>\r\n	<li>Sau : Thanh xoắn</li>\r\n</ul>\r\n\r\n<h3><strong>Hệ thống l&aacute;i</strong></h3>\r\n\r\n<ul>\r\n	<li>Hệ thống l&aacute;i : Trợ lực điện điều khiển điện tử</li>\r\n</ul>\r\n\r\n<h3><strong>Hệ thống &acirc;m thanh</strong></h3>\r\n\r\n<ul>\r\n	<li>Hệ thống loa : 6 loa</li>\r\n	<li>Hệ thống kết nối SYNC của Microsoft : Điều khiển bằng giọng n&oacute;i</li>\r\n	<li>Hệ thống &acirc;m thanh : CD 1 đĩa/ đ&agrave;i AM/ FM</li>\r\n	<li>Kết nối USB, AUX : C&oacute;</li>\r\n</ul>\r\n\r\n<h3><strong>Hệ thống điện</strong></h3>\r\n\r\n<ul>\r\n	<li>Kh&oacute;a cửa điện điều khiển từ xa : Kh&oacute;a điện th&ocirc;ng minh</li>\r\n	<li>Kh&oacute;a trung t&acirc;m : C&oacute;</li>\r\n	<li>Cảm biến gạt mưa : Tự động gạt mưa</li>\r\n	<li>Đ&egrave;n pha tự bật khi trời tối : C&oacute;</li>\r\n	<li>Khởi động bằng n&uacute;t bấm : C&oacute;</li>\r\n	<li>Gương chiếu hậu ph&iacute;a ngo&agrave;i điều khiển điện : C&oacute;</li>\r\n	<li>Cửa k&iacute;nh điều khiển điện : Điều khiển điện với n&uacute;t một chạm tự động cửa người l&aacute;i</li>\r\n</ul>\r\n\r\n<h3><strong>Trang thiết bị an to&agrave;n</strong></h3>\r\n\r\n<ul>\r\n	<li>Hệ thống ph&acirc;n phối lực phanh điện tử EBD : C&oacute;</li>\r\n	<li>Hệ thống chống b&oacute; cứng phanh ABS : C&oacute;</li>\r\n	<li>T&uacute;i kh&iacute; d&agrave;nh cho người l&aacute;i v&agrave; h&agrave;nh kh&aacute;ch ph&iacute;a trư&oacute;c : C&oacute;</li>\r\n	<li>T&uacute;i kh&iacute; b&ecirc;n cho h&agrave;nh kh&aacute;ch trước : C&oacute;</li>\r\n	<li>T&uacute;i kh&iacute; r&egrave;m dọc hai b&ecirc;n trần xe : C&oacute;</li>\r\n	<li>Hệ thống hỗ trợ lực phanh khẩn cấp : C&oacute;</li>\r\n	<li>Hỗ trợ khởi h&agrave;nh ngang dốc : C&oacute;</li>\r\n	<li>Hệ thống c&acirc;n bằng điện tử : (ESP)</li>\r\n	<li>Cảm biến l&ugrave;i ph&iacute;a sau : C&oacute;</li>\r\n	<li>Hệ thống chống trộm : C&oacute;</li>\r\n	<li>T&uacute;i kh&iacute; bảo vệ đầu gối người l&aacute;i : C&oacute;</li>\r\n</ul>\r\n\r\n<h3><strong>K&iacute;ch thước v&agrave; trọng lượng</strong></h3>\r\n\r\n<ul>\r\n	<li>D&agrave;i x Rộng x Cao (mm) : 3982 x 1722 x 1495</li>\r\n	<li>Chiều d&agrave;i cơ sở (mm) : 2489</li>\r\n</ul>\r\n\r\n<h3><strong>Trang thiết bị b&ecirc;n ngo&agrave;i xe</strong></h3>\r\n\r\n<ul>\r\n	<li>Gương chiếc hậu t&iacute;ch hợp với đ&egrave;n b&aacute;o rẽ : C&oacute;</li>\r\n	<li>Gạt mưa k&iacute;nh sau : C&oacute;</li>\r\n	<li>C&aacute;nh lướt gi&oacute; ph&iacute;a sau : C&oacute;</li>\r\n</ul>\r\n\r\n<h3><strong>Trang thiết bị ngoại thất</strong></h3>\r\n\r\n<ul>\r\n	<li>Đ&egrave;n sương m&ugrave; : C&oacute;</li>\r\n	<li>Hốc h&uacute;t gi&oacute; thấp c&oacute; viền cr&ocirc;m : C&oacute;</li>\r\n	<li>Đ&egrave;n pha trước : Halogen</li>\r\n	<li>Ốp th&acirc;n xe kiểu thể thao ph&iacute;a trước : C&oacute;</li>\r\n	<li>Gương chiếu hậu t&iacute;ch hợp đ&egrave;n b&aacute;o rẽ : C&oacute;</li>\r\n	<li>Gạt mưa k&iacute;nh sau : C&oacute;</li>\r\n	<li>C&aacute;nh lướt gi&oacute; ph&iacute;a sau : C&oacute;</li>\r\n</ul>\r\n\r\n<h3><strong>Th&ocirc;ng tin Ti&ecirc;u thụ nhi&ecirc;n liệu (*)</strong></h3>\r\n\r\n<ul>\r\n	<li>Kết hợp/ Trong Đ&ocirc; thị / Ngo&agrave;i Đ&ocirc; thị : 5,8L/ 7,7L/ 4,7L tr&ecirc;n 100km</li>\r\n</ul>\r\n\r\n<h3><strong>Trang thiết bị Nội thất</strong></h3>\r\n\r\n<ul>\r\n	<li>Chất liệu ghế : Da pha nỉ</li>\r\n	<li>Ghế l&aacute;i trước : Điều chỉnh tay 4 hướng</li>\r\n	<li>Ghế sau gập được 60/40/ : C&oacute;</li>\r\n	<li>Tay l&aacute;i 3 chấu thể thao bọc da, t&iacute;ch hợp n&uacute;t điều khiển &acirc;m thanh : C&oacute;</li>\r\n	<li>Đ&egrave;n trang tr&iacute; trong xe : C&oacute;</li>\r\n	<li>Đ&egrave;n trần ph&iacute;a trước : Với đ&egrave;n xem bản đồ c&aacute; nh&acirc;n</li>\r\n	<li>M&agrave;n h&igrave;nh hiển thị th&ocirc;ng tin đa th&ocirc;ng tin : B&aacute;o ti&ecirc;u thụ nhi&ecirc;n liệu, qu&atilde;ng đường, điều chỉnh độ s&aacute;ng đ&egrave;n v&agrave; theo d&otilde;i nhiệt độ b&ecirc;n ngo&agrave;i</li>\r\n	<li>Điều h&ograve;a nhiệt độ : Tự động / Electronic Automatic Temperature Control</li>\r\n</ul>\r\n\r\n<h3><strong>T&uacute;i kh&iacute;</strong></h3>\r\n\r\n<ul>\r\n	<li>Hệ thống 6 t&uacute;i kh&iacute;</li>\r\n</ul>\r\n', '1', '2', '4', '27', 'Hoa Kỳ', '579000000');
INSERT INTO `products` VALUES ('14', 'Ford Explorer 2016', 'Ford Explorer.png', 'Vận Hành An Toàn Hiệu Năng Tuyệt Vời', '<p><img src=\"https://xehay.vn/uploads/images/2016/04/03/xehay-ford-explorer-2016-250416-29.jpg\" style=\"margin:0px\" /></p>\r\n\r\n<p>C&aacute;c chuy&ecirc;n gia của Ford đ&atilde; t&iacute;ch hợp th&ecirc;m cho chiếc SUV n&agrave;y bộ chớp lưới tản nhiệt chủ động, nhờ đ&oacute; hệ số cản kh&iacute; động của Explorer đ&atilde; giảm 5%.</p>\r\n\r\n<p>Đ&egrave;n sương m&ugrave; c&oacute; một r&atilde;nh nhỏ để hướng d&ograve;ng kh&ocirc;ng kh&iacute; chảy v&ograve;ng ra ngo&agrave;i b&aacute;nh trước nhằm triệt ti&ecirc;u đ&aacute;ng kể lực cản kh&iacute; động. Điều n&agrave;y cũng khẳng định rằng Ford bắt đầu để &yacute; nhiều hơn tới tiểu tiết khi thiết kế xe hơi.</p>\r\n\r\n<p><strong>Nội thất</strong></p>\r\n\r\n<p><img src=\"https://xehay.vn/uploads/images/2016/04/03/xehay-ford-explorer-2016-250416-30.jpg\" style=\"margin:0px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&ugrave;y chọn t&iacute;nh năng MyFord Touch bổ sung th&ecirc;m một m&agrave;n h&igrave;nh cảm ứng điều khiển trung t&acirc;m độ ph&acirc;n giải cao, đi k&egrave;m l&agrave; 2 m&agrave;n h&igrave;nh hiển thị mới được lắp đặt cho cụm đồng hồ. D&ugrave; đ&acirc;y kh&ocirc;ng phải l&agrave; hệ thống th&acirc;n thiện với người d&ugrave;ng nhất nhưng n&oacute; vẫn hoạt động kh&aacute; tốt nhờ được cải thiện mỗi năm.</p>\r\n\r\n<p>Điểm cộng tiếp theo cho Explorer 2016 ch&iacute;nh l&agrave; c&aacute;c ph&iacute;m cảm ứng đa điểm tr&ecirc;n bảng điều khiển trung t&acirc;m đ&atilde; kh&ocirc;ng c&ograve;n v&agrave; thay v&agrave;o đ&oacute; l&agrave; những ph&iacute;m bấm vật l&yacute; thật tay hơn để chỉnh &acirc;m thanh v&agrave; nhiệt độ.</p>\r\n\r\n<p><img src=\"https://xehay.vn/uploads/images/2016/04/03/xehay-ford-explorer-2016-250416-8.jpg\" style=\"margin:0px\" /></p>\r\n\r\n<p>Kh&ocirc;ng gian đựng h&agrave;ng h&oacute;a ph&iacute;a sau xe cũng l&agrave; điểm hấp dẫn thu h&uacute;t c&aacute;c kh&aacute;ch h&agrave;ng đến với chiếc SUV n&agrave;y. Khi gập cả h&agrave;ng ghế thứ 2 v&agrave; 3, chiếc xe c&oacute; sức chứa h&agrave;nh l&yacute; tối đa l&ecirc;n đến 2.313L. Tuy nhi&ecirc;n con số n&agrave;y vẫn được đ&aacute;nh gi&aacute; l&agrave; &iacute;t hơn so với hầu hết c&aacute;c đối thủ, đặc biệt l&agrave; Chevrolet Traverse v&agrave; GMC Acadia.</p>\r\n\r\n<p><strong>C&aacute;c biến thể v&agrave; trang bị chi tiết</strong></p>\r\n\r\n<p><img src=\"https://xehay.vn/uploads/images/2016/04/03/xehay-ford-explorer-2016-250416-37.jpg\" style=\"margin:0px\" /></p>\r\n\r\n<p>Trong khi đ&oacute;, ở Limited, Ford Explorer 2016 sở hữu to&agrave;n bộ c&aacute;c t&iacute;nh năng của XLT 202A, v&agrave; kh&aacute;ch biệt ở b&aacute;nh xe 20 inch, ngoại thất mạ cr&ocirc;m, gương chiếu hậu ngo&agrave;i gấp tự động, camera ph&iacute;a trước, mở cốp xe rảnh tay, ch&acirc;n ga điện c&oacute; thể điều chỉnh, ghế trước l&agrave;m m&aacute;t, h&agrave;ng ghế 2 sưởi điện, h&agrave;ng ghế 3 gập điện dễ d&agrave;ng, ghế h&agrave;nh kh&aacute;ch c&oacute; thể điều chỉnh 8 hướng, v&ocirc; lăng gật g&ugrave; sưởi điện, một ổ cắm điện 110V, hệ thống dẫn đường v&agrave; hệ thống &acirc;m thanh Sony 12 loa v&agrave; radio HD.</p>\r\n\r\n<p><img src=\"https://xehay.vn/uploads/images/2016/04/03/xehay-ford-explorer-2016-250416-26.jpg\" style=\"margin:0px\" /></p>\r\n\r\n<p>T&ugrave;y chọn cho bản Limited l&agrave; g&oacute;i 301A v&agrave; g&oacute;i 303A (với động cơ tăng &aacute;p 2,3L), cả 2 g&oacute;i n&agrave;y đều bao gồm đ&egrave;n pha chiếu s&aacute;ng tự động, cần gạt nước tự động, gương chống ch&oacute;i, hệ thống hỗ trợ đỗ xe tự động song song v&agrave; v&ocirc;ng g&oacute;c, cảnh b&aacute;o sai l&agrave;n, hệ thống gi&aacute;m s&aacute;t điểm m&ugrave;, cảnh b&aacute;o va chạm sau, ghế trước massage v&agrave; d&acirc;y an to&agrave;n bơm hơi cho 2 h&agrave;nh kh&aacute;ch ngo&agrave;i c&ugrave;ng của h&agrave;ng ghế thứ 2. B&ecirc;n cạnh đ&oacute;, những n&acirc;ng cấp ri&ecirc;ng gồm c&oacute; kiểm so&aacute;t h&agrave;nh tr&igrave;nh th&iacute;ch ứng, cảnh b&aacute;o va chạm trước, phanh khẩn cấp v&agrave; một g&oacute;i Trailer Tow.</p>\r\n\r\n<p><strong>Truyền động v&agrave; hiệu suất</strong></p>\r\n\r\n<p><img src=\"https://xehay.vn/uploads/images/2016/04/03/xehay-ford-explorer-2016-250416-27.jpg\" style=\"margin:0px\" /></p>\r\n\r\n<p>Ford Explorer ti&ecirc;u chuẩn, XLT v&agrave; Limited sẽ c&ugrave;ng sử dụng động cơ V6 3,5L sản sinh c&ocirc;ng suất 290 m&atilde; lực v&agrave; 346Nm m&ocirc;-men xoắn. Với động cơ n&agrave;y, kh&aacute;ch h&agrave;ng sẽ được lựa chọn hệ dẫn động cầu trước ti&ecirc;u chuẩn hoặc t&ugrave;y chọn dẫn động 4 b&aacute;nh to&agrave;n thời gian. Tất cả c&aacute;c cấp độ của Ford Explorer đều được trang bị hệ thống hỗ trợ khởi h&agrave;nh ngang dốc, hệ thống quản l&yacute; địa h&igrave;nh của Ford với 4 chế độ l&aacute;i t&ugrave;y chọn tối ưu h&oacute;a lực k&eacute;o điện tử trong c&aacute;c điều kiện đường kh&aacute;c nhau.</p>\r\n\r\n<p><img src=\"https://xehay.vn/uploads/images/2016/04/03/xehay-ford-explorer-2016-250416-33.jpg\" style=\"margin:0px\" /></p>\r\n\r\n<p>C&ugrave;ng sử dụng động cơ tăng &aacute;p V6 3,5L sản sinh c&ocirc;ng suất 365 m&atilde; lực v&agrave; 475Nm m&ocirc;-men xoắn l&agrave; Explorer Sport v&agrave; Platinum, kết hợp c&ugrave;ng hộp số tự động 6 cấp v&agrave; ti&ecirc;u chuẩn l&agrave; hệ dẫn động 4 b&aacute;nh to&agrave;n thời gian.</p>\r\n\r\n<p>Theo EPA, khối động cơ n&agrave;y c&oacute; mức ti&ecirc;u thụ nhi&ecirc;n liệu chỉ 7,65L / 100km v&agrave; sức k&eacute;o l&ecirc;n đến 2.268kg, đồng thời n&oacute; cũng c&oacute; khả năng tăng tốc mạnh mẽ từ 0 &ndash; 96 km/h chỉ với 6,3 gi&acirc;y. Tất cả những con số tr&ecirc;n đều rất ấn tượng cho một chiếc SUV cỡ lớn như Explorer.</p>\r\n\r\n<p><strong>Vận h&agrave;nh v&agrave; cảm gi&aacute;c l&aacute;i</strong></p>\r\n\r\n<p><img src=\"https://xehay.vn/uploads/images/2016/04/03/xehay-ford-explorer-2016-250416-9.jpg\" style=\"margin:0px\" /></p>\r\n\r\n<p>Ford Explorer 2016 sẽ đem đến cho bạn những chuyến đi &ecirc;m mượt tr&ecirc;n đoạn đường cao tốc, đặc biệt xe c&oacute; khả năng c&aacute;ch &acirc;m giữ y&ecirc;n tĩnh cho khoang cabin kh&aacute; tốt, h&agrave;nh kh&aacute;ch sẽ chỉ thực sự thấy bất tiện khi xe di chuyển tr&ecirc;n những đoạn đường gồ ghề, nhiều ổ g&agrave; với b&aacute;nh xe 20 inch.</p>\r\n\r\n<p>Khả năng xử l&yacute; của Explorer cũng kh&aacute; đ&aacute;ng khen, tuy vậy do c&oacute; &ldquo;th&acirc;n h&igrave;nh&rdquo; vạm vỡ hơn đồng thời lại &iacute;t trang bị động cơ hơn c&aacute;c đối thủ kh&aacute;c n&ecirc;n Explorer dường như bị l&eacute;p vế.</p>\r\n\r\n<p><img src=\"https://xehay.vn/uploads/images/2016/04/03/xehay-ford-explorer-2016-250416-7.jpg\" style=\"margin:0px\" /></p>\r\n\r\n<p>Điểm cộng của Explorer ch&iacute;nh l&agrave; hệ thống treo thể thao t&ugrave;y chỉnh tr&ecirc;n bản Sport gi&uacute;p xe phản ứng nhanh hơn, đem đến sự tự tin cho c&aacute;c t&agrave;i xế. Đặc biệt, d&ugrave; c&oacute; mạnh mẽ hơn th&igrave; Ford Explorer Sport vẫn vận h&agrave;nh rất &ecirc;m &aacute;i tạo kh&ocirc;ng gian thoải m&aacute;i cho người ngồi tr&ecirc;n xe.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hai biến thể Sport v&agrave; Platinum với động cơ tăng &aacute;p V6 ti&ecirc;u chuẩn c&oacute; sức mạnh gần như khối V8 mang lại khả năng tăng tốc vượt trội so với c&aacute;c đối thủ c&ugrave;ng ph&acirc;n kh&uacute;c. Động cơ V6 cơ sở mặc d&ugrave; kh&ocirc;ng qu&aacute; mạnh mẽ nhưng cũng đủ để đ&aacute;p ứng kỳ vọng của hầu hết c&aacute;c kh&aacute;ch h&agrave;ng đối với một chiếc crossover 3 h&agrave;ng ghế. Trong khi đ&oacute; động cơ tăng &aacute;p 2,3L 4 xy-lanh mạnh nhất chắc chắn sẽ thu h&uacute;t những vị kh&aacute;ch y&ecirc;u th&iacute;ch tốc độ v&agrave; sự bứt ph&aacute;, sức mạnh của khối động cơ n&agrave;y sẽ được thể hiện r&otilde; n&eacute;t khi được di chuyển tr&ecirc;n đoạn đường cao tốc.</p>\r\n\r\n<p><strong>Gi&aacute; b&aacute;n v&agrave; đối thủ</strong></p>\r\n\r\n<p><img src=\"https://xehay.vn/uploads/images/2016/04/03/xehay-ford-explorer-2016-250416-26.jpg\" style=\"margin:0px\" /></p>\r\n\r\n<p>Theo dự kiến, cuối năm nay Ford Explorer 2016 sẽ ch&iacute;nh thức được &ldquo;cập cảng&rdquo; Việt Nam. Hiện tại, xe được ph&acirc;n phối tại thị trường Mỹ với gi&aacute; khởi điểm từ 32.000 USD (tương đương 711 triệu đồng).</p>\r\n\r\n<p>Đặc biệt, Explorer Platinum 2016 sẽ đến tay kh&aacute;ch h&agrave;ng v&agrave;o th&aacute;ng 9 tới tại thị trường Mỹ với mức gi&aacute; khởi điểm l&agrave; 53.495 USD (gần 1,2 tỷ đồng).</p>\r\n\r\n<p>Trong c&ugrave;ng ph&acirc;n kh&uacute;c, c&oacute; rất nhiều đối thủ c&ugrave;ng sở hữu kh&ocirc;ng gian nội thất v&agrave; chỗ duỗi ch&acirc;n của h&agrave;ng ghế thứ 3 rộng r&atilde;i giống như Explorer, thế nhưng Explorer lại kh&oacute; c&oacute; thể kiếm được chỗ để xe khi mang tr&ecirc;n m&igrave;nh một &ldquo;th&acirc;n h&igrave;nh vạm vỡ&rdquo; hơn những đối thủ kh&aacute;c như Chevrolet Traverse 2016, GMC Acadia 2016, Toyota Highlander hay Honda Pilot 2016.</p>\r\n', '1', '3', '4', '24', 'Hoa Kỳ', '2180000000');
INSERT INTO `products` VALUES ('15', 'Ford EcoSport Titanium Black Edition', 'EcoSport-Black-Ford.jpg', 'Thiết kế năng động mạnh mẽ, thông minh vượt trội, chất lượng chuẩn quốc tế, an toàn và thân thiện với môi trường.', '<p>&nbsp;</p>\r\n\r\n<h1>FORD ECOSPORT TITANIUM BLACK EDITION</h1>\r\n\r\n<p><a href=\"http://baogiaxeford.com/wp-content/uploads/2016/09/14317543_654582694701477_5601191262889196889_n.jpg\"><img alt=\"14317543_654582694701477_5601191262889196889_n\" src=\"http://baogiaxeford.com/wp-content/uploads/2016/09/14317543_654582694701477_5601191262889196889_n.jpg\" style=\"height:454px; margin:5px auto; width:829px\" /></a></p>\r\n\r\n<p><strong>Foprd Ecosport Black Edition 2016</strong> c&ugrave;ng một số n&acirc;ng cấp mới về ngoại h&igrave;nh tr&ecirc;n c&aacute;c phi&ecirc;n bản Ford EcoSport 2016 c&ograve;n lại. Hơn nữa, với khẩu hiệu &ldquo;T&aacute;o bạo sắc đen mới&rdquo;, phi&ecirc;n bản đặc biệt <strong>Ford</strong> <strong>Ecosport Black Edition 2016</strong> cũng sẽ được giới thiệu với nhiều thiết kế t&aacute;o bạo về ngoại thất như lưới tản nhiệt, chụp gương, la-zăng hợp kim v&agrave; gi&aacute; n&oacute;c được sơn đen b&oacute;ng c&aacute; t&iacute;nh.</p>\r\n\r\n<p><a href=\"http://baogiaxeford.com/wp-content/uploads/2016/09/ford-ecosport-black.png\"><img alt=\"ford-ecosport-black\" src=\"http://baogiaxeford.com/wp-content/uploads/2016/09/ford-ecosport-black.png\" style=\"height:538px; margin:5px auto; width:772px\" /></a></p>\r\n\r\n<h2 style=\"font-style:inherit\">NGOẠI THẤT</h2>\r\n\r\n<p><strong>Đầu xe Ford EcoSport Titanium Black Edition</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Ford EcoSport Titanium Black Edition\" src=\"http://baogiaxeford.com/wp-content/uploads/2015/01/ngoai-that-xe-ford-ecosport-black-edition-1.jpg\" style=\"height:380px; margin:0px; width:645px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2 style=\"font-style:inherit\">TIỆN NGHI</h2>\r\n\r\n<p><strong>Tablo Media Ford EcoSport Titanium Black Edition</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Ford EcoSport Titanium Black Edition\" src=\"http://baogiaxeford.com/wp-content/uploads/2015/01/taplo-ford-ecosport.jpg\" style=\"height:380px; margin:0px; width:645px\" /></p>\r\n\r\n<p><strong>TH&Ocirc;NG SỐ KỸ THUẬT</strong></p>\r\n\r\n<p><img alt=\"Ford EcoSport Titanium Black Edition\" src=\"http://baogiaxeford.com/wp-content/uploads/2015/01/mau-trang-xe-ford-ecosport-black-edition.jpg\" style=\"height:380px; margin:0px; width:645px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table cellspacing=\"0\" style=\"width:694px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>K&iacute;ch thước</strong></td>\r\n			<td><strong>Titanium 1.5P AT</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>D&agrave;i x Rộng x Cao</strong></td>\r\n			<td>4,241 x 1,765 x 1,693</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Chiều d&agrave;i cơ sở (mm)</strong></td>\r\n			<td>2521</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Chiều rộng cơ sở trước sau</strong></td>\r\n			<td>1,478/1,465</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>B&aacute;n k&iacute;nh quay v&ograve;ng (m)</strong></td>\r\n			<td>5.3</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Dung t&iacute;ch th&ugrave;ng nhi&ecirc;n liệu (L)</strong></td>\r\n			<td>52</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Động cơ</strong></td>\r\n			<td><strong>Titanium 1.5P AT</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Kiểu động cơ/ Engine Type</strong></td>\r\n			<td>Xăng 1.5L Duratec 16 Van</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Dung t&iacute;ch xi lanh</strong></td>\r\n			<td>1,498 cc</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>C&ocirc;ng suất cực đại (Ps/v&ograve;ng/ph&uacute;t)</strong></td>\r\n			<td>112 @ 6300 (or 82,3 kW/ 6300)</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>M&ocirc; men xoắn cực đại (Nm/v&ograve;ng/ph&uacute;t)</strong></td>\r\n			<td>140 @ 4500</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hệ thống nhi&ecirc;n liệu</strong></td>\r\n			<td>Phun nhi&ecirc;n liệu điện tử đa điểm</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hộp số</strong></td>\r\n			<td>Hộp số tự động 6 cấp ly hợp k&eacute;p</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hệ thống l&aacute;i</strong></td>\r\n			<td>Trợ lực điện điều khiển điện tử</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hệ thống giảm x&oacute;c</strong></td>\r\n			<td><strong>Titanium 1.5P AT</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Trước</strong></td>\r\n			<td>Kiểu Macpherson với thanh c&acirc;n bằng</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Sau</strong></td>\r\n			<td>Thanh xoắn</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Brakes: Front-Rear</strong></td>\r\n			<td>Đĩa &ndash; Tang trống</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>B&aacute;nh xe</strong></td>\r\n			<td>V&agrave;nh (m&acirc;m) đ&uacute;c hợp kim 16/16&Prime; Alloy</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Lốp</strong></td>\r\n			<td>195/50 R16</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>C&aacute;c trang thiết bị an to&agrave;n</strong></td>\r\n			<td><strong>Titanium 1.5P AT</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>T&uacute;i kh&iacute; d&agrave;nh cho người l&aacute;i v&agrave; h&agrave;nh kh&aacute;ch ph&iacute;a trư&oacute;c</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>T&uacute;i kh&iacute; r&egrave;m dọc hai b&ecirc;n trần xe</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>T&uacute;i kh&iacute; r&egrave;m dọc hai b&ecirc;n trần xe</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>T&uacute;i kh&iacute; bảo vệ đầu gối người l&aacute;i</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hệ thống chống b&oacute; cứng phanh (ABS)</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hệ thống ph&acirc;n phối lực phanh điện tử (EBD)</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hỗ trợ khởi h&agrave;nh ngang dốc (HLA)</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hệ thống c&acirc;n bằng điện tử (ESP)</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Cảm biến l&ugrave;i ph&iacute;a sau</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hệ thống chống trộm</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Trang thiết bị ngoại thất</strong></td>\r\n			<td><strong>Titanium 1.5P AT</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Đ&egrave;n pha trước</strong></td>\r\n			<td>Halogen</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Đ&egrave;n sương m&ugrave;</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Gương chiếu hậu t&iacute;ch hợp đ&egrave;n b&aacute;o rẽ</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Gạt mưa k&iacute;nh sau</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Cửa sổ trời điều khiển điện</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Trang thiết bị nội thất</strong></td>\r\n			<td><strong>Titanium 1.5P AT</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Điều h&ograve;a nhiệt độ</strong></td>\r\n			<td>Tự động</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Ghế</strong></td>\r\n			<td>Điều chỉnh tay 4 hướng</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Chất liệu ghế</strong></td>\r\n			<td>Da</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Ghế sau gập được 60/40</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Tay l&aacute;i 3 chấu thể thao bọc da, t&iacute;ch hợp n&uacute;t điều khiển &acirc;m thanh</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Đ&egrave;n chiếu s&aacute;ng trong xe</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Đ&egrave;n trần ph&iacute;a trước</strong></td>\r\n			<td>Với đ&egrave;n xem bản đồ c&aacute; nh&acirc;n</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hộp giữ lạnh</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>M&agrave;n h&igrave;nh hiển thị th&ocirc;ng tin đa th&ocirc;ng tin</strong></td>\r\n			<td>B&aacute;o ti&ecirc;u thụ nhi&ecirc;n liệu, qu&atilde;ng đường, điều chỉnh độ s&aacute;ng đ&egrave;n v&agrave; theo d&otilde;i nhiệt độ b&ecirc;n ngo&agrave;i</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>&Acirc;m thanh</strong></td>\r\n			<td><strong>Titanium 1.5P AT</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hệ thống &acirc;m thanh</strong></td>\r\n			<td>CD 1 đĩa/ đ&agrave;i AM/ FM/ Single CD In-dash/ AM/ FM</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Kết nối USB, AUX</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hệ thống loa</strong></td>\r\n			<td>6</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hệ thống kết nối SYNC của Microsoft</strong></td>\r\n			<td>Điều khiển bằng giọng n&oacute;i/ Voice control</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Hệ thống điện</strong></td>\r\n			<td><strong>Titanium 1.5P AT</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Kh&oacute;a cửa điện điều khiển từ xa</strong></td>\r\n			<td>Kh&oacute;a điện th&ocirc;ng minh</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Khởi động bằng n&uacute;t bấm</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Kh&oacute;a trung t&acirc;m</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Gương chiếu hậu ph&iacute;a ngo&agrave;i điều khiển điện</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Cửa k&iacute;nh điều khiển điện</strong></td>\r\n			<td>Điều khiển điện với n&uacute;t một chạm tự động cửa người l&aacute;i</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Cảm biến gạt mưa</strong></td>\r\n			<td>Tự động gạt mưa</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Đ&egrave;n pha tự bật khi trời tối</strong></td>\r\n			<td>C&oacute;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '1', '4', '4', '4', 'Hoa Kỳ', '664000000');
INSERT INTO `products` VALUES ('16', 'Ford Ranger 2016', 'Ford Ranger.png', 'Ford Ranger 2016 Wildtrak: quá nhiều ưu điểm', '<h2>&nbsp;</h2>\r\n\r\n<p><img alt=\"Đánh giá xe Ford Ranger 2016, thiết kế Ranger Wildtrak 2016 có gì nổi bật? 13\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/07/ford-ranger-2016-13.jpg\" style=\"height:382px; margin:0px auto 2px; width:581px\" /></p>\r\n\r\n<p>K&iacute;ch thước tổng thể D&agrave;i x Rộng x Cao của Ranger Wildtrak 3.2L 4&times;4 AT lần lượt đạt 5.362 x 1.860 x 1.848 (mm) c&ugrave;ng chiều d&agrave;i cơ sở 3.220 (mm) v&agrave; khoảng s&aacute;ng gầm 230 (mm). C&aacute;c phi&ecirc;n bản c&ograve;n lại giữ nguy&ecirc;n mọi th&ocirc;ng số trừ chiều cao tổng thể giảm đi đ&ocirc;i ch&uacute;t, từ XL cho đến XLS, XLT v&agrave; Wildrtak 2.2L c&oacute; trần xe c&aacute;ch mặt đất 1.815 (mm).</p>\r\n\r\n<p><img alt=\"Đánh giá xe Ford Ranger 2016, thiết kế Ranger Wildtrak 2016 có gì nổi bật? 8\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/07/ford-ranger-2016-8.jpg\" style=\"height:384px; margin:0px auto 2px; width:580px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Danh s&aacute;ch trang bị ngoại thất ti&ecirc;u chuẩn ở c&aacute;c phi&ecirc;n bản Ranger 2016 kh&aacute; đa dạng, tạo n&ecirc;n những chiếc xe đặc trưng của ri&ecirc;ng bạn ngay từ khi lăn b&aacute;nh khỏi showroom. Hai mẫu Wildtrak hiển nhi&ecirc;n &ldquo;ngầu&rdquo; v&agrave; tiện nghi hơn hẳn với m&acirc;m xe hợp kim s&aacute;u chấu k&eacute;p 18-inch, tay nắm cửa sơn đen b&oacute;ng, gương chiếu chỉnh/gập điện, t&iacute;ch hợp đ&egrave;n b&aacute;o rẽ, sấy điện c&ugrave;ng bộ trang bị thể thao bao gồm: thanh g&aacute; n&oacute;c, bệ l&ecirc;n xuống v&agrave; tấm l&oacute;t th&ugrave;ng h&agrave;ng. Những người anh em c&ograve;n lại cũng kh&ocirc;ng qu&aacute; k&eacute;m cạnh với tay nắm cửa mạ chrome hoặc sơn c&ugrave;ng m&agrave;u th&acirc;n xe, la-zăng hợp kim k&iacute;ch thước 17-inch hoặc m&acirc;m th&eacute;p 16-inch.</p>\r\n\r\n<p><img alt=\"Đánh giá xe Ford Ranger 2016, thiết kế Ranger Wildtrak 2016 có gì nổi bật? 6\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/07/ford-ranger-2016-16.jpg\" style=\"height:351px; margin:0px auto 2px; width:659px\" /></p>\r\n\r\n<p>Thiết kế ph&iacute;a sau của Ranger đơn giản, kh&ocirc;ng cầu k&igrave; nhưng vẫn bật l&ecirc;n được c&aacute; t&iacute;nh ri&ecirc;ng khi đi cạnh những mẫu b&aacute;n tải hiện hữu tr&ecirc;n thị trường. Ba &ocirc; vu&ocirc;ng cỡ lớn của cụm đ&egrave;n hậu xếp dọc s&aacute;t hai b&ecirc;n th&acirc;n xe cho ph&eacute;p c&aacute;c phương tiện theo đu&ocirc;i Ranger khả năng quan s&aacute;t r&otilde; r&agrave;ng, ngo&agrave;i ra c&ograve;n c&oacute; d&atilde;y đ&egrave;n LED b&aacute;o phanh phụ bố tr&iacute; kh&aacute; cao tr&ecirc;n phần khung s&aacute;t với cabin h&agrave;nh kh&aacute;ch.</p>\r\n\r\n<p><img alt=\"Ford Ranger 2016 giá bao nhiêu, đánh giá ford Ranger 2016 Wildtrak\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/07/ford-ranger-2016-14.jpg\" style=\"height:369px; margin:0px auto 2px; width:660px\" /></p>\r\n\r\n<p>Nắp chắn th&ugrave;ng h&agrave;ng tạo điểm nhấn với một g&acirc;n dập nổi sắc n&eacute;t chạy cắt ngang logo ovan m&agrave;u xanh biển, t&ecirc;n phi&ecirc;n bản đặt ở g&oacute;c phải nhưng nếu ở Wildtrak l&agrave; dạng tem d&aacute;n trong khi XLS, XLT v&agrave; XL được dập nổi v&agrave; mạ chrome b&oacute;ng bẩy. Cản sau ki&ecirc;m bệ l&ecirc;n bố tr&iacute; với độ cao hợp l&yacute; c&ugrave;ng c&aacute;c r&atilde;nh chống trượt gi&uacute;p việc l&ecirc;n xuống, chất dỡ h&agrave;ng h&ograve;a dễ d&agrave;ng hơn.</p>\r\n\r\n<p><img alt=\"Ford Ranger 2016 giá bao nhiêu, đánh giá ford Ranger 2016 Wildtrak 15\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/07/ford-ranger-2016-15.jpg\" style=\"height:386px; margin:0px 0px 2px; width:776px\" /></p>\r\n\r\n<p>Th&ugrave;ng h&agrave;ng của Ranger c&oacute; k&iacute;ch thước 1.450 x 1.560 x 450 (mm), kh&ocirc;ng qu&aacute; nổi trội so với c&aacute;c đối thủ như Hilux 2016, BT-50 facelift 2016 hay Navara 2015 vốn đ&atilde; c&oacute; th&ugrave;ng h&agrave;ng ph&iacute;a sau rộng r&atilde;i hơn kh&aacute; nhiều. Tuy nhi&ecirc;n Ford đ&atilde; b&ugrave; đắp điểm yếu đ&oacute; bằng một cổng nguồn 12V ngay hốc b&aacute;nh xe, rất tiện lợi khi bạn cần sử dụng điện trong những chuyến đi d&atilde; ngoại, cắm trại c&ugrave;ng người th&acirc;n v&agrave; gia đ<strong>&igrave;nh.</strong></p>\r\n\r\n<p><strong>Nội Thất</strong></p>\r\n\r\n<p>H&agrave;ng ghế sau rộng r&atilde;i đủ cho ba người lớn với c&aacute;c tựa đầu đầy đặn nhưng tựa lưng phẳng v&agrave; độ nghi&ecirc;ng &iacute;t n&ecirc;n sẽ kh&ocirc;ng mấy thoải m&aacute;i nếu cần phải đi đường d&agrave;i, cộng th&ecirc;m s&agrave;n xe nh&ocirc; cao ở vị tr&iacute; ngồi giữa khiến ch&acirc;n bị g&ograve; b&oacute; đ&ocirc;i ch&uacute;t.</p>\r\n\r\n<p><img alt=\"Ford Ranger 2016 giá bao nhiêu, đánh giá ford Ranger 2016 Wildtrak 10\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/07/ford-ranger-2016-10.jpg\" style=\"height:410px; margin:0px auto 2px; width:649px\" /></p>\r\n\r\n<p>V&ocirc;-lăng của Ranger l&agrave; dạng bốn chấu v&agrave; chỉ được bọc da ở hai phi&ecirc;n bản Wildtrak c&ugrave;ng XLT, c&aacute;c n&uacute;t bấm t&iacute;ch hợp đ&atilde; &ldquo;phủ k&iacute;n&rdquo; khu vực 3 giờ v&agrave; 6 giờ n&ecirc;n sẽ mất ch&uacute;t &iacute;t thời gian để l&agrave;m quen v&agrave; th&agrave;nh thạo sử dụng những chức năng hiện đại của Wildtrak 3.2L. Với một thao t&aacute;c gạt lẫy, cột tay l&aacute;i sẽ chuyển sang trạng th&aacute;i tự do để bạn dễ d&agrave;ng chọn tư thế vần v&ocirc;-lăng thoải m&aacute;i nhất. K&iacute;ch cỡ đường k&iacute;nh v&ocirc;-lăng rất vừa vặn, cảm gi&aacute;c xoay v&agrave; miết tay tr&ecirc;n bề mặt da hết sức dễ chịu v&agrave; thoải m&aacute;i nhờ độ ho&agrave;n thiện tốt.</p>\r\n\r\n<p><img alt=\"Ford Ranger 2016 giá bao nhiêu, đánh giá ford Ranger 2016 Wildtrak 2\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/07/ford-ranger-2016-2.jpg\" style=\"height:366px; margin:0px auto 2px; width:682px\" /></p>\r\n\r\n<p>Những d&ograve;ng xe Ford lu&ocirc;n mang đến &acirc;m thanh dập cửa chắc chắn, an t&acirc;m v&agrave; đ&aacute;ng tin cậy, Ranger cũng kh&ocirc;ng ngoại lệ nhờ chất liệu t&ocirc;n d&agrave;y, cứng c&aacute;p. B&ecirc;n trong, c&aacute;c h&agrave;nh kh&aacute;ch của Wildtrak c&oacute; được phần g&aacute;c tay phủ da &ecirc;m &aacute;i, c&aacute;c lẫy chuyển số thiết kế lịch l&atilde;m v&agrave; mạ chrome sang trọng, ri&ecirc;ng vị tr&iacute; người l&aacute;i bổ sung t&iacute;nh năng chống kẹt cho cửa k&iacute;nh chỉnh điện một chạm.</p>\r\n\r\n<h3 style=\"color:#333333; font-style:italic\"><strong>C&aacute;c tiện &iacute;ch tr&ecirc;n Ford Ranger 2016</strong></h3>\r\n\r\n<p><img alt=\"Ford Ranger 2016 giá bao nhiêu, đánh giá ford Ranger 2016 Wildtrak 4\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/07/ford-ranger-2016-4.jpg\" style=\"height:384px; margin:0px auto 2px; width:675px\" /></p>\r\n\r\n<ul>\r\n	<li><strong>Ford Ranger 2016</strong> được trang bị những t&iacute;nh năng th&ocirc;ng minh m&agrave; bạn kh&oacute; l&ograve;ng t&igrave;m thấy ở bất kỳ mẫu xe b&aacute;n tải n&agrave;o. V&iacute; dụ như SYNC&trade;2 với chức năng thực hiện c&aacute;c cuộc gọi, điều chỉnh nhiệt độ trong xe, hoặc sử dụng c&aacute;c t&iacute;nh năng hữu &iacute;ch từ c&aacute;c ứng dụng tr&ecirc;n chiếc di động của bạn. Ho&agrave;n to&agrave;n rảnh tay. M&agrave; kh&ocirc;ng phải chiếc xe du lịch n&agrave;o cũng c&oacute;.</li>\r\n	<li>Hệ thống SYNC&trade;2 cho ph&eacute;p bạn c&ugrave;ng l&uacute;c l&agrave;m nhiều việc như: hai tay kh&ocirc;ng rời khỏi v&ocirc; lăng, mắt lu&ocirc;n d&otilde;i theo h&agrave;nh tr&igrave;nh, bạn vẫn c&oacute; thể nghe-gọi điện thoại, hoặc bật những bản nhạc y&ecirc;u th&iacute;ch chỉ bằng v&agrave;i khẩu lệnh đơn giản.Kh&aacute;ch h&agrave;ng rất h&agrave;i l&ograve;ng khi điều khiển xe m&agrave; vẫn tập trung được sự an to&agrave;n tuyệt đối khi l&aacute;i xe.</li>\r\n	<li>Th&ocirc;ng qua 3 cổng kết nối tr&ecirc;n Ranger Mới: hai cổng USB v&agrave; đầu đọc thẻ SD, giờ đ&acirc;y bạn c&oacute; rất nhiều lựa chọn h&igrave;nh thức giải tr&iacute; tr&ecirc;n xe: nghe nhạc v&agrave; xem ảnh tr&ecirc;n m&agrave;n h&igrave;nh cảm ứng SYNC&trade;2. Bạn cũng c&oacute; thể vừa l&aacute;i xe vừa sạc v&agrave; sử dụng c&aacute;c thiết bị giải tr&iacute; qua cổng USB.</li>\r\n</ul>\r\n\r\n<p>Hệ thống sử dụng cảm biến đ&aacute;nh dấu l&agrave;n đường. Nếu bạn v&ocirc; t&igrave;nh đi chệch l&agrave;n đường quy định, hệ thống sẽ lập tức rung nhẹ v&ocirc;-lăng để cảnh b&aacute;o. Nếu bạn vẫn chưa nhận ra, hệ thống sẽ t&aacute;c động một lực nhẹ điều chỉnh v&ocirc; lăng để đưa xe trở về đ&uacute;ng l&agrave;n đường.</p>\r\n\r\n<p><img alt=\"Đánh giá xe Ford Ranger 2016, thiết kế Ranger Wildtrak 2016 có gì nổi bật?\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/07/ford-ranger-2016-18.jpg\" style=\"height:312px; margin:0px auto 2px; width:540px\" /></p>\r\n\r\n<p>An to&agrave;n hơn y&ecirc;n t&acirc;m hơn nhiều khi sở hữu tr&ecirc;n m&igrave;nh 6 t&uacute;i kh&iacute; an to&agrave;n: Vững ch&atilde;i th&ocirc;i chưa đủ, c&ograve;n phải an to&agrave;n. T&iacute;nh năng an to&agrave;n của <strong>xe ford Ranger 2016</strong> Mới được trang bị tới 6 t&uacute;i kh&iacute; bao gồm t&uacute;i kh&iacute; b&ecirc;n h&ocirc;ng v&agrave; t&uacute;i kh&iacute; r&egrave;m, kết hợp c&ugrave;ng Hệ thống C&acirc;n bằng Điện tử to&agrave;n diện v&agrave; C&ocirc;ng nghệ Hỗ trợ người l&aacute;i gi&uacute;p xe giảm thiểu va chạm v&agrave; lu&ocirc;n đi đ&uacute;ng l&agrave;n đường. Ranger Mới an to&agrave;n hơn bao giờ hết.</p>\r\n\r\n<p>Hệ thống đ&egrave;n chiếu s&aacute;ng th&ugrave;ng h&agrave;ng ph&iacute;a sau lu&ocirc;n đảm bảo cung cấp đủ &aacute;nh s&aacute;ng cần thiết, bất kể ng&agrave;y hay đ&ecirc;m. Kh&ocirc;ng chỉ vậy, Ranger Mới c&ograve;n được trang bị ổ cắm nguồn 230V cho ph&eacute;p bạn sạc trực tiếp điện thoại, m&aacute;y t&iacute;nh hay c&aacute;c thiết bị điện tử kh&aacute;c, bất kể bạn ở đ&acirc;u.</p>\r\n', '1', '5', '4', '23', 'Hoa Kỳ', '918000000');
INSERT INTO `products` VALUES ('17', 'Honda BR-V 2016 ', 'Honda BR-V.jpg', 'Sự kết hợp giữa phong cách SUV và MPV', '<h2><a href=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom54-e98b_wm.jpg\"><img alt=\"Sự kết hợp giữa phong cách SUV và MPV là những gì người ta thấy được khi đánh giá xe Honda BR-V 2016.\" src=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom54-e98b_wm.jpg\" style=\"margin-bottom:10px; margin-top:10px; width:610px\" /></a></h2>\r\n\r\n<p>Honda BR-V được ph&aacute;t triển tr&ecirc;n nền tảng khung gầm của chiếc Honda Brio với k&iacute;ch thước tổng thể chiều d&agrave;i x rộng x cao tương ứng 4.455 x 1.735 x 1650 (mm) v&agrave; chiều d&agrave;i trục cơ sở 2.660 mm. Theo m&ocirc; tả của Honda, BR-V l&agrave; &ldquo;sự kết hợp giữa kiểu d&aacute;ng, cảm gi&aacute;c l&aacute;i của một chiếc SUV v&agrave; sức mạnh, khoang cabin của một chiếc MPV&rdquo;. Mẫu Crossover 7 chỗ n&agrave;y c&oacute; thiết kế mang đậm phong c&aacute;ch Mobilio. Một số chi tiết ngoại thất nổi bật của xe c&oacute; thể kể đến như như gầm cao, gi&aacute; n&oacute;c, m&acirc;m xe bằng hợp kim k&iacute;ch cỡ 16 inch, đ&egrave;n LED bao quanh cụm đ&egrave;n hậu.</p>\r\n\r\n<h2><strong>Đầu xe Honda BR-V 2016</strong></h2>\r\n\r\n<h2><a href=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom42-e58f_wm.jpg\"><img alt=\"Đánh giá xe Honda BR-V 2016: Đầu xe được thiết kế thể thao hơn Honda Brio hay Honda Mobilio.\" src=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom42-e58f_wm.jpg\" style=\"margin-bottom:10px; margin-top:10px; width:610px\" /></a></h2>\r\n\r\n<h2><strong>Th&acirc;n xe </strong><strong>Honda BR-V 2016</strong></h2>\r\n\r\n<h2><a href=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom55-cbd5_wm.jpg\"><img alt=\"Những người đánh giá xe Honda BR-V 2016 cho rằng phần thân của mẫu xe này mang đậm dáng dấp của chiếc MPV Mobilio.\" src=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom55-cbd5_wm.jpg\" style=\"margin-bottom:10px; margin-top:10px; width:610px\" /></a><br />\r\nThiết kế th&acirc;n xe của BR-V 2016 mang đậm d&aacute;ng dấp của chiếc MPV Mobilio nhưng gầm xe được n&acirc;ng cao hơn với bộ la-zăng k&iacute;ch thước lớn hơn 16 inch. B&ecirc;n cạnh đ&oacute;, tấm ốp viền dưới chạy quanh xe c&ugrave;ng gi&aacute; để đồ ph&iacute;a tr&ecirc;n n&oacute;c xe mang lại cho BR-V c&aacute;i nh&igrave;n hiện đại, mạnh mẽ hơn.<em>Những người đ&aacute;nh gi&aacute; xe Honda BR-V 2016 cho rằng phần th&acirc;n của mẫu xe n&agrave;y mang đậm d&aacute;ng dấp của chiếc MPV Mobilio</em></h2>\r\n\r\n<h2><strong>Đu&ocirc;i xe </strong><strong>Honda BR-V 2016</strong></h2>\r\n\r\n<h2><a href=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom43-54aa_wm.jpg\"><img alt=\"Thiết kế đuôi xe Honda BR-V 2016 được làm mới rất nhiều.\" src=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom43-54aa_wm.jpg\" style=\"margin-bottom:10px; margin-top:10px; width:610px\" /></a></h2>\r\n\r\n<h2>NỘI THẤT</h2>\r\n\r\n<p><a href=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom23-c6e0_wm.jpg\"><img alt=\"Nội thất của Honda BR-V 2016 được chăm chút hơn hẳn so với các mẫu Brio hay Mobilio\" src=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom23-c6e0_wm.jpg\" style=\"width:610px\" /></a></p>\r\n\r\n<h2><em>Nội thất của Honda BR-V 2016 được chăm ch&uacute;t hơn hẳn so với c&aacute;c mẫu Brio hay Mobilio</em></h2>\r\n\r\n<h2><strong>Hệ thống ghế ngồi của Honda BR-V 2016</strong></h2>\r\n\r\n<h2><a href=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom4-a332_wm.jpg\"><img alt=\"Đánh giá xe Honda BR-V 2016: Các ghế ngồi của Honda BR-V 2016 được thiết kế theo kiểu 2-3-2.\" src=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom4-a332_wm.jpg\" style=\"margin-bottom:10px; margin-top:10px; width:610px\" /></a></h2>\r\n\r\n<h2>Hệ thống ghế ngồi của Honda BR-V 2016 được thiết kế theo kiểu 2-3-2. H&agrave;ng ghế thứ nhất v&agrave; thứ hai mang lại kh&ocirc;ng gian rộng r&atilde;i cả ở vị tr&iacute; để ch&acirc;n v&agrave; khoảng kh&ocirc;ng tr&ecirc;n đầu cho người ngồi. H&agrave;ng ghế giữa c&oacute; thể gập theo tỉ lệ 60:40; h&agrave;ng ghế thứ ba c&oacute; thể gập theo tỉ lệ 50:50 v&agrave; đủ chỗ cho hai người trưởng th&agrave;nh. C&oacute; thể n&oacute;i, BR-V mang lại kh&ocirc;ng gian h&agrave;ng ghế thứ 3 hiệu quả nhất trong c&aacute;c mẫu xe 7 chỗ c&ugrave;ng ph&acirc;n kh&uacute;c.<em>C&aacute;c ghế ngồi của Honda BR-V 2016 được thiết kế theo kiểu 2-3-2</em></h2>\r\n\r\n<h2><strong>Kh&ocirc;ng gian chứa đồ của Honda BR-V 2016</strong></h2>\r\n\r\n<h2><a href=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom26-35f6_wm.jpg\"><img alt=\"Đánh giá xe Honda BR-V 2016: Không gian khoang hành lý của Honda BR-V 2016 có thể thay đổi linh hoạt, đáp ứng nhu cầu của người sử dụng.\" src=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom26-35f6_wm.jpg\" style=\"margin-bottom:10px; margin-top:10px; width:610px\" /></a></h2>\r\n\r\n<h2><em>Kh&ocirc;ng gian khoang h&agrave;nh l&yacute; của Honda BR-V 2016 c&oacute; thể thay đổi linh hoạt, đ&aacute;p ứng nhu cầu của người sử dụng</em></h2>\r\n\r\n<h2>VẬN H&Agrave;NH</h2>\r\n\r\n<h2><strong>Động cơ của Honda BR-V 2016</strong></h2>\r\n\r\n<h2><a href=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom27-7678_wm.jpg\"><img alt=\"Honda BR-V 2016 được trang bị động cơ xăng 1.5L i-VTEC.\" src=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom27-7678_wm.jpg\" style=\"margin-bottom:10px; margin-top:10px; width:610px\" /></a></h2>\r\n\r\n<h2><em>Honda BR-V 2016 được trang bị động cơ xăng 1.5L i-VTEC</em></h2>\r\n\r\n<p>Honda BR-V phi&ecirc;n bản d&agrave;nh cho thị trường Indonesia được trang bị động cơ xăng 1.5L i-VTEC cho c&ocirc;ng suất 117 m&atilde; lực, m&ocirc; men xoắn cực đại 145 Nm. Sức mạnh từ động cơ được dẫn truyền bởi hộp số s&agrave;n 6 cấp hoặc hộp số CVT. Honda chưa c&ocirc;ng bố chi tiết khả năng ti&ecirc;u thụ nhi&ecirc;n liệu của động cơ song c&ugrave;ng phi&ecirc;n bản động cơ 1.5L tr&ecirc;n chiếc Mobilio hay City đều cho khả năng ti&ecirc;u thụ hết sức tiết kiệm.</p>\r\n\r\n<h2><strong>Cảm gi&aacute;c l&aacute;i của Honda BR-V 2016</strong></h2>\r\n\r\n<h2><a href=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom12-c0a1_wm.jpg\"><img alt=\"Các chuyên gia đánh giá xe Honda BR-V 2016 đều nhận xét rằng xe cho cảm giác cầm lái khá tốt.\" src=\"http://img1.banxehoi.com/2015/12/15/0lMvM1tt/icom12-c0a1_wm.jpg\" style=\"margin-bottom:10px; margin-top:10px; width:610px\" /></a></h2>\r\n\r\n<h2><em>C&aacute;c chuy&ecirc;n gia đ&aacute;nh gi&aacute; xe Honda BR-V 2016 đều nhận x&eacute;t rằng xe cho cảm gi&aacute;c cầm l&aacute;i kh&aacute; tốt</em></h2>\r\n\r\n<p>C&aacute;c chuy&ecirc;n gia <strong>đ&aacute;nh gi&aacute; xe Honda BR-V 2016</strong> đ&atilde; c&oacute; nhiều b&agrave;i l&aacute;i thử mẫu xe n&agrave;y. Theo nhận x&eacute;t của họ, chiếc BR-V 2016 cho cảm gi&aacute;c cầm l&aacute;i kh&aacute; tốt. Tuy vậy, hệ thống treo của xe vẫn c&ograve;n hơi cứng nhắc-đặc điểm của c&aacute;c mẫu Honda n&oacute;i chung. Ngo&agrave;i ra, v&ocirc;-lăng của xe c&oacute; phần hơi nhẹ nhưng vẫn cho khả năng xử l&yacute; mượt khi v&agrave;o cua. Hệ thống phanh cũng được đ&aacute;nh gi&aacute; tốt.</p>\r\n', '1', '4', '5', '13', 'Nhật Bản', '370000000');
INSERT INTO `products` VALUES ('18', 'Honda Odyssey 2016', 'Honda Odyssey 2016.png', 'Mẫu xe đa dụng hạng sang dành cho doanh nhân và gia đình', '<h2>&nbsp;</h2>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201607/honda-odyssey-2016_115_7554.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201607/honda-odyssey-2016_115_7554.jpg\" style=\"width:100%px\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Ngoại thất</h2>\r\n\r\n<h3>Tổng quan về ngoại h&igrave;nh</h3>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201607/honda-odyssey-2016_038_1734.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201607/honda-odyssey-2016_038_1734.jpg\" style=\"width:100%px\" /></a></p>\r\n\r\n<p>Kh&aacute; bất ngờ khi Honda kho&aacute;c l&ecirc;n chiếc xe của m&igrave;nh d&aacute;ng vẻ gai g&oacute;c với nhiều đường n&eacute;t g&atilde;y gọn v&agrave; dứt kho&aacute;t hơn l&agrave; sự thanh tho&aacute;t thường thấy ở d&ograve;ng xe MPV, điều n&agrave;y hẳn khiến người đối diện đ&ocirc;i ch&uacute;t bỡ ngỡ l&uacute;c vừa tiếp x&uacute;c với Odyssey, tuy vậy Odyssey 2.4 CVT 2016 vẫn hết sức cuốn h&uacute;t nhờ v&agrave;o phong c&aacute;ch đậm chất đ&ocirc; thị v&agrave; những chi tiết ngoại thất hiện đại. Nh&igrave;n chung, việc dung h&ograve;a nhiều trường ph&aacute;i trong một bản vẽ thiết kế c&oacute; lẽ l&agrave; một nước cờ mạo hiểm của Honda, hoặc được l&ograve;ng số đ&ocirc;ng hoặc sẽ rơi v&agrave;o trường hợp &ldquo;ngoại h&igrave;nh k&eacute;n kh&aacute;ch&rdquo;.</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201607/honda-odyssey-2016_009_6865.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201607/honda-odyssey-2016_009_6865.jpg\" style=\"width:100%px\" /></a></p>\r\n\r\n<p>K&iacute;ch thước d&agrave;i - rộng - cao của Odyssey lần lượt l&agrave; 4.830 x 1.820 x 1.695 (mm), chiều d&agrave;i cơ sở đạt 2.900 (mm), khoảng s&aacute;ng gầm xe 150 (mm) v&agrave; b&aacute;n k&iacute;nh quay v&ograve;ng tối thiểu đạt 5,4 (m). C&aacute;c th&ocirc;ng số n&agrave;y đều nhỏ hơn khi đặt cạnh Kia Grand Sedona với tổng thể 5.115 x 1.985 x 1.755 (mm) D&agrave;i x Rộng x Cao, 3.060 mm khoảng c&aacute;ch hai trục, 163 mm khoảng s&aacute;ng gầm xe v&agrave; 5,6 m b&aacute;n k&iacute;nh quay v&ograve;ng. C&oacute; thể thấy, để đi lại thuận tiện hơn trong những con phố đ&ocirc;ng đ&uacute;c n&ecirc;n Honda đ&atilde; chọn c&aacute;ch tinh chỉnh để Odyssey gọn g&agrave;ng hơn v&agrave; hy sinh ch&uacute;t &iacute;t kh&ocirc;ng gian nội thất b&ecirc;n trong.</p>\r\n\r\n<h2>Nội thất</h2>\r\n\r\n<h3>Tổng quan khoang xe</h3>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201607/honda-odyssey-2016_029_6717.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201607/honda-odyssey-2016_029_6717.jpg\" style=\"width:100%px\" /></a></p>\r\n\r\n<p>Nh&igrave;n chung, những gi&aacute; trị b&ecirc;n trong Odyssey mang đến c&oacute; thể n&oacute;i l&agrave; tương xứng với vị thế của một chiếc xe tr&ecirc;n 2 tỷ đồng, từ chất liệu trang tr&iacute; da, gỗ v&agrave; chrom sang trọng cho đến c&aacute;ch sắp xếp v&agrave; bố tr&iacute; kh&ocirc;ng gian phục vụ h&agrave;nh kh&aacute;ch hết sức rộng r&atilde;i v&agrave; thoải m&aacute;i.</p>\r\n\r\n<p>C&aacute;c trang bị tuy vậy chưa đủ sức khiến người sử dụng &ldquo;cho&aacute;ng ngợp&rdquo;, vốn dĩ Honda tập trung nhiều hơn v&agrave;o yếu tố thực dụng v&agrave; sự bền bỉ d&agrave;nh cho kh&aacute;ch h&agrave;ng, vậy n&ecirc;n danh s&aacute;ch những t&iacute;nh năng tiện nghi ở Odyssey 2016 c&oacute; phần thiệt th&ograve;i so với c&aacute;c đối thủ kh&aacute;c.</p>\r\n\r\n<h2>Thiết bị tiện nghi</h2>\r\n\r\n<h3>Hệ thống nghe nh&igrave;n</h3>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201607/honda-odyssey-2016_021_3960.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201607/honda-odyssey-2016_021_3960.jpg\" style=\"width:100%px\" /></a></p>\r\n\r\n<p>So với đối thủ Sedona th&igrave; hệ thống giải tr&iacute; m&agrave; Odyssey phục vụ h&agrave;nh kh&aacute;ch thua k&eacute;m hơn đ&ocirc;i ch&uacute;t, với cụm 6 loa (bao gồm hai loa bổng tweeter) hỗ trợ radio AM/FM, nghe nhạc MP3/WMA, đầu CD một đĩa cũng như kết nối Bluetooth/USB/HDMI với c&aacute;c thiết bị ngoại vi kh&aacute;c. B&ecirc;n cạnh c&aacute;c n&uacute;t bấm tr&ecirc;n v&ocirc;-lăng th&igrave; c&aacute;c t&ugrave;y chỉnh &acirc;m thanh c&ograve;n xuất hiện ở m&agrave;n h&igrave;nh cảm ứng 7-inch. H&agrave;nh kh&aacute;ch ph&iacute;a sau c&ograve;n được &ldquo;thửa ri&ecirc;ng&rdquo; một m&agrave;n h&igrave;nh cỡ lớn d&agrave;nh cho việc thư gi&atilde;n.</p>\r\n\r\n<h2>Vận h&agrave;nh, cảm gi&aacute;c l&aacute;i v&agrave; an to&agrave;n</h2>\r\n\r\n<h3><em>Động cơ, hộp số v&agrave; hệ truyền động</em></h3>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201607/honda-odyssey-2016_169_2818.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201607/honda-odyssey-2016_169_2818.jpg\" style=\"width:100%px\" /></a></p>\r\n\r\n<p>Nhiệm vụ quan trọng nhất của Odyssey 2016 ch&iacute;nh l&agrave; g&oacute;p sức bảo vệ m&ocirc;i trường th&ocirc;ng qua tổ hợp c&ocirc;ng nghệ Earth Dream danh tiếng, bao gồm động cơ xăng 2.4L v&agrave; hộp số v&ocirc; cấp CVT.</p>\r\n\r\n<p>Dưới nắp capo l&agrave; 4 xi-lanh dung t&iacute;ch 2.356cc, trục cam đ&ocirc;i DOHC 16 van ứng dụng c&ocirc;ng nghệ van biến thi&ecirc;n i-VTEC, c&ocirc;ng suất tối đa 173 m&atilde; lực đại tại 6.200 v&ograve;ng/ph&uacute;t v&agrave; m&ocirc;-men xoắn cực đại 225 Nm ở 4.000 v&ograve;ng/ph&uacute;t. Hiển nhi&ecirc;n th&ocirc;ng số của Odyssey kh&ocirc;ng thể bắt kịp phi&ecirc;n bản m&aacute;y xăng của Sedona, m&atilde; động cơ Lambda 3.3 MPI dung t&iacute;ch 3.342cc với 266 m&atilde; lực v&agrave; 318 Nm.</p>\r\n\r\n<h3><strong>An to&agrave;n</strong></h3>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201608/honda-odyssey-2016_5345.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201608/honda-odyssey-2016_5345.jpg\" style=\"width:100%px\" /></a></p>\r\n\r\n<p>Với một mẫu xe danh cho gia đ&igrave;nh th&igrave; yếu tố an to&agrave;n thật sự quan trọng, v&agrave; Odyssey 2016 c&oacute; đầy đủ những trang bị v&agrave; t&iacute;nh năng cần thiết để mọi người c&ugrave;ng đi đến nơi, về đến chốn.</p>\r\n', '1', '6', '5', '9', 'Nhật Bản', '1990000000');
INSERT INTO `products` VALUES ('19', 'Audi TT Coupe 2016', 'Audi tt-coupe.png', 'Một sự lựa chọn hoàn hảo cho các tính đồ xe thể thao và những bạn trẻ thành đạt.', '<h3><strong>Ngoại thất</strong></h3>\r\n\r\n<p>Được bổ sung 7 m&agrave;u ngoại thất ho&agrave;n to&agrave;n mới, Audi TT sở hữu nhiều t&ugrave;y chọn m&agrave;u sắc hơn bất kỳ người tiền nhiệm n&agrave;o trước đ&oacute; với tổng cộng 11 m&agrave;u.</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0825_7420.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0825_7420.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0821_8337.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0821_8337.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0827_9587.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0827_9587.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><strong>Audi TT 2016 với thiết kế sang trọng, thể thao v&agrave; qu&yacute; ph&aacute;i</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i h&igrave;nh d&aacute;ng &quot;tr&ograve;n trịa&quot; đặc trưng, mẫu xe thể thao độc đ&aacute;o n&agrave;y sở hữu thiết kế mềm mại với những đường n&eacute;t bức ph&aacute; mạnh mẽ từ trước ra sau. Mặt trước xe kh&aacute; nổi bật với đường viền h&igrave;nh chữ V tr&ecirc;n ca-p&ocirc;, l&agrave;m gợi nhớ đến si&ecirc;u xe Audi R8 v&agrave; lưới tản nhiệt &quot;Single-Frame&quot; mạnh mẽ hơn với những đường ngang mạ cr&ocirc;m s&aacute;ng b&oacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0823_9306.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0823_9306.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0822_3649.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0822_3649.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0826_9648.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0826_9648.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><strong>Mũi xe c&oacute; thiết kế thể thao hiện đại, tương đồng với d&ograve;ng si&ecirc;u xe Audi R8 nổi tiếng</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Th&ecirc;m v&agrave;o đ&oacute;, thiết kế đ&egrave;n pha hiện đại v&agrave; mới mẻ khiến cho TT Coupe trở th&agrave;nh mẫu xe thể thao &quot;kh&ocirc;ng thể bị chối từ&quot;. Cụm đ&egrave;n pha Xenon Plus ti&ecirc;u chuẩn được t&iacute;ch hợp dải đ&egrave;n LED chạy ban ng&agrave;y được bố tr&iacute; vu&ocirc;ng g&oacute;c, đan ch&eacute;o nhau kh&aacute; ấn tượng. Thật thiếu s&oacute;t khi kh&ocirc;ng nhắc đến hệ thống đ&egrave;n b&aacute;o rẽ dạng tia bắt mắt được trang bị tr&ecirc;n cả đ&egrave;n pha v&agrave; đ&egrave;n hậu như l&agrave; t&ugrave;y chọn ti&ecirc;u chuẩn.</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0861_8046.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0861_8046.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0860_2626.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0860_2626.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0862_2809.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0862_2809.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0888_1867.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0888_1867.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0886_1127.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0886_1127.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><strong>Đu&ocirc;i xe &quot;quyến rũ&quot; c&ugrave;ng chiếc cốp rộng r&atilde;i của Audi TT Coupe 2016</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, để tăng th&ecirc;m sự sang trọng v&agrave; tinh tế trong thiết kế của m&igrave;nh, Audi bổ sung hai t&ugrave;y chọn trang bị đ&egrave;n pha v&agrave; đ&egrave;n hậu dạng LED hoặc Audi LED Matrix mới nhất.</p>\r\n\r\n<p>Kh&aacute;c với thiết kế mềm mại của phần đầu, th&acirc;n xe được bổ sung nhiều đường n&eacute;t mạnh mẽ, khỏe khoắn v&agrave; mang đến cảm gi&aacute;c của một chiếc xe thể thao đ&iacute;ch thực.</p>\r\n\r\n<p>Trong khi đ&oacute;, ở ph&iacute;a sau, ngo&agrave;i cụm đ&egrave;n hậu c&oacute; thiết kế kh&aacute; tinh tế, Audi TT Coupe c&ograve;n được trang bị sẵn c&aacute;nh lướt gi&oacute; c&oacute; thiết kế ấn tượng, tự động k&iacute;ch hoạt khi xe đạt tốc độ 120km/h. Hệ thống ống xả cũng l&agrave; điểm nhấn quan trọng trong thiết kế đu&ocirc;i xe. Với h&igrave;nh d&aacute;ng thể thao v&agrave; trang tr&iacute; bắt mắt, hai ống xả n&agrave;y l&agrave;m gợi nhớ đến thế hệ Audi TT đầu ti&ecirc;n, ra đời từ những năm 1998.</p>\r\n\r\n<h3>Nội thất</h3>\r\n\r\n<p>Audi đưa ra 3 t&ugrave;y chọn m&agrave;u nội thất cho mẫu xe thể thao n&agrave;y. Sự phối hợp m&agrave;u sắc tinh tế trong cabin xe mang lại cảm gi&aacute;c thanh lịch v&agrave; sang trọng. Hơn nữa, với 2+2 chỗ ngồi v&agrave; cốp xe c&oacute; dung t&iacute;ch 305 l&iacute;t (hơn 13 l&iacute;t so với thế hệ trươc), mẫu Coup&eacute; n&agrave;y kh&aacute; th&iacute;ch hợp để sử dụng thường ng&agrave;y hay trong những chuyến đi du lịch xa.</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0887_1107.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0887_1107.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0867_2972.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0867_2972.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0871_4839.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0871_4839.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0874_6462.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0874_6462.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><strong>Nội thất thể thao v&agrave; lịch l&atilde;m của Audi TT 2016</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nội thất mẫu xe thể thao n&agrave;y kh&aacute; đơn giản, do &aacute;p dụng nguy&ecirc;n tắc thiết kế &quot;Less is More&quot;. Tất cả c&aacute;c chi tiết được sắp xếp một c&aacute;ch r&otilde; r&agrave;ng, theo cấu tr&uacute;c nhất định nhưng kh&ocirc;ng k&eacute;m phần bắt mắt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0876_3887.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0876_3887.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0877_8798.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0877_8798.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/_img0880_8199.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/_img0880_8199.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bề mặt nội thất được l&agrave;m phẳng đi, kết hợp với đường viền nổi bật. Xe được trang bị bộ ghế thể thao ti&ecirc;u chuẩn, t&iacute;ch hợp c&ocirc;ng nghệ tựa đầu th&ocirc;ng minh. V&ocirc;-lăng bọc da sang trọng, viền chấu bằng nh&ocirc;m s&aacute;ng b&oacute;ng v&agrave; t&iacute;ch hợp nhiều chức năng.</p>\r\n\r\n<h3><strong>Động cơ</strong></h3>\r\n\r\n<p>Audi TT v&agrave; TTS c&oacute; ba t&ugrave;y chọn động cơ tăng &aacute;p 4 xy-lanh c&oacute; c&ocirc;ng suất trong khoảng từ 184 đến 310 m&atilde; lực, t&iacute;ch hợp sẵn bộ phun nhi&ecirc;n liệu trực tiếp v&agrave; hệ thống start-stop.</p>\r\n\r\n<p>Phi&ecirc;n bản m&agrave; Audi mang đến Vietnam Motor Show 2014 được trang bị hệ thống dẫn động bốn b&aacute;nh Quattro, động cơ xăng tăng &aacute;p TFSI 4 xy-lanh 2.0L c&oacute; c&ocirc;ng suất 230 m&atilde; lực v&agrave; m&ocirc;-men xoắn cực đại 370Nm tại 1.600 - 4.300 v&ograve;ng/ph&uacute;t. Mẫu xe n&agrave;y c&oacute; khả năng tăng tốc 0 - 100km/h trong v&ograve;ng 5,3 gi&acirc;y với tốc độ tối đa l&ecirc;n đến 250km/h. Mức ti&ecirc;u hao nhi&ecirc;n liệu của xe khoảng 6.8L/100km.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '1', '3', '1', '35', 'Đức', '1780000000');
INSERT INTO `products` VALUES ('20', 'Audi A8L', '2012-Audi-A8L.png', 'Audi A8L Sendan Hạng sang siêu an toàn', '<p><a href=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1917_1992.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1917_1992.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://www.danhgiaxe.com/audi-a8\">Audi A8</a> nằm trong ph&acirc;n kh&uacute;c sedan Full-Size cạnh tranh với Mercedes S-Class (gi&aacute; từ 3,48 tỷ đồng ), BMW 7-Series (gi&aacute; từ 4,24 tỷ đồng), Lexus LX460L (gi&aacute; từ 5,64 tỷ đồng)</p>\r\n\r\n<p>A8L sở hữu k&iacute;ch thước tổng thể d&agrave;i x rộng x cao tương ứng 5.265 x 2.111 x 1.471 (mm) chiều d&agrave;i cơ sở của xe đạt 3.122 mm. Điểm nhấn trong thiết kế của A8L 2014 l&agrave; hệ thống đ&egrave;n pha ma trận &quot;Matrix LED&quot; . Với hệ thống đ&egrave;n n&agrave;y, Audi một lần nữa khẳng định vị tr&iacute; bậc thầy c&ocirc;ng nghệ về &aacute;nh s&aacute;ng của m&igrave;nh. Đ&egrave;n LED ma trận tr&ecirc;n A8L 2014 được t&iacute;ch hợp nhiều t&iacute;nh năng v&agrave; c&ocirc;ng nghệ ti&ecirc;n tiến. B&ecirc;n cạnh việc cải thiện khả năng chiếu s&aacute;ng, Matrix LED c&ograve;n c&oacute; khả năng chiếu s&aacute;ng th&ocirc;ng minh đối với c&aacute;c xe đi ngược chiều, hay gi&uacute;p ph&aacute;t hiện v&agrave; cảnh b&aacute;o cho người đi bộ...</p>\r\n\r\n<p>Hệ thống đ&egrave;n n&agrave;y gồm những b&oacute;ng LED ch&ugrave;m s&aacute;ng cao được chia th&agrave;nh nhiều phần nhỏ với thấu k&iacute;nh v&agrave; ch&oacute;a đ&egrave;n ri&ecirc;ng biệt. T&ugrave;y v&agrave;o từng điều kiện &aacute;nh s&aacute;ng b&ecirc;n ngo&agrave;i m&agrave; ch&uacute;ng được k&iacute;ch hoạt hoặc ngưng k&iacute;ch hoạt hoặc l&agrave;m tối cục bộ. Tr&ecirc;n xe A8 mới, mỗi đ&egrave;n pha của xe được gắn 25 b&oacute;ng LED ch&ugrave;m s&aacute;ng cao v&agrave; được ph&acirc;n chia theo từng nh&oacute;m, mỗi nh&oacute;m gồm 5 đ&egrave;n LED với một ch&oacute;a đ&egrave;n.</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1771_891.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1771_891.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p>Phi&ecirc;n bản A8L 3.0 TFSI trang bị động cơ 3.0L turbo tăng &aacute;p, phun xăng trực tiếp c&oacute; c&ocirc;ng suất 310 m&atilde; lực tại v&ograve;ng tua 5.200 - 6.500 v&ograve;ng/ph&uacute;t, m&ocirc;-men xoắn cực đại 440 Nm tại v&ograve;ng tua 2.900 - 4.750 v&ograve;ng/ph&uacute;t. Phi&ecirc;n bản n&agrave;y c&oacute; khả năng tăng tốc từ 0-100 Km/h trong 5,9 gi&acirc;y, tốc độ tối đa đạt 250 Km/h.</p>\r\n\r\n<p>Phi&ecirc;n bản A8L 4.0 TFSI trang bị động cơ 4.0L turbo tăng &aacute;p, phun xăng trực tiếp sản sinh c&ocirc;ng suất 435 m&atilde; lực tại v&ograve;ng tua 5.100 - 6.000 v&ograve;ng/ph&uacute;t, m&ocirc;-men xoắn cực đại 600 Nm tại v&ograve;ng tua 1.500 - 5.000 v&ograve;ng/ph&uacute;t. Khả năng tăng tốc từ 0-100 Km/h trong 4,6s tốc độ tối đa 250 Km/h</p>\r\n\r\n<p>Cả hai phi&ecirc;n bản đều được trang bị hộp số tự động Tiptronic 8 cấp, hệ dẫn động 4 b&aacute;nh quattro.</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1852_4260.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1852_4260.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p>Nội thất A8L được ốp da v&agrave; gỗ b&oacute;ng sang trọng. Xe được trang bị tay l&aacute;i 4 chấu bọc da đa chức năng t&iacute;ch hợp lẫy chuyển số tr&ecirc;n v&ocirc;-lăng v&agrave; c&aacute;c ph&iacute;m bấm điều khiển hệ thống &acirc;m thanh, giải tr&iacute;; hệ thống đ&egrave;n trang tr&iacute; nội thất điều chỉnh 3 m&agrave;u kh&aacute;c nhau. Ghế xe điều chỉnh điện ở c&aacute;c vị tr&iacute;, ghế trước xe điều chỉnh điện, chức năng bơm hơi tựa lưng ghế bốn hướng với chức năng ghi nhớ v&agrave; điều khiển d&acirc;y đai an to&agrave;n; hệ thống điều h&ograve;a kh&ocirc;ng kh&iacute; 4 v&ugrave;ng tự động cao cấp; hệ thống MMI trung t&acirc;m cảm ứng với m&agrave;n h&igrave;nh m&agrave;u 8 inch ẩn dưới bảng tablo, DVD/CD, ổ cứng 20GB hỗ trợ c&aacute;c định dạng nhạc v&agrave; video, hệ thống &acirc;m thanh BOSE, hệ thống kết nối Bluetooth, ổ cắm 230 V; Cửa sổ trời Panorama ở hai h&agrave;ng ghế; hệ thống giải tr&iacute; cho h&agrave;ng ghế sau...</p>\r\n\r\n<p>Xem th&ecirc;m : <a href=\"http://www.sanxehot.vn/mua-ban-xe/audi-a8\">C&aacute;c đời xe Audi A8 đang b&aacute;n tr&ecirc;n thị trường.</a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1827_4326.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1827_4326.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p>Audi A8L 2014 c&oacute; 12 t&ugrave;y chọn m&agrave;u sơn ngoại thất c&ugrave;ng 5 t&ugrave;y chọn ghế bọc da cao cấp. Phi&ecirc;n bản A8L 3.0L c&oacute; gi&aacute; b&aacute;n c&ocirc;ng bố 4,4 tỷ đồng, phi&ecirc;n bản A8L 4.0L c&oacute; gi&aacute; b&aacute;n c&ocirc;ng bố 4,8 tỷ đồng. Xe được bảo h&agrave;nh 3 năm kh&ocirc;ng giới hạn số Km</p>\r\n\r\n<p>H&igrave;nh ảnh chi tiết Audi A8L 2014.</p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1743_2406.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1743_2406.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1751_4525.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1751_4525.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1744_4091.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1744_4091.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1757_2507.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1757_2507.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n\r\n<p><a href=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1778_8844.jpg\"><img alt=\"\" src=\"http://static.danhgiaxe.com/data/201448/audi-a8-2015-1778_8844.jpg\" style=\"margin:0px auto; width:100%px\" /></a></p>\r\n', '1', '1', '1', '34', 'Đức', '4400000000');
INSERT INTO `products` VALUES ('21', 'Audi A5 Sportback', 'Audi A5.png', 'Bỏ ra 2275 tỷ đồng bạn sẽ có một động cơ thể thao, nội thất sang trọng và sự đa dụng mà chỉ có ở Audi A5 Sportback', '<p><img src=\"http://autopro1.vcmedia.vn/kkBBtYAj9E3OhV9Ioct7jf6WGlF36/Image/2012/Audi-A5-Sportback/AudiA5SP-17_d61e7.jpg\" /><br />\r\n&nbsp;</p>\r\n\r\n<p>Sở hữu số đo &quot;ba v&ograve;ng&quot; 4.712 x 2.020 x 1.319mm (d&agrave;i, rộng, cao) những g&igrave; m&agrave; Audi A5 Sportback tạo ấn tượng vẫn l&agrave; thiết kế đơn giản v&agrave; lịch l&atilde;m đan xen một ch&uacute;t thể thao nhờ ngoại h&igrave;nh theo kiểu d&aacute;ng coupe 4 cửa. Với lối thiết kế n&agrave;y, sẽ c&oacute; nhiều người tho&aacute;ng qua lầm tưởng đ&acirc;y l&agrave; một chiếc Audi A7 m&agrave; h&atilde;ng xe Đức ra mắt c&aacute;ch đ&acirc;y &iacute;t l&acirc;u. Cả hai đều giống nhau bởi mui xe k&eacute;o d&agrave;i v&agrave; tr&ugrave;m gần hết khoang h&agrave;nh l&yacute;, một đặc trưng của những mẫu xe coupe. Th&acirc;n xe lộ r&otilde; đường cong dập nổi lượn h&igrave;nh chữ S nối liền từ cụm đ&egrave;n pha trước cho đến cụm đ&egrave;n hậu sau.</p>\r\n\r\n<p><img src=\"http://autopro1.vcmedia.vn/kkBBtYAj9E3OhV9Ioct7jf6WGlF36/Image/2012/Audi-A5-Sportback/AudiA5SP-14_dc2c9.jpg\" /></p>\r\n\r\n<p>V&agrave;nh xe được l&agrave;m bằng kim loại đ&uacute;c 5 chấu c&oacute; k&iacute;ch thước 17 inch ti&ecirc;u chuẩn cũng đủ l&agrave;m chiếc xe tr&ocirc;ng trở n&ecirc;n thể thao v&agrave; đặc biệt. Ngo&agrave;i ra bạn c&ograve;n c&oacute; t&ugrave;y chọn v&agrave;nh với k&iacute;ch cỡ 19 v&agrave; 20&rdquo;. Cụm đ&egrave;n hậu của xe qu&aacute; đơn giản v&agrave; đa phần giống c&aacute;c mẫu sedan trước của Audi nhưng cụm đ&egrave;n pha l&agrave; điểm sẽ hay g&acirc;y hiểu nhầm cho những kh&aacute;ch h&agrave;ng. L&yacute; do đơn giản v&igrave; n&oacute; qu&aacute; giống mẫu sedan Audi A6 nhất l&agrave; ở cụm đ&egrave;n pha, ngo&agrave;i ra A5 Sportback vẫn nổi bật nhờ cụm lưới tản nhiệt h&igrave;nh thang ngược cỡ lớn với logo Audi ch&iacute;nh giữa v&agrave; hai hốc h&uacute;t gi&oacute; ph&iacute;a dưới.</p>\r\n\r\n<p><img src=\"http://autopro1.vcmedia.vn/kkBBtYAj9E3OhV9Ioct7jf6WGlF36/Image/2012/Audi-A5-Sportback/AudiA5SP-22_14553.jpg\" /></p>\r\n\r\n<p>Sở hữu lớp cửa d&agrave;y, chắc nịch, nội thất của A5 tạo ra một kh&ocirc;ng gian rộng với chỗ để ch&acirc;n thoải m&aacute;i cho cả h&agrave;ng ghế trước lẫn h&agrave;ng ghế sau v&agrave; v&ocirc; c&ugrave;ng quen thuộc với những ai đ&atilde; từng trải nghiệm những mẫu Audi kh&aacute;c. Lối thiết kế đơn giản v&agrave; chỉ c&oacute; sự hiện diện của những ph&iacute;m cần thiết, nguy&ecirc;n liệu nhựa cao cấp cho người d&ugrave;ng cảm thấy &quot;thật tay&quot;. Người l&aacute;i lu&ocirc;n c&oacute; cảm gi&aacute;c được l&agrave;m chủ v&agrave; dễ d&aacute;ng tiếp cận, sử dụng những thiết bị m&agrave; Audi trang bị cho A5 Sportback với thời gian ngắn khi Audi đ&atilde; kịp cải tiến gi&uacute;p hệ thống giải tr&iacute; MMI sử dụng dễ d&agrave;ng hơn.</p>\r\n\r\n<p><img src=\"http://autopro1.vcmedia.vn/kkBBtYAj9E3OhV9Ioct7jf6WGlF36/Image/2012/Audi-A5-Sportback/AudiA5SP-20_c2609.jpg\" /></p>\r\n\r\n<p>Vẫn d&ugrave;ng n&uacute;m xoay c&ugrave;ng một số ph&iacute;m bấm xung quanh cần số để điều chỉnh v&agrave; mọi th&ocirc;ng tin đều hiện r&otilde; l&ecirc;n một m&agrave;n h&igrave;nh 7 inch được đặt ch&iacute;nh giữa khu vực trung t&acirc;m, Audi A5 Sportback do Audi Việt Nam ph&acirc;n phối sử dụng đầu đọc CD 6 đĩa/MP3 c&ugrave;ng 10 loa ti&ecirc;u chuẩn. Bạn cũng c&oacute; thể lựa chọn đầu đọc DVD với hệ thống 14 loa h&agrave;ng hiệu Bang&amp;Olufsen, hỗ trợ camera l&ugrave;i l&agrave; những t&ugrave;y chọn ngốn kh&ocirc;ng &iacute;t tiền của mẫu xe n&agrave;y.</p>\r\n\r\n<p><img src=\"http://autopro1.vcmedia.vn/kkBBtYAj9E3OhV9Ioct7jf6WGlF36/Image/2012/Audi-A5-Sportback/AudiA5SP-24_eeea6.jpg\" /></p>\r\n\r\n<p>Ngo&agrave;i ra chiếc xe c&ograve;n được hỗ trợ một ổ cứng dung lượng 40GB k&egrave;m theo bộ xử l&yacute; NVIDIA để tăng cường đồ họa. Một chi tiết đặc biệt m&agrave; Audi mới chỉ trang bị tr&ecirc;n nguy&ecirc;n mẫu A5 Sportback n&agrave;y ch&iacute;nh l&agrave; v&ocirc;-lăng ba chấu bọc da thay thế cho kiểu bốn chấu trước kia, tất nhi&ecirc;n kh&ocirc;ng thể thiếu những ph&iacute;m t&iacute;ch hợp đa chức năng. Bảng đồng hồ trung t&acirc;m với đồng hồ đo tốc độ một đơn vị km/h v&agrave; đồng hồ đo v&ograve;ng tua m&aacute;y, ch&iacute;nh giữa c&oacute; một m&agrave;n h&igrave;nh L.E.D sẽ th&ocirc;ng b&aacute;o r&otilde; r&agrave;ng h&agrave;nh tr&igrave;nh cũng như thể trạng của xe. Bộ ghế da Nappa của Mỹ cũng gi&uacute;p người ngồi trở n&ecirc;n &ecirc;m v&agrave; dễ chịu hơn, đ&acirc;y được coi như một t&ugrave;y chọn ti&ecirc;u chuẩn Audi A5 Sportback.</p>\r\n\r\n<p><img src=\"http://autopro1.vcmedia.vn/kkBBtYAj9E3OhV9Ioct7jf6WGlF36/Image/2012/Audi-A5-Sportback/AudiA5SP-27_3521e.jpg\" /></p>\r\n\r\n<p>Do được thiết kế theo dạng coupe n&ecirc;n khoang h&agrave;nh l&yacute; v&agrave; khoang h&agrave;nh kh&aacute;ch của Audi A5 Sportback sẽ được th&ocirc;ng với nhau, đồng nghĩa với việc khi bạn gập h&agrave;ng ghế thứ hai xuống thể t&iacute;ch khoang chở đồ sẽ nhanh ch&oacute;ng được tăng từ 480L l&ecirc;n 1.283L.</p>\r\n\r\n<p><img src=\"http://autopro1.vcmedia.vn/kkBBtYAj9E3OhV9Ioct7jf6WGlF36/Image/2012/Audi-A5-Sportback/AudiA5SP-15_9d54a.jpg\" /><br />\r\n&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Audi Việt Nam đưa ra thị trường nội địa phi&ecirc;n bản Audi A5 Sportback với động cơ I4 2.0 TFSI kết hợp với hệ thống tăng &aacute;p v&agrave; phun nhi&ecirc;n liệu điện tử cho c&ocirc;ng suất tối đa 211 m&atilde; lực trong dải v&ograve;ng tua 4.300 &ndash; 6.000 v&ograve;ng/ph&uacute;t. Momen xoắn cực đại sẽ xuất hiện ngay ở v&ograve;ng tua 1.500 &ndash; 4.200 v&ograve;ng/ph&uacute;t. Để đạt được điều n&agrave;y c&ograve;n phải kể đến sự g&oacute;p mặt của hộp số tự động 7 cấp v&agrave; hệ thống dẫn động 4 b&aacute;nh to&agrave;n thời gian Quattro. Tất cả những con số tr&ecirc;n đ&atilde; gi&uacute;p chiếc xe tăng tốc từ 0-100 km/h trong điều kiện phi ti&ecirc;u chuẩn tại Việt nam l&agrave; 7s. V&ocirc;-lăng cho cảm gi&aacute;c nhẹ nh&agrave;ng, điều n&agrave;y gi&uacute;p người l&aacute;i giảm được nhiều lực khi phải &ldquo;vần&rdquo; v&ocirc;-lăng trong phố nhưng lại phản xạ lại kh&ocirc;ng thật tay ở tốc độ dưới 40 km/h. Do được mở momen xoắn ở v&ograve;ng tua thấp n&ecirc;n ch&acirc;n ga c&oacute; ch&uacute;t nhạy v&agrave; bạn sẽ phải l&agrave;m quen điều n&agrave;y sau khoảng 15 ph&uacute;t di chuyển li&ecirc;n tục trong nội th&agrave;nh, tuy nhi&ecirc;n xe sẽ nhanh ch&oacute;ng đạt được gia tốc cần thiết m&agrave; bạn kh&ocirc;ng phải ga lớn.</p>\r\n\r\n<p><img src=\"http://autopro1.vcmedia.vn/kkBBtYAj9E3OhV9Ioct7jf6WGlF36/Image/2012/Audi-A5-Sportback/AudiA5SP-4_c505a.jpg\" /></p>\r\n\r\n<p>Đơn giản nhưng vẫn nổi bật l&agrave; cảm nhận khi A5 vận h&agrave;nh tr&ecirc;n đường phố<br />\r\n<br />\r\n<img src=\"http://autopro1.vcmedia.vn/kkBBtYAj9E3OhV9Ioct7jf6WGlF36/Image/2012/Audi-A5-Sportback/AudiA5SP-5_f9d70.jpg\" /></p>\r\n\r\n<p>Phần đu&ocirc;i xe mang lại cảm gi&aacute;c thể thao trong khi cụm đ&egrave;n hậu trở th&agrave;nh &quot;đặc điểm nhận dạng&quot; đặc trưng của Audi</p>\r\n\r\n<p>Ngo&agrave;i xa lộ, hệ thống dẫn động bốn b&aacute;nh to&agrave;n thời gian Quattro tỏ ra thực sự hữu dụng khi chạy tr&ecirc;n 80 km/h vẫn cho cảm gi&aacute;c xe đầm v&agrave; linh hoạt. Một số c&aacute;c trang thiết bị an to&agrave;n nổi bật tr&ecirc;n Audi A5 Sportback bao gồm hệ thống chống b&oacute; cứng ph&aacute;nh ABS, c&acirc;n bằng điện tử ESP, hệ thống kiểm so&aacute;t lực k&eacute;o ASR. Hệ thống điều h&ograve;a tự động ba v&ugrave;ng gi&uacute;p những h&agrave;nh kh&aacute;ch tr&ecirc;n xe đều c&oacute; thể kiểm so&aacute;t được nhiệt độ th&iacute;ch hợp với bản th&acirc;n trong suốt h&agrave;nh tr&igrave;nh d&agrave;i.</p>\r\n\r\n<p><img src=\"http://autopro1.vcmedia.vn/kkBBtYAj9E3OhV9Ioct7jf6WGlF36/Image/2012/Audi-A5-Sportback/AudiA5SP-29_1c10a.jpg\" /><br />\r\n<br />\r\n<img src=\"http://autopro1.vcmedia.vn/kkBBtYAj9E3OhV9Ioct7jf6WGlF36/Image/2012/Audi-A5-Sportback/AudiA5SP-30_8d615.jpg\" /></p>\r\n\r\n<p>Hệ thống ESP l&agrave; thứ l&agrave;m việc tỏ ra hiệu quả v&agrave; ch&iacute;nh x&aacute;c khi chạy z&iacute;c zắc ở tốc độ 60 km/h c&ugrave;ng khoảng c&aacute;ch giữa hai cọc l&agrave; 16m, duy tr&igrave; ga v&agrave; kết quả chiếc xe ph&aacute;t ra những tiếng ma s&aacute;t giữa lốp với mặt đường ch&oacute;i tai khi đ&aacute;nh l&aacute;i gấp nhưng ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; cảm gi&aacute;c bị mất l&aacute;i hay đu&ocirc;i xe bị văng. Nhờ cảm biến v&ocirc;-lăng theo tốc độ n&ecirc;n khi chạy ở vận tốc lớn bạn ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m với cảm gi&aacute;c tay l&aacute;i sẽ đầm, chắc v&agrave; thật hơn rất nhiều đồng thời phản ứng chuẩn x&aacute;c hơn .</p>\r\n\r\n<p><img src=\"http://autopro1.vcmedia.vn/kkBBtYAj9E3OhV9Ioct7jf6WGlF36/Image/2012/Audi-A5-Sportback/AudiA5SP-10_90165.jpg\" /><br />\r\n&nbsp;</p>\r\n\r\n<p>A5 Sportback th&iacute;ch hợp với nh&oacute;m kh&aacute;ch h&agrave;ng năng động, ưa chuộng ngoại h&igrave;nh đặc biệt c&ugrave;ng sự sang trọng b&ecirc;n trong, xen lẫn việc đam m&ecirc; tốc độ v&agrave; sử dụng chiếc xe d&ugrave;ng để đi lại h&agrave;ng ng&agrave;y. Quan trọng hơn họ chấp nhận chi trả số tiền 2.275 tỷ Đồng để sở hữu chiếc xe!</p>\r\n\r\n<p>Một số h&igrave;nh ảnh chi tiết của Audi A5 Sportback:</p>\r\n\r\n<p><img src=\"http://autopro1.vcmedia.vn/kkBBtYAj9E3OhV9Ioct7jf6WGlF36/Image/2012/Audi-A5-Sportback/AudiA5SP-11_8cf0c.jpg\" /></p>\r\n\r\n<p><em>A5 Sportback vẫn nổi bật nhờ cụm lưới tản nhiệt h&igrave;nh thang ngược cỡ lớn với logo Audi ch&iacute;nh giữa v&agrave; hai hốc h&uacute;t gi&oacute; ph&iacute;a dưới.</em></p>\r\n\r\n<p><img src=\"http://autopro1.vcmedia.vn/kkBBtYAj9E3OhV9Ioct7jf6WGlF36/Image/2012/Audi-A5-Sportback/AudiA5SP-12_5e25d.jpg\" /></p>\r\n', '1', '1', '1', '33', 'Đức', '2275000000');
INSERT INTO `products` VALUES ('22', 'BMW 730Li', 'BMW 730Li.png', 'BMW serie 7 2016 mang triết lý thiết kế \"năng động cao\" kết hợp với \"thoải mái sang trọng trên những chặng đường dài\".', '<p><strong>Phi&ecirc;n bản BMW 730Li đ&atilde; ch&iacute;nh thức c&oacute; mặt tại Việt Nam. D&ograve;ng xe ti&ecirc;n phong trong ng&agrave;nh c&ocirc;ng nghiệp &ocirc; t&ocirc;.</strong></p>\r\n\r\n<p>Nối tiếp sau th&agrave;nh c&ocirc;ng vượt mong đợi của phi&ecirc;n bản 740Li được ra mắt từ cuối năm 2015. H&ocirc;m nay, Euro Auto &ndash; nh&agrave; nhập khẩu được ủy quyền ch&iacute;nh thức của tập đo&agrave;n BMW tại Việt Nam, đ&atilde; ch&iacute;nh thức giới thiệu phi&ecirc;n bản BMW 730Li ho&agrave;n to&agrave;n mới đến thị trường Việt Nam.</p>\r\n\r\n<p><img alt=\"BMW 730Li đã chính thức có mặt tại Việt Nam\" src=\"http://giaxe.bmw.vn/media/posts/car_info/1457081238.jpg\" style=\"float:left; height:auto; margin:0px 60px 5px 0px; width:420px\" /></p>\r\n\r\n<p><strong>BMW 730Li đ&atilde; ch&iacute;nh thức c&oacute; mặt tại Việt Nam</strong></p>\r\n\r\n<p>Phi&ecirc;n bản th&acirc;n d&agrave;i BMW 730Li ho&agrave;n to&agrave;n mới (Long Wheelbase - phi&ecirc;n bản c&oacute; chiều d&agrave;i nhỉnh hơn bản ti&ecirc;u chuẩn) được Euro Auto giới thiệu tại Việt Nam, lần đầu ti&ecirc;n được trang bị hệ thống dẫn đường chuy&ecirc;n dụng BMW Navigation Professional System.</p>\r\n\r\n<p><img alt=\"Những công nghệ đột phá trên BMW Series 7 hoàn toàn mới\" src=\"http://giaxe.bmw.vn/media/posts/car_info/1457081429.jpg\" style=\"float:right; height:auto; margin:0px 0px 5px 60px; width:420px\" /></p>\r\n\r\n<p><strong>Những c&ocirc;ng nghệ đột ph&aacute; tr&ecirc;n BMW Series 7 ho&agrave;n to&agrave;n mới</strong></p>\r\n\r\n<p>1. Động cơ mới: Ứng dụng c&ocirc;ng nghệ EfficientDynamics.</p>\r\n\r\n<p>2. Nguy&ecirc;n l&yacute; &ldquo;Carbon Core&rdquo;: S&aacute;ng tạo tr&ecirc;n từng sợi Carbon.</p>\r\n\r\n<p>3. Ch&igrave;a kh&oacute;a m&agrave;n h&igrave;nh hiển thị th&ocirc;ng minh BMW Display Key: Dễ d&agrave;ng kiểm tra th&ocirc;ng tin hữu &iacute;ch ngay tr&ecirc;n tay.</p>\r\n\r\n<p>4. Hệ thống điều khiển bằng cử chỉ (BMW Gesture Control): kết hợp với m&agrave;n h&igrave;nh cảm ứng 10,25&rdquo; với c&ocirc;ng nghệ cảm ứng điện dung Touch Display. M&agrave;n h&igrave;nh hiển thị LCD 10,25&rdquo; với c&ocirc;ng nghệ cảm ứng Touch Display.</p>\r\n\r\n<p>5. Hệ thống điều khiển iDrive thế hệ mới, trang bị c&ocirc;ng nghệ cảm ứng.</p>\r\n\r\n<p>6. Chức năng hiển thị th&ocirc;ng tin tr&ecirc;n k&iacute;nh chắn gi&oacute; (HUD) ti&ecirc;u chuẩn tr&ecirc;n xe.</p>\r\n\r\n<p>7. Hệ thống đ&egrave;n nội thất (Ambient Lightning) trang bị ti&ecirc;u chuẩn.</p>\r\n\r\n<p>8. Hệ thống đ&egrave;n trải thảm dẫn đường độc đ&aacute;o (Welcome Light Carpet) trang bị ti&ecirc;u chuẩn.</p>\r\n\r\n<p>9. G&oacute;i m&ugrave;i hương Ambient Air Package cho nội thất xe (t&ugrave;y chọn.</p>\r\n\r\n<p>10. Chức năng ghế massage cho ghế t&agrave;i xế v&agrave; h&agrave;nh kh&aacute;ch (t&ugrave;y chọn).</p>\r\n\r\n<p>11. Hệ thống đ&egrave;n pha tự th&iacute;ch ứng (Adaptive LED Headlight) trang bị ti&ecirc;u chuẩn.</p>\r\n\r\n<p>12. Hệ thống treo kh&iacute; n&eacute;n cho cầu trước v&agrave; cầu sau (Air suspension) trang bị ti&ecirc;u chuẩn.</p>\r\n\r\n<p>13. Hệ thống hỗ trợ đ&aacute;nh l&aacute;i chủ động (Integral active steering) trang bị ti&ecirc;u chuẩn.</p>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p><strong>TH&Ocirc;NG SỐ KỸ THUẬT</strong></p>\r\n\r\n<p><img alt=\"technical\" src=\"http://giaxe.bmw.vn/media/posts/car_info/1457081825.jpg\" style=\"height:auto; margin:0px; width:480px\" /></p>\r\n\r\n<p><strong>KHI CHUYỂN ĐỘNG H&Ograve;A C&Ugrave;NG CẢM X&Uacute;C</strong></p>\r\n\r\n<p>G&oacute;i trang bị ngoại thất mạ chrome Pure Excellence ti&ecirc;u chuẩn cho 730Li.</p>\r\n\r\n<p>Sẽ kh&ocirc;ng tốn nhiều thời gian để nhận ra: BMW Series 7 ho&agrave;n to&agrave;n mới l&agrave; mẫu sedan thượng hạng kết hợp bởi phong th&aacute;i, sự năng động v&agrave; kỹ thuật chế t&aacute;c đỉnh cao. Thoạt nh&igrave;n từ ngoại thất, những chi tiết được mạ Chrome b&oacute;ng trang tr&iacute; tinh tế cho Khe tho&aacute;t gi&oacute; (Air Breather), lưới tản nhiệt th&ocirc;ng minh đ&oacute;ng/mở tự động (Active Air Stream) l&agrave; một trong những trang bị độc đ&aacute;o v&agrave; thu h&uacute;t về mặt thiết kế. Hệ thống đ&egrave;n chiếu s&aacute;ng LED (Adaptive LED) ti&ecirc;u chuẩn, với chức năng tự động điều chỉnh g&oacute;c chiếu đ&egrave;n pha, tự động điều khiển dải s&aacute;ng (BMW Selective Beam), cũng như hệ thống mở rộng g&oacute;c chiếu s&aacute;ng khi v&agrave;o cua, đảm bảo cho sự vận h&agrave;nh an to&agrave;n tối ưu tr&ecirc;n đường.</p>\r\n\r\n<p>Những đường g&acirc;n nổi r&otilde; b&ecirc;n h&ocirc;ng chiếc BMW Series 7 ho&agrave;n to&agrave;n mới mang đến cảm nhận của sự mạnh mẽ v&agrave; bắt mắt mang đến cho người thưởng l&atilde;m bất cứ khi n&agrave;o chiếc xe xuất hiện. Tay nắm cửa được thiết kế liền lạc với đường g&acirc;n b&ecirc;n h&ocirc;ng xe. Hốc tho&aacute;t gi&oacute; (Air Breather) lần đầu ti&ecirc;n xuất hiện tr&ecirc;n d&ograve;ng Series 7, được bao quanh bởi đường trang tr&iacute; mạ chrome s&aacute;ng b&oacute;ng, k&eacute;o d&agrave;i li&ecirc;n tục theo m&eacute;p dưới của cửa trước v&agrave; cửa sau, mang đến cảm quan độc đ&aacute;o v&agrave; nhấn mạnh độ sang trọng, đường trang tr&iacute; mạ chrome thậm ch&iacute; gi&uacute;p th&acirc;n xe tr&ocirc;ng d&agrave;i hơn, đồng thời nhấn mạnh đến trọng t&acirc;m thấp v&agrave; b&aacute;m đường của chiếc xe, to&aacute;t l&ecirc;n c&aacute; t&iacute;nh đương đại v&agrave; thanh lịch, thiết kế n&agrave;y cũng đồng thời gi&uacute;p tăng cường t&iacute;nh kh&iacute; động học của BMW Series 7 ho&agrave;n to&agrave;n mới.</p>\r\n\r\n<p>BMW Series 7 ho&agrave;n to&agrave;n mới c&ograve;n trang bị cho kh&aacute;ch h&agrave;ng g&oacute;i trang bị ngoại thất mạ Chrome (Pure Excellence) ti&ecirc;u chuẩn. Với g&oacute;i trang bị n&agrave;y, chất liệu chrome được trang tr&iacute; trong mọi kh&iacute;a cạnh v&agrave; từ mọi g&oacute;c độ của chiếc xe. Điển h&igrave;nh, bạn c&oacute; thể t&igrave;m thấy điểm nhấn từ chất liệu chrome s&aacute;ng b&oacute;ng tr&ecirc;n thanh lưới tản nhiệt, tr&ecirc;n cản trước v&agrave; sau, tr&ecirc;n hốc lấy gi&oacute; ở cản trước v&agrave; ốp m&aacute; phanh với m&agrave;u sơn đen bắt mắt.</p>\r\n\r\n<p><strong>NỘI THẤT</strong></p>\r\n\r\n<p><a href=\"http://giaxe.bmw.vn/media/posts/car_info/1457081978994207.png\"><img alt=\"TRẢI NGHIỆM HOÀN HẢO TẠI MỌI VỊ TRÍ NGỒI TRÊN XE\" src=\"http://giaxe.bmw.vn/media/posts/car_info/1457081978994207.png\" style=\"height:auto; margin:0px; width:960px\" /></a></p>\r\n\r\n<p><strong>TRẢI NGHIỆM HO&Agrave;N HẢO TẠI MỌI VỊ TR&Iacute; NGỒI TR&Ecirc;N XE</strong></p>\r\n\r\n<p>Nhẹ bước v&agrave;o nội thất, bạn sẽ thật sự tận hưởng một tuyệt t&aacute;c cao cấp, sang trọng được thiết kế từ những vật liệu chất lượng c&ugrave;ng m&agrave;u sắc trang nh&atilde;, h&agrave;i ho&agrave;, gi&uacute;p tạo n&ecirc;n một kh&ocirc;ng gian hiện đại ấm &aacute;p, sẵn s&agrave;ng ch&agrave;o đ&oacute;n bất cứ sự hiện diện n&agrave;o. Nội thất thế hiện sự &ldquo;h&agrave;o ph&oacute;ng&rdquo; của kh&ocirc;ng gian, v&agrave; t&iacute;nh đẳng cấp được nhấn mạnh bởi c&aacute;c đường trang tr&iacute; được đặt nằm ngang gi&uacute;p xe tr&ocirc;ng rộng hơn. Sự tinh tế sang trọng tr&agrave;n ngập b&ecirc;n trong BMW Series 7 ho&agrave;n to&agrave;n mới c&oacute; thể được tăng cường bởi h&agrave;ng loạt c&aacute;c t&iacute;nh năng đẳng cấp nhất, gi&uacute;p việc di chuyển giờ đ&acirc;y kh&ocirc;ng chỉ thoải m&aacute;i hơn, m&agrave; c&ograve;n n&acirc;ng việc tận hưởng l&ecirc;n một tầm cao mới.</p>\r\n\r\n<p>Với BMW Series 7 ho&agrave;n to&agrave;n mới, sự thoải m&aacute;i v&agrave; thư gi&atilde;n sẽ trở th&agrave;nh người bạn đồng h&agrave;nh tr&ecirc;n mọi nẻo đường. Với khoang h&agrave;nh kh&aacute;ch thượng hạng, BMW Series 7 khiến bạn ch&igrave;m đắm trong những chiếc ghế da cao cấp được l&agrave;m từ những vật liệu da Dakota h&agrave;ng đầu, c&ugrave;ng với khoảng để ch&acirc;n rộng r&atilde;i, bạn sẽ ho&agrave;n to&agrave;n cảm nhận được bầu kh&ocirc;ng kh&iacute; sang trọng, đẳng cấp d&agrave;nh cho ri&ecirc;ng m&igrave;nh. Chiếc m&aacute;y t&iacute;nh bảng BMW Touch Command (t&ugrave;y chọn) sẽ trở th&agrave;nh người trợ l&yacute; đặc biệt cho chủ nh&acirc;n trong việc điều khiển c&aacute;c chức năng được t&iacute;ch hợp tr&ecirc;n xe ngay từ h&agrave;ng ghế sau.</p>\r\n', '1', '1', '2', '2', 'Đức', '4100000000');
INSERT INTO `products` VALUES ('23', 'Toyota Innova 2017', 'toyota-innova-2017-1.jpg', 'Toyota Innova 2017 ra đời là tiếng nói của sự thay đổi không ngừng, là ánh sáng của thiết kế thời trang tinh tế và là sức mạnh của sự bền bỉ trong kỷ nguyên của những sáng tạo vượt bậc.', '<p><img alt=\"oto toyota innova new 2016 2017 giá xe mới nhất\" src=\"http://giatoyota.vn/wp-content/uploads/2016/07/oto-toyota-innova-new-2016-5.jpg\" style=\"height:600px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p><strong>Ngoại thất của Toyota Innova 2017</strong></p>\r\n\r\n<p>Toyota Innova 2017 được cho rằng sẽ thấp hơn phi&ecirc;n bản tiền nhiệm để xe c&oacute; d&aacute;ng trường v&agrave; cảm nhận được sức rướn nhiều hơn. Đồng thời, xe cũng c&oacute; những n&eacute;t rất thể thao nhờ mang tr&ecirc;n m&igrave;nh lưới tản nhiệt đến từ Corolla tinh tế v&agrave; rất tương đồng với ng&ocirc;n ngữ thiết kế của Innova thế hệ mới</p>\r\n\r\n<p><img alt=\"oto toyota innova new 2017 giá xe ô tô\" src=\"http://giatoyota.vn/wp-content/uploads/2016/07/oto-toyota-innova-new-2016-2.jpg\" style=\"height:600px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p>Đ&egrave;n pha của phi&ecirc;n bản mới sẽ c&oacute; th&ecirc;m sự bổ sung tinh tế của DRLS rất độc đ&aacute;o v&agrave; cuốn h&uacute;t</p>\r\n\r\n<p><img alt=\"oto toyota innova new 2016 8\" src=\"http://giatoyota.vn/wp-content/uploads/2016/07/oto-toyota-innova-new-2016-8.jpg\" style=\"height:1067px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p>Cụm đ&egrave;n sương m&ugrave; cũng c&acirc;n xứng v&agrave; mang h&igrave;nh đa gi&aacute;c trẻ trung, mang lại n&eacute;t đẹp hiện đại cho phần đầu xe</p>\r\n\r\n<p>Ph&iacute;a đu&ocirc;i xe Toyota Innova 2017 hầu như được giữ nguy&ecirc;n n&eacute;t đẹp của phi&ecirc;n bản cũ nhưng c&oacute; th&ecirc;m cụm đ&egrave;n phanh tr&ecirc;n cao được trang bị c&ocirc;ng nghệ Led v&ugrave;a đẹp mắt vừa tiết kiệm năng lượng lại l&agrave;m tr&ograve;n tr&aacute;ch nhiệm b&aacute;o hiệu cho c&aacute;c xe ph&iacute;a sau rằng xe tr&ecirc;n đang phanh gấp để c&oacute; thể nhanh ch&oacute;ng chuyển hướng l&aacute;i</p>\r\n\r\n<p>Một điểm cộng nữa cho Innova 2017 l&agrave; c&aacute;c chrome t&ocirc;n tạo lưới tản nhiệt dự kiến được k&eacute;o d&agrave;i theo dọc theo đường biển đăng k&iacute; khiến đu&ocirc;i xe trở n&ecirc;n b&iacute; ẩn v&agrave; thu h&uacute;t hơn</p>\r\n\r\n<h3><strong>Nội thất của Toyota Innova 2017</strong></h3>\r\n\r\n<p>Sở hữu một n&ocirc;i thất b&oacute;ng bẩy, h&agrave;o nho&aacute;ng đầy trẻ trung, Toyota Innova 2017 r&otilde; r&agrave;ng được tr&ocirc;ng như một thiếu gia thời hiện đại.</p>\r\n\r\n<p><img alt=\"oto toyota innova new 2017 tại Việt Nam\" src=\"http://giatoyota.vn/wp-content/uploads/2016/07/oto-toyota-innova-new-2016-6.jpg\" style=\"height:600px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p>Đằng sau c&aacute;nh cửa l&agrave; một kh&ocirc;ng gian tho&aacute;ng rộng với nhiều khoảng s&aacute;ng tinh tế, Toyota Innova 2017 kh&ocirc;ng cần tới cửa sổ trời cũng c&oacute; thể thu h&uacute;t một lượng vừa đủ &aacute;nh s&aacute;ng tự nhi&ecirc;n v&agrave;o trong xe khiến kh&ocirc;ng gian trở n&ecirc;n h&agrave;i h&ograve;a v&agrave; th&acirc;n thiện với thi&ecirc;n nhi&ecirc;n b&ecirc;n ngo&agrave;i</p>\r\n\r\n<p>Bản E kh&ocirc;ng k&eacute;m phần thu h&uacute;t với chất liệu nỉ bọc ghế v&agrave; tappi cửa</p>\r\n\r\n<p>Bảng điều khiển mới tinh nhờ đ&egrave;n b&aacute;o vị tr&iacute; cần số, đ&egrave;n n&agrave;y được trang bị c&ocirc;ng nghệ led ho&agrave;n hảo, phối m&agrave;u trẻ trung v&agrave; trang nh&atilde;</p>\r\n\r\n<p><img alt=\"xe oto innova V 2017\" src=\"http://giatoyota.vn/wp-content/uploads/2016/07/xe-oto-innova-V-3-1.jpg\" style=\"height:388px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p>Bản V được ch&uacute; &yacute; bởi được tạo n&ecirc;n từ chất liệu da cao cấp, ốp gỗ v&agrave; mạ bạc t&ocirc;n th&ecirc;m vẻ thanh lịch v&agrave; sang trọng cho xe</p>\r\n\r\n<p>Ngo&agrave;i ra, bản V c&ograve;n đặc biệt với hệ thống &acirc;m thanh DVD 6 loa chất lượng, m&agrave;n h&igrave;nh cảm ứng 9 inch khiến người l&aacute;i v&agrave; c&aacute;c h&agrave;nh kh&aacute;ch thỏa sức xem phim, nghe nhạc dưới dạng đu&ocirc;i mp3 hoặc wma. C&oacute; cổng USB v&agrave; c&aacute;c kết nối thiết bị hiện đại đ&aacute;p ứng ng&agrave;y c&agrave;ng cao nhu cầu giải tr&iacute; của h&agrave;nh kh&aacute;ch</p>\r\n\r\n<p>Cả 4 phi&ecirc;n bản đề được trang bị điều h&ograve;a kh&ocirc;ng kh&iacute; 2 d&agrave;n lạnh, l&agrave;m m&aacute;t cực nhanh v&agrave; tản nhiệt rộng khắp kh&ocirc;ng gian xe</p>\r\n\r\n<h3><strong>Động cơ</strong></h3>\r\n\r\n<p>Động cơ của Innova 2017 được cho biết rằng vẫn kế thừa n&eacute;t dẻo dai, bền bỉ v&agrave; tiết kiệm nhi&ecirc;n liệu từ c&aacute;c phi&ecirc;n bản trước. Với việc sử dụng động cơ 4 xi lanh mang m&atilde; 1TR-FE với dung t&iacute;ch 2.0L v&agrave; hệ thống biến thi&ecirc;n th&ocirc;ng minh VVT-i, Toyota Innova 2017 đang l&agrave; ng&ocirc;i sao động cơ tỏa s&aacute;ng nhất tr&ecirc;n bầu trời ph&acirc;n kh&uacute;c xe gia đ&igrave;nh thời điểm hiện tại với c&ocirc;ng suất cực đại đạt 137 m&atilde; lực tại v&ograve;ng tua 4.000 v&ograve;ng/ ph&uacute;t.</p>\r\n\r\n<p><img alt=\"oto toyota innova new 2016 \" src=\"http://giatoyota.vn/wp-content/uploads/2016/07/oto-toyota-innova-new-2016-7-1.jpg\" style=\"height:600px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p>Phi&ecirc;n bản V v&agrave; E sử dụng hộp số tự động 6 cấp thay cho loại 4 cấp như phi&ecirc;n bản tiền nhiệm c&ograve;n hộp số s&agrave;n 5 cấp được sử dụng cho phi&ecirc;n bản E</p>\r\n\r\n<p><strong>An to&agrave;n</strong></p>\r\n\r\n<p>L&agrave; một h&atilde;ng xe h&agrave;ng đầu thế giới, Toyota lu&ocirc;n đề cao vấn đề an to&agrave;n v&igrave; vậy việc trang bị c&aacute;c t&iacute;nh năng an to&agrave;n ng&agrave;y c&agrave;ng được đ&agrave;o s&acirc;u nghi&ecirc;n cứu v&agrave; n&acirc;ng cao t&iacute;nh ứng dụng trong c&aacute;c d&ograve;ng xe của h&atilde;ng n&agrave;y.</p>\r\n\r\n<p>Bản G v&agrave; E của Toyata Innova 2017 được trang bị an to&agrave;n đầy đủ với 3 t&uacute;i kh&iacute; v&agrave; được tiết kế th&ecirc;m hệ thống chống b&oacute; cứng phanh ABS mới, ph&acirc;n phối lực phanh điện tử hiệu quả EBD , c&acirc;n bằng điện tử đạt chuẩn VSC v&agrave; hỗ trợ phanh khẩn cấp BA cực nhanh ch&oacute;ng.</p>\r\n\r\n<p><img alt=\"xe oto innova V \" src=\"http://giatoyota.vn/wp-content/uploads/2016/07/xe-oto-innova-V-0-1.jpg\" style=\"height:385px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p>Bản V cao cấp nhất được trang bị 7 t&uacute;i kh&iacute;, ngo&agrave;i c&aacute;c t&iacute;nh năng an to&agrave;n như bản G v&agrave; E, bản V c&ograve;n được thiết kế th&ecirc;m c&acirc;n bằng th&acirc;n th&acirc;n xe mới VSC v&agrave; hỗ trợ khởi h&agrave;nh ngang dốc an to&agrave;n tuyệt đối HAC. Th&ecirc;m cả đ&egrave;n b&aacute;o phanh gấp EBS hiện đại, Innova 2017 khiến nhiều đối thủ phải ghen tị về t&iacute;nh năng an to&agrave;n đạt ti&ecirc;u chuẩn quốc tế.</p>\r\n', '1', '3', '3', '5', 'Nhật Bản', '793000000');
INSERT INTO `products` VALUES ('24', 'BMW X4', 'BWM X4.png', 'X4 cũng được  trang bị các chế độ vận hành như Comfort (Êm ái), Sport (Thể thao) hay Eco Pro (Tiết kiệm nhiên liệu)', '<p>&nbsp;</p>\r\n\r\n<p><img alt=\"BMW X4 (5).jpg\" src=\"http://cdn3.autodaily.vn/du-lieu/2014/09/06/BMW%20X4%20(5)-1.jpg\" style=\"height:361px; margin:0px; width:645px\" /></p>\r\n\r\n<p><em>Cận cảnh BMW X4 đ&atilde; ch&iacute;nh thức c&oacute; mặt tại Việt Nam</em></p>\r\n\r\n<p>L&agrave; sự kết hợp giữa hệ thống khung gầm của X3 v&agrave; kiểu d&aacute;ng thể thao của X6, chiếc X4 c&oacute; k&iacute;ch thước &quot;nhỉnh&quot; hơn X3 về chiều d&agrave;i (14mm) nhưng lại thấp hơn X3 đến (36mm). <strong>BMW X4 2014</strong> c&oacute; chiều d&agrave;i 4.671 mm, rộng 1.882 mm v&agrave; cao 1.622mm v&agrave; của BMW X6 l&agrave; 4.879 mm d&agrave;i, 1.984 mm rộng v&agrave; 1.689 mm cao. Xe sở hữu mặt trước hầm hố với lưới tản nhiệt đặc trưng, cụm đ&egrave;n pha v&agrave; đ&egrave;n sương m&ugrave; độc đ&aacute;o. Phần đu&ocirc;i xe g&acirc;y ấn tượng với cụm đ&egrave;n hậu LED h&igrave;nh chữ L v&agrave; bộ khuếch t&aacute;n gi&oacute;.<a href=\"http://autodaily.vn/2014/09/can-canh-bmw-x4-tai-viet-nam/#\"> </a></p>\r\n\r\n<p><a href=\"http://autodaily.vn/2014/09/can-canh-bmw-x4-tai-viet-nam/#\"><img alt=\"BMW X4 (4).jpg\" src=\"http://cdn1.autodaily.vn/du-lieu/2014/09/06/BMW%20X4%20(4).jpg\" style=\"height:120px; margin:0px; width:212px\" /> </a><a href=\"http://autodaily.vn/2014/09/can-canh-bmw-x4-tai-viet-nam/#\"><img alt=\"BMW X4 (6)-1.jpg\" src=\"http://cdn1.autodaily.vn/du-lieu/2014/09/06/BMW%20X4%20(6)-1.jpg\" style=\"height:120px; margin:0px; width:212px\" /> </a><a href=\"http://autodaily.vn/2014/09/can-canh-bmw-x4-tai-viet-nam/#\"><img alt=\"BMW X4 (7)-1.jpg\" src=\"http://cdn1.autodaily.vn/du-lieu/2014/09/06/BMW%20X4%20(7)-1.jpg\" style=\"height:120px; margin:0px; width:212px\" /></a></p>\r\n\r\n<p><em>Tổng quan ngoại thất của BMW X4</em></p>\r\n\r\n<p>C&aacute;c trang bị ngoại thất của BMW X4 c&oacute; thể đến như như bộ m&acirc;m đ&uacute;c cỡ 18inch (kiểu 605), gương chiếu hậu gập/chỉnh điện t&iacute;ch hợp đ&egrave;n b&aacute;o rẽ v&agrave; hệ thống sấy k&iacute;nh. Cụm đ&egrave;n pha Xenon với t&iacute;nh năng tự động điều chỉnh cường độ chiếu s&aacute;ng đi c&ugrave;ng v&ograve;ng LED Corona v&agrave; d&atilde;y đ&egrave;n &quot;m&iacute; mắt&quot;, đ&egrave;n sương m&ugrave; nằm chung với hốc h&uacute;t gi&oacute; ph&iacute;a trước. Tr&ecirc;n bệ bước của to&agrave;n bộ cửa tr&ecirc;n xe được khắc chữ BMW.</p>\r\n\r\n<h2>Nội thất v&agrave; trang bị tiện &iacute;ch</h2>\r\n\r\n<p><img alt=\"BMW X4 (11).jpg\" src=\"http://cdn2.autodaily.vn/du-lieu/2014/09/06/BMW%20X4%20(11).jpg\" style=\"height:361px; margin:0px; width:645px\" /></p>\r\n\r\n<p><em>Kh&ocirc;ng gian nội thất ở h&agrave;ng trước của BMW X4</em></p>\r\n\r\n<p>Nội thất của X4 vẫn được sử dụng chất liệu da Sensatec, ghế ngồi c&oacute; thể điều chỉnh 8 hướng, c&oacute; khả năng lưu 2 vị tr&iacute;. Phần t&aacute;p-l&ocirc;, bảng điều khiển trung t&acirc;m v&agrave; nẹp cửa được ốp gỗ n&acirc;u b&oacute;ng. M&agrave;n h&igrave;nh trung t&acirc;m 6.5 inch được thiết kế nổi bật hơn với 2 đường viền cr&ocirc;m c&aacute;ch điệu, bố tr&iacute; hướng về người l&aacute;i một g&oacute;c 7 độ, gi&uacute;p quan s&aacute;t dễ d&agrave;ng v&agrave; thuận tiện. Nổi bật l&agrave; cần số tự động 8 cấp với thiết kế đẹp mắt với c&aacute;c n&uacute;t hỗ trợ vận h&agrave;nh v&agrave; chuyển chế độ vận h&agrave;nh được viền cr&ocirc;m.</p>\r\n\r\n<p><a href=\"http://autodaily.vn/2014/09/can-canh-bmw-x4-tai-viet-nam/#\"><img alt=\"BMW X4 (10).jpg\" src=\"http://cdn2.autodaily.vn/du-lieu/2014/09/06/BMW%20X4%20(10).jpg\" style=\"height:120px; margin:0px; width:212px\" /> </a><a href=\"http://autodaily.vn/2014/09/can-canh-bmw-x4-tai-viet-nam/#\"><img alt=\"BMW X4 (21).jpg\" src=\"http://cdn2.autodaily.vn/du-lieu/2014/09/06/BMW%20X4%20(21).jpg\" style=\"height:120px; margin:0px; width:212px\" /> </a><a href=\"http://autodaily.vn/2014/09/can-canh-bmw-x4-tai-viet-nam/#\"><img alt=\"BMW X4 (23).jpg\" src=\"http://cdn2.autodaily.vn/du-lieu/2014/09/06/BMW%20X4%20(23).jpg\" style=\"height:120px; margin:0px; width:212px\" /></a></p>\r\n\r\n<p><em>C&aacute;c chi tiết nội thất của BMW X4</em></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, <strong>BMW X4<a href=\"http://autodaily.vn/2014/08/can-canh-bmw-x3-may-dau-moi-ve-viet-nam/\"> </a></strong>cũng được trang bị c&aacute;c kết nối cơ bản như cổng AUX/IN, cổng USB, chức năng kết nối bluetooth rảnh tay với điện thoại thật tiện dụng. H&agrave;ng ghế sau của BMW<strong><a href=\"http://autodaily.vn/2014/08/can-canh-bmw-x3-may-dau-moi-ve-viet-nam/\"> </a>X4 xDrive 28i </strong>c&oacute; thiết kế h&agrave;i h&ograve;a, tinh gọn, đ&aacute;p ứng tốt nhu cầu di chuyển cho 05 người, đảm bảo kh&ocirc;ng gian rộng r&atilde;i, c&oacute; khả năng gấp theo 03 nấc 40:20:40. Nắp cốp sau của xe được trang bị chức năng đ&oacute;ng/mở tự động chỉ với một n&uacute;t bấm.</p>\r\n\r\n<h2>Động cơ, hệ thống truyền lực</h2>\r\n\r\n<p><img alt=\"BMW X4 (32).jpg\" src=\"http://cdn1.autodaily.vn/du-lieu/2014/09/06/BMW%20X4%20(32).jpg\" style=\"height:361px; margin:0px; width:645px\" /></p>\r\n\r\n<p><em>Khối động cơ 2.0 l&iacute;t cho c&ocirc;ng suất 245 m&atilde; lực của X4 xDrive 28i</em></p>\r\n\r\n<p><strong>BMW X4 2014 xDrive 28i</strong> d&ugrave;ng động cơ 4 xi-lanh dung t&iacute;ch 2.0 l&iacute;t cho c&ocirc;ng suất 245 m&atilde; lực tại 5000 v&ograve;ng/ph&uacute;t v&agrave; m&ocirc;-men xoắn cực đại 350Nm, hộp số tự động 8 cấp v&agrave; dẫn động 4 b&aacute;nh xDrive. Xe c&oacute; khả năng tăng tốc 0-100km/h mất 6 gi&acirc;y, tốc độ tối đa 208km/h. Mức ti&ecirc;u thụ nhi&ecirc;n liệu trung b&igrave;nh l&agrave; 8,3 l&iacute;t/100km</p>\r\n', '1', '4', '2', '40', 'Đức', '2768000000');
INSERT INTO `products` VALUES ('25', 'Honda CR-V 2017', 'honda_crv.png', 'Honda CR-V đời 2017 có ngoại hình mang thiên hướng táo bạo và thể thao hơn, loại bỏ hoàn toàn những nét thiết kế bầu bầu ở thế hệ trước', '<h2>&nbsp;</h2>\r\n\r\n<p><img alt=\"Honda CR-V 2017 chốt giá từ 25,000$ tại Mỹ\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/11/honda-cr-v-2017-3.jpg\" style=\"height:633px; width:1198px\" /></p>\r\n\r\n<p><strong>Honda CR-V 2017</strong>&nbsp;tại Mỹ sẽ c&oacute; sẵn 3 phi&ecirc;n bản l&agrave; LX, EX v&agrave; Touring. Những phi&ecirc;n bản ti&ecirc;u chuẩn sẽ sở hữu khối động cơ 2.4L DOHC, t&iacute;ch hợp c&ocirc;ng nghệ điều khiển van biến thi&ecirc;n i-VTEC v&agrave; một t&ugrave;y chọn kh&aacute;c l&agrave; loại tăng &aacute;p 1.5L DOHC lấy từ Honda Civic, nhưng được điều chỉnh l&ecirc;n c&ocirc;ng suất 190 m&atilde; lực (tăng 16 m&atilde; lực) v&agrave; m&ocirc;-men xoắn 242Nm. Cả hai động cơ đi k&egrave;m hộp số v&ocirc; cấp CVT. Gi&aacute; b&aacute;n ch&iacute;nh thức cho phi&ecirc;n bản LX l&agrave; 24.925 USD, tăng 200 USD so với thế hệ tiền nhiệm. Trong khi phi&ecirc;n bản thường được người ti&ecirc;u d&ugrave;ng lựa chọn nhất l&agrave; EX sẽ c&oacute; gi&aacute; tăng 580 USD v&agrave; ở mức 27.575 USD. Cuối c&ugrave;ng l&agrave; phi&ecirc;n bản Touring sẽ c&oacute; gi&aacute; 33.275 USD tăng 180 USD. CR-V 2017 phi&ecirc;n bản ti&ecirc;u chuẩn được trang bị hệ dẫn động cầu trước, nếu kh&aacute;ch h&agrave;ng lựa chọn hệ dẫn động 4 b&aacute;nh sẽ phải bỏ th&ecirc;m 1.300 USD.</p>\r\n\r\n<h2><strong>Thiết kế nội ngoại thất v&agrave; gi&aacute; b&aacute;n</strong></h2>\r\n\r\n<p>Về mặt thiết kế, Honda CR-V thế hệ thứ 5 sở hữu ngoại h&igrave;nh mang thi&ecirc;n hướng t&aacute;o bạo v&agrave; thể thao hơn, loại bỏ ho&agrave;n to&agrave;n những n&eacute;t thiết kế bầu bầu ở thế hệ trước. Đ&egrave;n pha LED xếp lớp, mang đặc trưng của Honda đ&atilde; từng &aacute;p dụng tr&ecirc;n Accord v&agrave; Civic, tạo th&agrave;nh một điểm nhận diện mới. Hốc h&uacute;t gi&oacute; ph&iacute;a dưới tự động đ&oacute;ng/mở để cải thiện t&iacute;nh kh&iacute; động học. Đ&egrave;n hậu LED thiết kế chạy v&agrave;o phần đu&ocirc;i nhiều hơn.</p>\r\n\r\n<p><img alt=\"Đánh giá xe Honda CR-V 2017 về thiết kế nội ngoại thất và giá bán\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/11/danh-gia-xe-honda-cr-v-2017.jpg\" style=\"height:615px; width:1168px\" /></p>\r\n\r\n<p>Mui xe d&agrave;i hơn, tăng chiều d&agrave;i cơ sơ, phần đu&ocirc;i nh&ocirc; ra ngắn v&agrave; trang bị ống xả k&eacute;p. M&acirc;m xe hợp kim nh&ocirc;m t&ugrave;y chọn 17 inch hoặc 18 inch, Đ&egrave;n pha tự động điều chỉnh độ cao, cảm biến gạt mưa,&hellip; Kh&ocirc;ng gian nội thất rộng r&atilde;i hơn, với khoảng trống duỗi ch&acirc;n ph&iacute;a sau tốt nhất ph&acirc;n kh&uacute;c. M&agrave;n h&igrave;nh giải tr&iacute; trung t&acirc;m c&oacute; viền đen b&oacute;ng mang lại c&aacute;i nh&igrave;n hiện đại, c&aacute;c n&uacute;t điều chỉnh đều ở dạng cảm ứng. Cụm đồng hồ mang đậm &acirc;m hưởng từ Civic, hiển thị ho&agrave;n to&agrave;n dạng TFT đẹp mắt.</p>\r\n\r\n<p><img alt=\"Đánh giá xe Honda CR-V 2017 về thiết kế nội ngoại thất và giá bán\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/11/honda-cr-v-2017-4.jpg\" style=\"height:634px; width:1200px\" /></p>\r\n\r\n<p>Đ&egrave;n pha tự động điều chỉnh độ cao, cảm biến gạt mưa,&hellip; l&agrave; những thay đổi đ&aacute;ng ch&uacute; &yacute; kh&aacute;c. C&oacute; thể n&oacute;i, Honda CR-V 2017 l&agrave; sự lột x&aacute;c so với thế hệ cũ. Ngoại h&igrave;nh mang thi&ecirc;n hướng t&aacute;o bạo v&agrave; thể thao hơn, kh&ocirc;ng c&ograve;n những n&eacute;t thiết kế bầu bầu ở thế hệ trước. Đ&egrave;n pha LED xếp lớp, mang đặc trưng của Honda đ&atilde; &aacute;p dụng tr&ecirc;n Accord v&agrave; Civic, tạo th&agrave;nh một điểm nhận diện mới. Hốc h&uacute;t gi&oacute; ph&iacute;a dưới tự động đ&oacute;ng/mở để cải thiện t&iacute;nh kh&iacute; động học. Điểm chung duy nhất ở ph&iacute;a trước chỉ l&agrave; chi tiết gương chiếu hậu.</p>\r\n\r\n<p><img alt=\"Đánh giá xe Honda CR-V 2017 về thiết kế nội ngoại thất và giá bán\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/11/danh-gia-xe-honda-cr-v-2017-1.jpg\" style=\"height:612px; width:1167px\" /></p>\r\n\r\n<p>Về an to&agrave;n,&nbsp;<strong>Honda CR-V 2017</strong>&nbsp;đ&atilde; trang bị bộ c&ocirc;ng nghệ an to&agrave;n Honda Sensing từ đời 2015, gi&uacute;p mẫu xe được đ&aacute;nh gi&aacute; rất cao bởi tạp ch&iacute; Motor Trend. Phi&ecirc;n bản 2017 c&ograve;n mở rộng việc &aacute;p dụng Honda Sensing ngay ở những phi&ecirc;n bản ti&ecirc;u chuẩn. Honda Sensing gồm c&oacute; tự động phanh khẩn cấp với cảnh b&aacute;o va chạm ph&iacute;a trước (gồm cả người đi bộ), ph&aacute;t hiện chệch khỏi đường, cảnh b&aacute;o chệch l&agrave;n, kiểm so&aacute;t h&agrave;nh tr&igrave;nh th&iacute;ch ứng, hỗ trợ giữ l&agrave;n, cảnh b&aacute;o điểm m&ugrave;, v&agrave; cảnh b&aacute;o điểm m&ugrave; khi l&ugrave;i.</p>\r\n\r\n<p><img alt=\"Đánh giá xe Honda CR-V 2017 về thiết kế nội ngoại thất và giá bán\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/11/honda-cr-v-2017-1-1.jpg\" style=\"height:636px; width:1200px\" /></p>\r\n\r\n<p><strong>CR-V 2017</strong>&nbsp;ph&aacute;t triển dựa tr&ecirc;n platform của Civic, gi&uacute;p chiều d&agrave;i tăng 30mm, chiều d&agrave;i cơ sở tăng 40mm, cao v&agrave; rộng hơn 35mm so với thế hệ cũ. Điều n&agrave;y mang đến một kh&ocirc;ng gian rộng r&atilde;i hơn, dung t&iacute;ch khoang chứa đồ ph&iacute;a sau tăng th&ecirc;m 56 l&iacute;t.</p>\r\n\r\n<h2><strong>Đ&aacute;nh gi&aacute; nội thất Honda CR-V 2017:</strong></h2>\r\n\r\n<p>Nội thất cũng nhận được rất nhiều thay đổi đ&aacute;ng kể. Đầu ti&ecirc;n phải nhắc đến kh&ocirc;ng gian rộng r&atilde;i hơn, với khoảng trống duỗi ch&acirc;n ph&iacute;a sau tốt nhất ph&acirc;n kh&uacute;c. M&agrave;n h&igrave;nh giải tr&iacute; trung t&acirc;m c&oacute; viền đen b&oacute;ng mang lại c&aacute;i nh&igrave;n hiện đại, c&aacute;c n&uacute;t điều chỉnh đều ở dạng cảm ứng. Cụm đồng hồ mang đậm &acirc;m hưởng từ Civic, hiển thị ho&agrave;n to&agrave;n dạng TFT đẹp mắt. Trong khi đ&oacute;, cần số vẫn đặt lửng như thế trước d&ugrave; tạo h&igrave;nh cứng c&aacute;p hơn, b&ecirc;n cạnh c&oacute; thể thấy sự bổ sung n&uacute;t bấm phanh tay điện tử, chống tr&ocirc;i xe v&agrave; chế độ l&aacute;i tiết kiệm ECON.</p>\r\n\r\n<p><img alt=\"Đánh giá xe Honda CR-V 2017 về thiết kế nội ngoại thất và giá bán\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/11/danh-gia-xe-honda-cr-v-2017-2.jpg\" style=\"height:617px; width:1169px\" /></p>\r\n\r\n<p><strong>Nội thất Honda CR-V 2017</strong>&nbsp;kh&ocirc;ng c&aacute;ch mạng thay đổi như ngoại h&igrave;nh, nhưng nhiều thay đổi tiểu tiết lại mang lại sự kh&aacute;c biệt lớn. M&agrave;n h&igrave;nh giải tr&iacute; trung t&acirc;m c&oacute; viền đen b&oacute;ng mang lại c&aacute;i nh&igrave;n hiện đại. Cụm đồng hồ mang đậm &acirc;m hưởng từ Civic, hiển thị ho&agrave;n to&agrave;n dạng TFT đẹp mắt. Trong khi đ&oacute;, cần số vẫn đặt lửng như thế trước d&ugrave; tạo h&igrave;nh cứng c&aacute;p hơn, b&ecirc;n cạnh c&oacute; thể thấy sự bố tr&iacute; lại n&uacute;t bấm phanh tay điện tử, chống tr&ocirc;i xe v&agrave; chế độ l&aacute;i tiết kiệm ECON. Hệ thống giải tr&iacute; tr&ecirc;n Honda CR-V thế hệ thứ 5 d&ugrave;ng hệ điều h&agrave;nh Android, t&iacute;ch hợp hệ thống dẫn đường Honda Satellite-Linked Navigation System ph&aacute;t triển c&ugrave;ng c&aacute;c chuy&ecirc;n gia của Garmin.</p>\r\n\r\n<p><img alt=\"Đánh giá xe Honda CR-V 2017 về thiết kế nội ngoại thất và giá bán\" src=\"http://danhgiaxe.net/wp-content/uploads/2016/11/honda-cr-v-2017-2-1.jpg\" style=\"height:634px; width:1200px\" /></p>\r\n\r\n<p>Hệ thống giải tr&iacute; tr&ecirc;n Honda CR-V thế hệ thứ 5 d&ugrave;ng hệ điều h&agrave;nh Android, t&iacute;ch hợp hệ thống dẫn đường Honda Satellite-Linked Navigation System ph&aacute;t triển c&ugrave;ng c&aacute;c chuy&ecirc;n gia của Garmin. Ngo&agrave;i ra, sự thay đổi kh&aacute;c gồm, khởi động động cơ từ xa, cổng sạc USB, sấy gương, điều h&ograve;a 2 c&ugrave;ng, ghế l&aacute;i chỉnh điện 8 hướng,&hellip; Về an to&agrave;n, Honda CR-V đ&atilde; trang bị hệ thống&nbsp;tự động phanh khẩn cấp với cảnh b&aacute;o va chạm ph&iacute;a trước (gồm cả người đi bộ), ph&aacute;t hiện chệch khỏi đường, cảnh b&aacute;o chệch l&agrave;n, kiểm so&aacute;t h&agrave;nh tr&igrave;nh th&iacute;ch ứng, hỗ trợ giữ l&agrave;n, cảnh b&aacute;o điểm m&ugrave;, v&agrave; cảnh b&aacute;o điểm m&ugrave; khi l&ugrave;i.</p>\r\n', '1', '4', '5', null, 'Nhật Bản', '1178000000');
INSERT INTO `products` VALUES ('26', 'Honda Acord', 'accord.png', 'Thiết kế lịch lãm hơn, động cơ cải tiến, tiết kiệm nhiên liệu hơn với công nghệ Earth Dreams, đổi mới đèn sương mù và bổ sung đèn LED chạy ban ngày hiện đại cho cụm đèn pha.', '<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Ngoai-that-xe-Honda-Accord-2016.jpg\"><img alt=\"Ngoai that xe Honda Accord 2016\" src=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Ngoai-that-xe-Honda-Accord-2016-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto; width:700px\" /></a></p>\r\n\r\n<p>Nh&igrave;n một c&aacute;ch tổng thể, Honda Accord 2016 vẫn mang n&eacute;t thanh lịch v&agrave; sang trọng, thiết kế c&oacute; phần thể thao hơn nhưng kh&ocirc;ng qu&aacute; mạnh mẽ. Chiều d&agrave;i tổng thể của Honda Accord 2016 giảm đi đ&aacute;ng kể so với thế hệ trước, từ 4.950 mm xuống c&ograve;n 4.870 mm theo xu hướng chung của c&aacute;c d&ograve;ng xe hạng D.</p>\r\n\r\n<p><strong>Đầu xe</strong></p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Dau-xe-honda-accord-2016.jpg\"><img alt=\"Dau xe honda accord 2016\" src=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Dau-xe-honda-accord-2016-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto; width:700px\" /></a></p>\r\n\r\n<p>C&oacute; lẽ &nbsp;<strong>Honda Accord 2016&nbsp;</strong>muốn nhắm v&agrave;o ph&acirc;n kh&uacute;c kh&aacute;ch h&agrave;ng th&agrave;nh đạt, đứng tuổi nhưng vẫn t&igrave;m kiếm sự đổi mới v&agrave; ph&aacute; c&aacute;ch&hellip; C&oacute; rất nhiều chi tiết mang n&eacute;t hiện đại nhưng lại pha lẫn t&iacute;nh truyền thống. Điển h&igrave;nh l&agrave; cụm đ&egrave;n pha h&igrave;nh b&igrave;nh h&agrave;nh kinh điển được t&ocirc; điểm bằng d&atilde;y đ&egrave;n LED trang nh&atilde; v&agrave; b&oacute;ng đ&egrave;n cốt cũng được viền LED.<br />\r\n<strong>Th&acirc;n xe</strong></p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Than-xe-honda-accord-2016.jpg\"><img alt=\"Than xe honda accord 2016\" src=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Than-xe-honda-accord-2016-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto; width:700px\" /></a></p>\r\n\r\n<p>Honda Accord tuy ngắn hơn phi&ecirc;n bản tiền nhiệm nhưng với k&iacute;ch thước tổng thể d&agrave;i x rộng x cao tương ứng 4.870 x 1.850 x 1.465 (mm), nhưng vẫn giữ được sự thon thả v&agrave; lịch l&atilde;m của một chiếc sedan cỡ trung.<br />\r\n<strong>Đu&ocirc;i xe</strong><br />\r\n<a href=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Duoi-xe-Honda-Accord-2016.jpg\"><img alt=\"Duoi xe Honda Accord 2016\" src=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Duoi-xe-Honda-Accord-2016-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto; width:700px\" /></a></p>\r\n\r\n<p>Kh&aacute;c hẳn thế hệ trước, phần đu&ocirc;i xe c&oacute; thiết kế trau chuốt sắc sảo v&agrave; g&oacute;c cạnh hơn. Cản sau b&egrave; ra hai b&ecirc;n tạo sự vững chắc nhưng vẫn giữ được sự thon gọn. Đ&egrave;n sau cũng được trang bị c&ocirc;ng nghệ LED hiện đại hơn dạng Halogen ở thế hệ trước.<br />\r\n<strong>Nội thất xe Honda Accord 2016</strong></p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Noi-that-xe-Honda-Accord-2016.jpg\"><img alt=\"Noi that xe Honda Accord 2016\" src=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Noi-that-xe-Honda-Accord-2016-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto; width:700px\" /></a></p>\r\n\r\n<p>Nội thất tr&ecirc;n Honda Accord với thiết kế ho&agrave;n to&agrave;n mới mang lại cảm gi&aacute;c sang trọng v&agrave; lịch l&atilde;m. Cabin l&aacute;i cho cảm gi&aacute;c tầm nh&igrave;n rộng đầy &aacute;nh s&aacute;ng v&agrave; bề thế. C&aacute;c chi tiết ốp giả gỗ được tận dụng tối đa khắp cabin xe kết hợp c&ugrave;ng chất liệu da cao cấp với những đường chỉ nổi t&ocirc; điểm th&ecirc;m sự sang trọng trong cabin xe.<br />\r\n<strong>V&ocirc; lăng</strong></p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Vo-lang-xe-honda-accord-2016.jpg\"><img alt=\"Vo lang xe honda accord 2016\" src=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Vo-lang-xe-honda-accord-2016-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto; width:700px\" /></a></p>\r\n\r\n<p>V&ocirc;-lăng của Honda Accord l&agrave; dạng 4 chấu thể thao, bọc da cao cấp, t&iacute;ch hợp c&aacute;c n&uacute;t bấm điều chỉnh đa chức năng. Honda lu&ocirc;n thiết kế v&ocirc;-lăng theo phong c&aacute;ch lịch l&atilde;m v&agrave; tiện dụng. Chiếc Accord 2016 n&agrave;y cũng vậy, người l&aacute;i ho&agrave;n to&agrave;n bị thu h&uacute;t ngay khi nh&igrave;n thấy chiếc v&ocirc;-lăng n&agrave;y.</p>\r\n\r\n<p><strong>H&agrave;ng ghế trước</strong></p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Hang-ghe-truoc-honda-accord-2016.jpg\"><img alt=\"Hang ghe truoc honda accord 2016\" src=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Hang-ghe-truoc-honda-accord-2016-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto; width:700px\" /></a></p>\r\n\r\n<p>L&agrave; một trong những mẫu xe được đ&aacute;nh gi&aacute; cao ở kh&ocirc;ng gian tiện nghi v&agrave; độ rộng r&atilde;i cabin xe, kh&ocirc;ng gian b&ecirc;n trong xe l&agrave; kh&aacute; thoải m&aacute;i cho người ngồi duỗi ch&acirc;n, đặc biệt l&agrave; h&agrave;ng ghế sau.<br />\r\n<strong>Trang bị tiện nghi tr&ecirc;n xe</strong></p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Tien-nghi-tren-xe-accord-2016.jpg\"><img alt=\"Tien nghi tren xe accord 2016\" src=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Tien-nghi-tren-xe-accord-2016-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto; width:700px\" /></a></p>\r\n\r\n<p>D&agrave;n &acirc;m thanh ti&ecirc;u chuẩn tr&ecirc;n Honda Accord 2016 kh&aacute; n&eacute;t v&agrave; phủ khắp kh&ocirc;ng gian trong xe. Ch&uacute;ng t&ocirc;i thật sự ấn tượng ngo&agrave;i mong đợi với sự thể hiện của hệ thống &acirc;m thanh 6 loa, v&agrave; c&oacute; lẽ cũng nhờ v&agrave;o hệ thống hệ thống chủ động kiểm so&aacute;t tiếng ồn ANC (Active Noise Control) lọc tạp &acirc;m tốt kết hợp với hệ thống ASC (Active Sound Control) trợ thanh đ&atilde; để lại kh&ocirc;ng gian tĩnh lặng cho nhạc &acirc;m b&ecirc;n trong được tr&igrave;nh diễn ho&agrave;n hảo.</p>\r\n\r\n<p><strong>Khoang chứa đồ</strong></p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Khoang-chua-do-honda-accord-2016.jpg\"><img alt=\"Khoang chua do honda accord 2016\" src=\"http://hondagiaiphong.com/wp-content/uploads/2015/09/Khoang-chua-do-honda-accord-2016-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto; width:700px\" /></a></p>\r\n\r\n<p>Khoang h&agrave;nh l&yacute; kh&aacute; rộng r&atilde;i cho những chuyến h&agrave;nh tr&igrave;nh xa.</p>\r\n', '1', '6', '5', null, 'Nhật Bản', '1390000000');
INSERT INTO `products` VALUES ('27', 'Honda City 2017', 'Hondacity.png', 'Mẫu xe City 2017 có thêm nhiều tính năng mới', '<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Honda-city-2017-Modulo.jpg\"><img alt=\"Honda city 2017 modulo\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Honda-city-2017-Modulo-700x320.jpg\" style=\"float:none; height:320px; margin:10px auto 0px; width:700px\" /></a><br />\r\n<strong>Mẫu xe City 2017 c&oacute; th&ecirc;m nhiều t&iacute;nh năng mới</strong></p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Khoi-dong-Start-Stop-tren-Honda-City-2017-1.jpg\"><img alt=\"khởi động start stop trên honda city 2017\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Khoi-dong-Start-Stop-tren-Honda-City-2017-1-700x365.jpg\" style=\"float:none; height:365px; margin:10px auto 0px; width:700px\" /></a></p>\r\n\r\n<ul style=\"list-style-type:circle\">\r\n	<li>Khởi động Start/Stop tr&ecirc;n Honda City mới</li>\r\n	<li>Khởi h&agrave;nh ngang dốc</li>\r\n	<li>Gương gập điện</li>\r\n	<li>N&uacute;t chỉnh &acirc;m thanh ngay tr&ecirc;n v&ocirc; lăng</li>\r\n	<li>Bổ sung m&agrave;u đỏ mận v&agrave; ngoại thất bắt mắt th&ecirc;m m&agrave;u đỏ mận</li>\r\n</ul>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/honda-city-2017-mau-do-man.jpg\"><img alt=\"Honda city 2017 màu đỏ mậnHonda city 2017 màu đỏ mận\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/honda-city-2017-mau-do-man-700x319.jpg\" style=\"float:none; height:319px; margin:10px auto 0px; width:700px\" /></a></p>\r\n\r\n<p>Honda Việt Nam đ&atilde; từng bước chiếm trọn tr&aacute;i tim kh&aacute;ch h&agrave;ng v&agrave; trở th&agrave;nh lựa chọn đầu ti&ecirc;n cho những ai đang t&igrave;m kiếm một chiếc xe ưng &yacute; trong ph&acirc;n kh&uacute;c sedan cỡ nhỏ. Với năm m&agrave;u sống động bao gồm: Xanh dương c&aacute; t&iacute;nh, Đen &aacute;nh độc t&ocirc;n, Titan mạnh mẽ, Ghi bạc thời trang v&agrave; Trắng ng&agrave; tinh tế. Giống như c&aacute;c mẫu xe kh&aacute;c, City ho&agrave;n to&agrave;n mới sẽ được &aacute;p dụng chế độ bảo h&agrave;nh 3 năm hoặc 100.000 km t&ugrave;y theo điều kiện n&agrave;o đến trước. B&ecirc;n cạnh đ&oacute;, Honda Việt Nam cũng cung cấp g&oacute;i gia hạn bảo h&agrave;nh t&ugrave;y chọn.</p>\r\n\r\n<p><strong>C&ocirc;ng nghệ cốt l&otilde;i ứng dụng tr&ecirc;n Honda City</strong></p>\r\n\r\n<p>Ứng dụng tr&ecirc;n Động cơ xăng &ndash; Động cơ dẫn đầu ng&agrave;nh về t&iacute;nh năng vận h&agrave;nh v&agrave; tiết kiệm nhi&ecirc;n liệu.</p>\r\n\r\n<p>Với việc cải tiến c&ocirc;ng nghệ điều khiển van biến thi&ecirc;n VTEC nguy&ecirc;n bản nhằm tăng cường hiệu quả qu&aacute; tr&igrave;nh đốt ch&aacute;y v&agrave; giảm thiểu ma s&aacute;t, c&ocirc;ng nghệ n&agrave;y mang đến sự kết hợp tối ưu giữa c&ocirc;ng suất đầu ra cao v&agrave; hiệu suất sử dụng nhi&ecirc;n liệu. Hơn thế nữa, h&agrave;ng loạt c&aacute;c kiểu động cơ mới đ&atilde; được ph&aacute;t triển, ứng dụng cấu tr&uacute;c mới nhằm đạt được khả năng linh hoạt cao hơn.</p>\r\n\r\n<p>Thiết kế kh&iacute; động học v&agrave; phong c&aacute;ch thể thao to&aacute;t l&ecirc;n kiểu d&aacute;ng trẻ trung đầy linh hoạt, g&acirc;y ấn tượng ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n.</p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Honda-city-2017-c%C3%B3-thi%E1%BA%BFt-k%E1%BA%BF-kh%C3%AD-%C4%91%E1%BB%99ng-h%E1%BB%8Dc-v%C3%A0-phong-c%C3%A1ch-th%E1%BB%83-thao.jpg\"><img alt=\"Honda city 2017 có thiết kế khí động học và phong cách thể thao\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Honda-city-2017-c%C3%B3-thi%E1%BA%BFt-k%E1%BA%BF-kh%C3%AD-%C4%91%E1%BB%99ng-h%E1%BB%8Dc-v%C3%A0-phong-c%C3%A1ch-th%E1%BB%83-thao-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto 0px; width:700px\" /></a></p>\r\n\r\n<p>G&oacute;i tiện &iacute;ch th&ocirc;ng minh bao gồm c&aacute;c tiện &iacute;ch cao cấp như chế độ l&aacute;i tiết kiệm nhi&ecirc;n liệu Econ mode, lẫy chuyển số tay, cảm biến l&ugrave;i, chế độ thoại rảnh tay, cửa gi&oacute; điều h&ograve;a cho h&agrave;ng ghế ph&iacute;a sau, v.v&hellip;mang lại gi&aacute; trị mới phục vụ tối đa nhu cầu kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Trai-nghiem-thu-vi-khi-lai-xe-Honda-city-2017.jpg\"><img alt=\"Trai nghiem thu vi khi lai xe Honda city 2017\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Trai-nghiem-thu-vi-khi-lai-xe-Honda-city-2017-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto 0px; width:700px\" /></a></p>\r\n\r\n<p>Trải nghiệm th&uacute; vị khi l&aacute;i xe: Hộp số v&ocirc; cấp CVT &ndash; một trong những nh&acirc;n tố thuộc c&ocirc;ng nghệ truyền động đột ph&aacute; Earth Dreams thế hệ mới của Honda tr&ecirc;n to&agrave;n cầu &ndash; lần đầu ti&ecirc;n được trang bị, gi&uacute;p tối đa hiệu suất v&agrave; tiết kiệm nhi&ecirc;n liệu. C&ocirc;ng suất v&agrave; m&ocirc;men xoắn cực đại dẫn đầu so với c&aacute;c d&ograve;ng xe c&ugrave;ng ph&acirc;n kh&uacute;c kết hợp với lẫy chuyển số tay g&oacute;p phần tạo n&ecirc;n cảm gi&aacute;c l&aacute;i đầy hứng khởi.</p>\r\n\r\n<p><strong>Thiết kế ngoại thất Honda City 2017</strong></p>\r\n\r\n<p>Ngoại thất của City mới được thiết kế dựa tr&ecirc;n &yacute; tưởng &ldquo;Chuyển động giao thoa mượt m&agrave;&rdquo;.Chiều d&agrave;i cơ sở được gia tăng th&ecirc;m 50mm so với thiết kế hiện tại, theo đ&oacute; chiều d&agrave;i v&agrave; chiều cao tổng thể của xe cũng tăng tương ứng 45mm v&agrave; 5mm gi&uacute;p xe vận h&agrave;nh đầm chắc hơn, cabin cũng rộng hơn l&agrave;m tăng sự thoải m&aacute;i cho h&agrave;nh kh&aacute;ch. Đ&acirc;y l&agrave; thiết kế kiểu d&aacute;ng mới của Honda, kết hợp vẻ tao nh&atilde; từ phong c&aacute;ch xe coupe với những đường chuyển động đồng nhất, mang lại hiệu quả kh&iacute; động học với những đường n&eacute;t năng động v&agrave; thể thao.</p>\r\n\r\n<p><strong>Đầu xe</strong></p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Dau-xe-Honda-City-2017.jpg\"><img alt=\"Đầu xe Honda City 2017\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Dau-xe-Honda-City-2017.jpg\" style=\"float:none; height:224px; margin:10px auto 0px; width:448px\" /></a></p>\r\n\r\n<p>&ldquo;Đ&ocirc;i c&aacute;nh vững ch&atilde;i&rdquo; l&agrave; triết l&yacute; thiết kế mới của Honda. Ốp mặt ca-lăng mạ cr&ocirc;m v&agrave; cụm đ&egrave;n pha được kết nối với nhau l&agrave;m nổi bật logo chữ H, nhấn mạnh độ tinh xảo v&agrave; cao cấp. Đ&egrave;n b&aacute;o rẽ s&aacute;ng b&oacute;ng ở phần r&igrave;a của đ&ocirc;i c&aacute;nh tạo ấn tượng gọn g&agrave;ng sắc n&eacute;t. Phần giữa của đ&ocirc;i c&aacute;nh gồm đ&egrave;n sương m&ugrave; v&agrave; đ&egrave;n pha c&oacute; kiểu tối m&agrave;u, tương phản với phần mạ bạc ở r&igrave;a của đ&ocirc;i c&aacute;nh.</p>\r\n\r\n<p><strong>Th&acirc;n xe</strong></p>\r\n\r\n<p>Phong c&aacute;ch xe coupe kết hợp với đường g&acirc;n khỏe khoắn sắc sảo vuốt từ cửa trước tới đ&egrave;n hậu l&agrave; một điểm nhấn nổi bật của Honda City mới. Đường g&acirc;n tr&ecirc;n th&acirc;n xe s&acirc;u v&agrave; sắc cạnh lu&ocirc;n hiện r&otilde; d&ugrave; trong điều kiện thời tiết nắng hay m&acirc;y m&ugrave;, đem lại ấn tượng mạnh ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n. Hơn thế nữa, thiết kế chắn b&ugrave;n trước v&agrave; sau xe mang lại cảm gi&aacute;c linh hoạt nhưng cũng kh&ocirc;ng k&eacute;m phần thể thao.</p>\r\n\r\n<p><strong>Đu&ocirc;i xe</strong></p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Duoi-xe-Honda-City-2017.jpg\"><img alt=\"Đuôi xe Honda City 2017\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Duoi-xe-Honda-City-2017.jpg\" style=\"float:none; height:269px; margin:10px auto 0px; width:448px\" /></a><br />\r\nĐ&egrave;n hậu được thiết kế m&agrave;u đỏ ngọc, mở rộng sang cả nắp cốp xe v&agrave; kết nối với thanh trang tr&iacute; mạ cr&ocirc;m, tạo n&ecirc;n một vẻ ngo&agrave;i đầm chắc v&agrave; sang trọng.</p>\r\n\r\n<p><strong>B&aacute;nh xe</strong></p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/banh-xe-honda-city-2017.jpg\"><img alt=\"Banh xe Honda city 2017\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/banh-xe-honda-city-2017.jpg\" style=\"float:none; height:252px; margin:10px auto 0px; width:448px\" /></a></p>\r\n\r\n<p>La zăng đ&uacute;c 16 inch với thiết kế 5 chấu đậm chất thể thao v&agrave; c&aacute; t&iacute;nh đưa bạn tỏa s&aacute;ng độc nhất tr&ecirc;n mọi cung đường (CVT).</p>\r\n\r\n<p><strong>Thiết kế nội thất Honda City 2017</strong></p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Noi-that-Honda-City-2017.jpg\"><img alt=\"Nội thất Honda City 2017\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Noi-that-Honda-City-2017-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto 0px; width:700px\" /></a></p>\r\n\r\n<p>Hướng tới mục ti&ecirc;u tạo ra chiếc Honda City mới hiện đại v&agrave; cao cấp, Honda x&acirc;y dựng &yacute; tưởng thiết kết nội thất với &yacute; tưởng &ldquo;Khoang l&aacute;i đa tầng&rdquo; thể hiện kiểu d&aacute;ng hiện đại v&agrave; thể thao c&ugrave;ng với bề mặt chất lượng cao v&agrave; cảm gi&aacute;c thoải m&aacute;i khi điều khiển.</p>\r\n\r\n<p><strong>Bảng điều khiển</strong></p>\r\n\r\n<p>Bảng điều khiển bao gồm nhiều lớp c&oacute; thiết kế bề mặt kh&aacute;c nhau. Đối lập với phần nền đen, bảng T-zone được mạ bạc đem đến hiệu ứng tương phản 3 chiều. Bề mặt tổng thể của bảng điều khiển c&oacute; m&agrave;u kim loại mang lại cảm gi&aacute;c sang trọng. Cửa gi&oacute; điều h&ograve;a nhiệt độ cực mỏng v&agrave; phong c&aacute;ch. Th&ecirc;m v&agrave;o đ&oacute;, chỉ số mức độ ti&ecirc;u hao nhi&ecirc;n liệu tức thời, trung b&igrave;nh v&agrave; qu&atilde;ng đường c&ograve;n lại m&agrave; xe c&oacute; thể đi đều được hiển thị rất thuận tiện cho người điều khiển.</p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Bang-dieu-khien-Honda-City-2017-thuan-tien-cho-nguoi-su-dung.jpg\"><img alt=\"Bang dieu khien Honda City 2017 thuan tien cho nguoi su dung\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Bang-dieu-khien-Honda-City-2017-thuan-tien-cho-nguoi-su-dung-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto 0px; width:700px\" /></a></p>\r\n\r\n<p><strong>Tiện &iacute;ch tối ưu</strong></p>\r\n\r\n<p>City mới được t&iacute;ch hợp nhiều kh&ocirc;ng gian chứa đồ cho h&agrave;nh kh&aacute;ch như hộc đựng cốc h&agrave;ng ghế trước, hộc đựng đồ cửa xe trước v&agrave; sau, hộc đồ ph&iacute;a trước ghế l&aacute;i v&agrave; ghế phụ&hellip;</p>\r\n\r\n<p>City mới theo đuổi c&aacute;c gi&aacute; trị vượt l&ecirc;n tr&ecirc;n bề ngo&agrave;i nhỏ gọn c&ugrave;ng với thiết kế thể thao năng động v&agrave; cabin sang trọng. Hơn thế nữa, g&oacute;i tiện &iacute;ch th&ocirc;ng minh bao gồm cảm biến l&ugrave;i, chế độ thoại rảnh tay, lẫy chuyển số tr&ecirc;n v&ocirc; lăng&hellip; sẽ l&agrave;m h&agrave;i l&ograve;ng kh&aacute;ch h&agrave;ng hơn nữa, gi&uacute;p kh&aacute;ch h&agrave;ng tận hưởng niềm vui l&aacute;i xe một c&aacute;ch trọn vẹn.</p>\r\n\r\n<p><strong><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Hinh-anh-Honda-city-2017.jpg\"><img alt=\"hinh anh honda city 2017\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Hinh-anh-Honda-city-2017-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto 0px; width:700px\" /></a></strong></p>\r\n\r\n<p><strong>C&ocirc;ng nghệ ứng dụng tr&ecirc;n xe Honda City 2017</strong></p>\r\n\r\n<p><strong>Động cơ Honda City 2017</strong></p>\r\n\r\n<p>Động cơ 1.5L SOHC i-VTEC: City mới sử dụng động cơ 16-van 4-xilanh 1.5L i-VTEC sản sinh c&ocirc;ng suất cực đại 88 kW/6600rpm v&agrave; momen xoắn cực đại 145 Nm/4600rpm, mang lại khả năng vận h&agrave;nh v&agrave; hiệu suất ti&ecirc;u thụ nhi&ecirc;n liệu tối ưu trong ph&acirc;n kh&uacute;c.</p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Dong-co-earth-dreams-cua-Honda-City.jpg\"><img alt=\"Động cơ Earth Dreams của Honda City\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Dong-co-earth-dreams-cua-Honda-City.jpg\" style=\"float:none; height:491px; margin:10px auto 0px; width:670px\" /></a></p>\r\n\r\n<p>&ldquo;Hệ thống hướng dẫn l&aacute;i tiết kiệm nhi&ecirc;n liệu&rdquo; gi&uacute;p người l&aacute;i chủ động tiết kiệm tối đa nhi&ecirc;n liệu. Với dải đ&egrave;n LED bao quanh đồng hồ tốc độ, hệ thống hướng dẫn l&aacute;i tiết kiệm nhi&ecirc;n liệu chỉ ra trạng th&aacute;i ti&ecirc;u thụ nhi&ecirc;n liệu của chiếc xe v&agrave; hướng dẫn t&agrave;i xế vận h&agrave;nh xe hiệu quả hơn. Đặc biệt, City mới lần đầu ti&ecirc;n được trang bị n&uacute;t bấm ECON. Khi chế độ Econ được k&iacute;ch hoạt, một số đặc t&iacute;nh vận h&agrave;nh của động cơ sẽ được được điều chỉnh để n&acirc;ng cao hiệu quả sử dụng nhi&ecirc;n liệu.</p>\r\n\r\n<p><strong>Hộp số Honda City</strong></p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Hop-so-5-cap-honda-city-2017.jpg\"><img alt=\"Hộp số 5 cấp Honda City 2017\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/Hop-so-5-cap-honda-city-2017-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto 0px; width:700px\" /></a></p>\r\n\r\n<p>Hộp số tự động v&ocirc; cấp (CVT) gi&uacute;p chuyển động mượt m&agrave;, &ecirc;m &aacute;i v&agrave; kh&ocirc;ng c&ograve;n cảm gi&aacute;c hẫng/giật mỗi khi chuyển số.<br />\r\nỨng dụng nền tảng c&ocirc;ng nghệ đột ph&aacute; Earth Dreams của Honda với hộp số CVT:</p>\r\n\r\n<ul>\r\n	<li>Hệ thống kết hợp kiểm so&aacute;t chuyển số, bướm ga v&agrave; thủy lực cung cấp khả năng th&iacute;ch ứng với nhu cầu tăng tốc của người l&aacute;i, gi&uacute;p xe tăng tốc nhanh v&agrave; li&ecirc;n tục từ l&uacute;c khởi động cho đến khi đạt tốc độ mong muốn, mang đến cảm gi&aacute;c l&aacute;i tự nhi&ecirc;n v&agrave; thể thao.</li>\r\n	<li>Dải truyền rộng v&agrave; cơ cấu truyền động CVT gọn nhẹ c&ugrave;ng ma s&aacute;t được giảm thiểu, gi&uacute;p tối ưu h&oacute;a hiệu suất truyền động v&agrave; cải thiện khả năng tiết kiệm nhi&ecirc;n liệu.</li>\r\n	<li>Nếu muốn trải nghiệm cảm gi&aacute;c l&aacute;i phấn kh&iacute;ch thể thao, bạn c&oacute; thể chuyển cần số sang chế độ S v&agrave; sử dụng lẫy chuyển số tay t&iacute;ch hợp tr&ecirc;n v&ocirc; lăng. Khi đ&oacute;, hộp số v&ocirc; cấp chuyển sang chế độ truyền động với 7 cấp số ảo.</li>\r\n</ul>\r\n\r\n<p><strong>Khung gầm City 2017</strong></p>\r\n\r\n<p>Với mục ti&ecirc;u trở th&agrave;nh chiếc sedan cỡ nhỏ năng động v&agrave; linh hoạt, khung gầm của City thế hệ thứ 4 được thiết kế mới ho&agrave;n to&agrave;n. Khả năng c&acirc;n bằng gi&uacute;p tăng cường sự thoải m&aacute;i khi l&aacute;i xe đồng thời cải thiện độ an to&agrave;n. Để đạt được mục ti&ecirc;u về hiệu suất khung gầm, bao gồm độ ổn định khi l&aacute;i xe v&agrave; độ phản hồi với v&ocirc; lăng; cảm gi&aacute;c thoải m&aacute;i khi đi tr&ecirc;n đường phẳng v&agrave; giảm bớt độ x&oacute;c; hệ thống phanh hiệu quả; c&acirc;n bằng tiếp x&uacute;c mặt đất v&agrave; hiệu suất ti&ecirc;u thụ nhi&ecirc;n liệu tối đa, một loạt những cải tiến to&agrave;n diện đ&atilde; được &aacute;p dụng cho hệ thống khung gầm của City mới, bao gồm:</p>\r\n\r\n<p><strong>Hệ thống giảm x&oacute;c</strong></p>\r\n\r\n<p>&ndash; Hệ thống treo trước MacPherson kiểu d&aacute;ng mới được tối ưu h&oacute;a gi&uacute;p tăng m&ocirc;men xoắn tự định hướng v&agrave; duy tr&igrave; cảm gi&aacute;c b&aacute;m đường trong mọi điều kiện l&aacute;i xe.</p>\r\n\r\n<p>&ndash; Hệ thống treo sau giằng xoắn h&igrave;nh chữ Hcải thiện độ ổn định v&agrave; sự thoải m&aacute;i khi l&aacute;i, giảm trọng lượng, tăng kh&ocirc;ng gian b&ecirc;n trong xe. Thiết kế mới ch&iacute;nh l&agrave; khởi đầu của việc &aacute;p dụng nhiều loại giảm x&oacute;c, l&agrave;m giảm g&oacute;c lắp đặt bộ giảm x&oacute;c v&agrave; c&aacute;c loại đệm chống chất lỏng cho ống l&oacute;t.</p>\r\n\r\n<p><strong>An to&agrave;n tr&ecirc;n Honda City</strong></p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/He-thong-an-toan-tren-Honda-city-2017.jpg\"><img alt=\"He thong an toan tren Honda city 2017\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/He-thong-an-toan-tren-Honda-city-2017-700x394.jpg\" style=\"float:none; height:394px; margin:10px auto 0px; width:700px\" /></a></p>\r\n\r\n<p>Honda li&ecirc;n tục ph&aacute;t triển c&ocirc;ng nghệ an to&agrave;n chủ động v&agrave; thụ động. City mới c&oacute; phần th&acirc;n cứng c&aacute;p kết hợp với Kỹ thuật Tương th&iacute;ch Ti&ecirc;n tiến (ACETM) gi&uacute;p tăng cường khả năng tự bảo vệ đồng thời giảm nhẹ hư hại g&acirc;y ra đối với xe kh&aacute;c. Th&ecirc;m v&agrave;o đ&oacute; l&agrave; thiết kế giảm nhẹ chấn thương cho người đi bộ ở phần trước của xe.</p>\r\n\r\n<p><a href=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/H%E1%BB%87-th%E1%BB%91ng-ch%E1%BB%91ng-b%C3%B3-c%E1%BB%A9ng-phanh-ABS-v%C3%A0-H%E1%BB%87-th%E1%BB%91ng-ph%C3%A2n-b%E1%BB%95-l%E1%BB%B1c-phanh-%C4%91i%E1%BB%87n-t%E1%BB%AD-EBD.jpg\"><img alt=\"Hệ thống chống bó cứng phanh ABS và Hệ thống phân bổ lực phanh điện tử EBD\" src=\"http://hondagiaiphong.com/wp-content/uploads/2017/01/H%E1%BB%87-th%E1%BB%91ng-ch%E1%BB%91ng-b%C3%B3-c%E1%BB%A9ng-phanh-ABS-v%C3%A0-H%E1%BB%87-th%E1%BB%91ng-ph%C3%A2n-b%E1%BB%95-l%E1%BB%B1c-phanh-%C4%91i%E1%BB%87n-t%E1%BB%AD-EBD-700x394.jpg\" style=\"height:394px; margin:0px; width:700px\" /></a></p>\r\n\r\n<p>Hệ thống chống b&oacute; cứng phanh ABS v&agrave; Hệ thống ph&acirc;n bổ lực phanh điện tử EBD</p>\r\n\r\n<p>Hệ thống t&uacute;i kh&iacute; SRS: được thiết kế nhằm l&agrave;m giảm chấn thương m&agrave; vẫn đảm bảo kh&ocirc;ng gian của người ngồi trong xe.</p>\r\n', '1', '6', '5', '15', 'Nhật Bản', '533000000');
INSERT INTO `products` VALUES ('28', 'Toyota Vios', 'Xe-Toyota-Vios.png', 'Về ngoại thất trẻ trung, năng động và đầy sức hút.', '<p><img alt=\"Động cơ Vios E 2017 2018 2019 có gì mới\" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/08/1395217812_vios_2014_ngoaithat2.jpg\" style=\"height:440px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<h2><strong>Về ngoại thất trẻ trung, năng động v&agrave; đầy sức h&uacute;t.</strong></h2>\r\n\r\n<p>Toyota Vios Model 2017 được thiết kế động học với chiều d&agrave;i 4,41 m chiều rộng 1,7m. Ấn tượng đầu ti&ecirc;n thuộc về d&agrave;n đ&egrave;n pha được trang bị c&ocirc;ng nghệ halogen projector độc đ&aacute;o g&oacute;p phần l&agrave;m n&ecirc;n n&eacute;t c&aacute; t&iacute;nh đặc biệt cho phần đầu xe đồng thời mang tới một khả năng chiếu s&aacute;ng tuyệt vời.</p>\r\n\r\n<p><img alt=\"Các thiết kế của Xe Toyota Vios 2017\" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/08/1395218948_vios_ngoithat_anhnho1.jpg\" style=\"height:475px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p>Lưới tản nhiệt được trang điểm lộng lẫy bằng thanh ngang crom cỡ lớn sang trọng, lạ mắt v&agrave; tinh tế. Xung quanh th&acirc;n xe, những đường dập gai g&oacute;c mang đến cho Toyota Vios 2017 những n&eacute;t rất mới, tiềm ẩn một n&eacute;t b&iacute; ẩn m&agrave; phong c&aacute;ch v&agrave; qu&yacute; ph&aacute;i.</p>\r\n\r\n<p><img alt=\"Toyota Vios 2017 - hinh anh thong so\" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/08/1395218640_vios_ngoithat_anhnho2.jpg\" style=\"height:475px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p>Gương chiếu hậu mang m&agrave;u của th&acirc;n xe. Ch&uacute;ng kh&ocirc;ng chỉ xinh xắn m&agrave; c&ograve;n tiện &iacute;ch khi c&oacute; thể gập chỉnh điện v&agrave; được t&iacute;ch hợp với đ&egrave;n xi nhan an to&agrave;n. Đ&acirc;y l&agrave; một trong những minh chứng cho thấy, Vios 2017 vừa đẹp, vừa đa dụng.</p>\r\n\r\n<p>Những ch&acirc;n trụ vững chắc của Toyota Vios 2017 l&agrave; la zăng hợp kim đa chấu, mang lại cho xe n&eacute;t thể thao, khỏe khoắn v&agrave; cứng c&aacute;p.</p>\r\n\r\n<p><img alt=\"Đuôi xe Vios E 2017\" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/08/IMG_0365.jpg\" style=\"height:600px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p>Nh&igrave;n về ph&iacute;a đu&ocirc;i xe, Toyota Vios 2017 kh&ocirc;ng c&oacute; nhiều n&eacute;t kh&aacute;c biệt với bản cũ tuy nhi&ecirc;n vẫn hấp dẫn bởi cụm đ&egrave;n hậu mở rộng vừa đ&aacute;p ứng &aacute;nh s&aacute;ng cho l&aacute;i xe quan s&aacute;t kh&ocirc;ng gian ph&iacute;a sau, vừa gi&uacute;p những phương tiện ph&iacute;a sau dễ quan s&aacute;t để c&oacute; hướng di chuyển hợp l&yacute;.</p>\r\n\r\n<p><strong>Nội thất sang trọng, thanh lịch v&agrave; đa dụng</strong></p>\r\n\r\n<p>Qu&yacute; vị sẽ ho&agrave;n to&agrave;n bị chinh phục khi vừa chạm c&aacute;i nh&igrave;n đầu ti&ecirc;n v&agrave;o nội thất Toyota Vios 2017. Một kh&ocirc;ng gian mở ra với nhiều chiều &aacute;nh s&aacute;ng biến chiếc xe th&agrave;nh một ng&ocirc;i nh&agrave; di động tr&agrave;n trề sức sống. Bảng đồng hồ thể thao, c&aacute; t&iacute;nh với thiết kế 3D 3 v&ograve;ng độc đ&aacute;o đặc biệt với v&ograve;ng đồng hồ trung t&acirc;m được mạ Crom xinh xắn. M&agrave;n h&igrave;nh hiện thị &aacute;nh s&aacute;ng xanh hiện đại cho biết c&aacute;c th&ocirc;ng số như mức ti&ecirc;u hao nhi&ecirc;n liệu, thời gian, vận tốc tức thời của xe. V&ocirc; lăng chia chấu đặc biệt được t&iacute;ch hợp với n&uacute;t điều khiển gọn g&agrave;ng v&agrave; tiện dụng.</p>\r\n\r\n<p><img alt=\"noi that vios g 2017\" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/10/noi-that-vios-g-1024x533.jpg\" style=\"height:417px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p>Hệ thống ghế ngồi ở bản G đặc biệt được bọc da &ecirc;m &aacute;i được thiết kế tinh tế lối đi rộng, tiện lợi cho việc di chuyển m&agrave; h&agrave;ng ghế vẫn c&ograve;n n&eacute;t bề thế, ph&ugrave; hợp với kh&ocirc;ng gian xe. Khoảng trần v&agrave; khoảng để ch&acirc;n đều cho cảm gi&aacute;c rộng r&atilde;i v&agrave; thoải m&aacute;i hơn. H&agrave;ng ghế sau c&oacute; khả năng gập chỉnh điện 60 : 40 mở rộng khoang h&agrave;nh l&yacute;, hỗ trợ tối đa để chuy&ecirc;n chở cho những chuyến đi nhiều h&agrave;nh l&yacute; cồng kềnh.</p>\r\n\r\n<p>Toyota Vios 2017 được trang bị hệ thống điều h&ograve;a với khả năng l&agrave;m m&aacute;t cực nhanh. Hệ thống n&agrave;y c&oacute; khả năng điều chỉnh tự động ở bản cao cấp G v&agrave; chỉnh tay ở bản E v&agrave; J. Hơi m&aacute;t lan tỏa nhanh ch&oacute;ng v&agrave; được ph&acirc;n bố đều khắp kh&ocirc;ng gian xe.</p>\r\n\r\n<p>Hệ thống giải tr&iacute; được đầu tư d&agrave;n CD 1 đĩa, t&iacute;ch hợp AM/ FM ph&aacute;t nhạc ở cả 3 dạng MP3/ WMA/ AAC v&agrave; c&oacute; thể kết nối USB/ AUX</p>\r\n\r\n<p><img alt=\"Nội thất Vios E 2017\" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/08/IMG_0323.jpg\" style=\"height:601px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<p>bảng đồng hồ taplo ph&iacute;a l&aacute;i xe, với đồng hồ đa th&ocirc;ng tin gi&uacute;p bạn c&oacute; thể t&igrave;m thấy những th&ocirc;ng tin hữu &iacute;ch về &ocirc; t&ocirc;, như ti&ecirc;u thụ nhi&ecirc;n liệu của xe, đo đường, v&agrave; c&aacute;c dữ liệu cơ bản kh&aacute;c. Ngo&agrave;i ra, bảng điều khiển của xe th&agrave;nh phố n&agrave;y cũng l&agrave;m cho bạn dễ d&agrave;ng hơn để nh&igrave;n thấy mặt trước của xe.</p>\r\n\r\n<h3>Động cơ của Xe Toyota Vios 2017</h3>\r\n\r\n<p>Cả Vios E v&agrave; c&aacute;c Phi&ecirc;n bản G c&oacute; 4 xi-lanh thẳng h&agrave;ng, 16 van nạp x&atilde;, DOHC v&agrave; với hệ thống van biến thi&ecirc;n th&ocirc;ng minh VVT-i trong loại động cơ của n&oacute;, v&agrave; cũng năm 1497 như khả năng của họ. Xe Toyota Vios 2017 d&ograve;ng xe city car tiết kiệm nhi&ecirc;n liệu, với thiết kế ấn tượng v&agrave; độ bền bỉ. Vận tốc tối đa đạt 170 km / h với dung t&iacute;ch b&igrave;nh xăng 42L.</p>\r\n\r\n<p><img alt=\"Xe Toyota Vios 2017 2018 2019 khuyến mãi giá bao nhiêu?\" src=\"http://toyotahungvuong.net/wp-content/uploads/2015/08/1395371010_vios_vanhanh_anhlon2.jpg\" style=\"height:440px; margin:4px auto 7px; width:800px\" /></p>\r\n\r\n<h3>Hệ thống an to&agrave;n hiện đại v&agrave; hoạt động hiệu quả</h3>\r\n\r\n<p>C&aacute;c nh&agrave; thiết kế đầu tư cho Toyota Vios Model 2017 một hệ thống an to&agrave;n với đầy đủ t&iacute;nh năng hiện đại v&agrave; ho&agrave;n hảo. Đầu ti&ecirc;n phải kể đến hệ thống b&oacute; cứng phanh ABS gi&uacute;p người l&aacute;i y&ecirc;n t&acirc;m khi phanh gấp m&agrave; phanh kh&ocirc;ng bị b&oacute; cứng, xe vẫn tiếp tục lăn b&aacute;nh với tốc độ chậm hơn hỗ trợ thực hiện đ&aacute;ng l&aacute;i tr&aacute;nh chướng ngại vật v&agrave; tiếp tục cuộc h&agrave;nh tr&igrave;nh. Tiếp sau l&agrave; hệ thống c&acirc;n bằng th&acirc;n xe gi&uacute;p xe kh&ocirc;ng dễ bị lật ở những kh&uacute;c cua tay &aacute;o hay địa h&igrave;nh phức tạp. Hệ thống ph&acirc;n bổ lực phanh EBD ph&acirc;n bổ đều lực phanh l&ecirc;n cả 4 b&aacute;nh xe khiến xe giảm vận tốc y&ecirc;n ả mỗi khi l&aacute;i xe nh&aacute; phanh hay phanh gấp. Một yếu tố nữa l&agrave;m n&ecirc;n n&eacute;t ho&agrave;n hảo của hệ thống an to&agrave;n l&agrave; hệ thống hỗ trợ phanh khẩn cấp BA. Khi l&aacute;i xe phanh gấp, ngay lập tức bộ phận cảm biến nhận th&ocirc;ng tin v&agrave; nhanh ch&oacute;ng k&iacute;ch hoạt van điện cấp kh&iacute; n&eacute;n dồn v&agrave;o hệ thống khuếch đại lực phanh. Hỗ trợ lực phanh đủ mạnh để xe giảm tốc nhanh ch&oacute;ng theo nhu cầu của t&agrave;i xế.</p>\r\n', '1', '6', '3', '30', 'Nhật Bản', '564000000');
INSERT INTO `products` VALUES ('29', 'Hyundai TUCSON', 'TUCSON.png', '                             NĂNG LƯỢNG HỨNG KHỞI CHO TỪNG PHÚT GIÂY CẦM LÁI', '<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NGOẠI THẤ</p>\r\n\r\n<p>&lt;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/THEMXE/Tucson/NgoaiThat/parallax1.jpg\" style=\"height:491px; margin-top:-135px; width:1150px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>THIẾT KẾ THỂ THAO &ndash; MANG ĐẲNG CẤP VƯỢT TRỘI</strong></p>\r\n\r\n<p><strong>Lazang hợp kim</strong>Xe được trang bị 2 phi&ecirc;n bản t&ugrave;y chọn lazang: Lazang th&eacute;p 17 inch v&agrave; lazang hợp kim 18 inch mang t&iacute;nh thẩm mỹ cao cho sản phẩm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/THEMXE/Tucson/NgoaiThat/ngoaithat31.jpg\" style=\"height:auto; width:467px\" /></p>\r\n\r\n<p><strong>Đ&egrave;n hậu LED 3D</strong>Hyundai Tucson được trang bị đ&egrave;n hậu dạng LED, c&oacute; khả năng hiển thị &aacute;nh s&aacute;ng 3 chiều, gi&uacute;p tăng cường độ an to&agrave;n khi vận h&agrave;nh, đồng thời đem đến hiệu quả thẩm mỹ cao, thu h&uacute;t mọi &aacute;nh nh&igrave;n.</p>\r\n\r\n<p><img alt=\"exterior_front_sub1_on\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/THEMXE/Tucson/NgoaiThat/ngoaithat32.jpg\" style=\"height:auto; width:467px\" /></p>\r\n\r\n<p><strong>Tấm cản sau &nbsp;</strong>Cản sau của Hyundai Tucson được thiết kế đẹp mắt, đồng thời cũng tăng th&ecirc;m độ an to&agrave;n cho xe khi vận h&agrave;nh tr&ecirc;n đường.</p>\r\n\r\n<p>NỘI THẤT</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/THEMXE/Tucson/NoiThat/parallax2.jpg\" style=\"height:491px; width:1150px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>TIỆN NGHI HIỆN ĐẠI</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hyundai Tucson sở hữu một kh&ocirc;ng gian nội thất trực quan, được tối ưu h&oacute;a về mặt kh&ocirc;ng gian v&agrave; chức năng dựa tr&ecirc;n nền tảng c&ocirc;ng nghệ HMI ti&ecirc;n tiến, gi&uacute;p người l&aacute;i dễ d&agrave;ng l&agrave;m chủ c&aacute;c chức năng, tăng cường khả năng kết nối t&ugrave;y theo sở th&iacute;ch c&aacute; nh&acirc;n của mỗi người.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/THEMXE/Tucson/NoiThat/noithat2.jpg\" style=\"height:auto; width:311.328px\" /></p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh gi&aacute;m s&aacute;t lớn 4.2 Inch</strong>Bảng đồng hồ trung t&acirc;m của Hyundai Tucson c&oacute; m&agrave;n h&igrave;nh gi&aacute;m s&aacute;t LCD c&oacute; k&iacute;ch cỡ lớn 4,2 inch, gi&uacute;p hiển thị v&agrave; theo d&otilde;i c&aacute;c th&ocirc;ng số vận h&agrave;nh của chiếc xe một c&aacute;ch dễ d&agrave;ng hơn. Th&ecirc;m v&agrave;o đ&oacute;, c&aacute;c chức năng n&agrave;y c&oacute; thể dễ d&agrave;ng sử dụng v&agrave; c&agrave;i đặt bởi c&aacute;c n&uacute;t điều khiển được t&iacute;ch hợp tr&ecirc;n v&ocirc; lăng.</p>\r\n\r\n<p>VẬN H&Agrave;NH</p>\r\n\r\n<p><img alt=\"\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/THEMXE/Tucson/VanHanh/parallax3.jpg\" style=\"height:491px; width:1150px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>NĂNG ĐỘNG - MẠNH MẼ - HIỆU QUẢ</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hyundai Tucson được trang bị động cơ c&ugrave;ng hộp số được tối ưu, đem lại hiệu quả vận h&agrave;nh cao nhất c&ugrave;ng khả năng tiết kiệm nhi&ecirc;n liệu cao nhất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Động cơ xăng 2.0 MPi</strong></p>\r\n\r\n<p>Hyundai Tucson được trang bị động cơ xăng phun xăng đa điểm, dung t&iacute;ch 2.0L cho c&ocirc;ng suất tối đa 155 m&atilde; lực tại 6.200 v&ograve;ng/ph&uacute;t v&agrave; momen xoắn 192 Nm tại 4.000 v&ograve;ng/ph&uacute;t. Kết hợp c&ugrave;ng với hộp số tự động 6 cấp khối động cơ của Tucson đem đến một hiệu suất l&yacute; tưởng, triệt ti&ecirc;u độ ồn v&agrave; tiết kiệm nhi&ecirc;n liệu tối đa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/THEMXE/Tucson/VanHanh/vanhanh1.jpg\" style=\"height:133px; width:306px\" /><br />\r\nHệ thống treo trước<br />\r\nMacpherson gi&uacute;p phản xạ tay l&aacute;i ch&iacute;nh x&aacute;c</p>\r\n\r\n<p><img alt=\"\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/THEMXE/Tucson/VanHanh/vanhanh2.jpg\" style=\"height:133px; width:306px\" /><br />\r\nHệ thống treo sau<br />\r\nLi&ecirc;n kết đa điểm gi&uacute;p th&acirc;n xe ổn định</p>\r\n\r\n<p>AN TO&Agrave;N</p>\r\n\r\n<p><img alt=\"\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/THEMXE/Tucson/Antoan/parallax4.jpg\" style=\"height:491px; width:1150px\" /></p>\r\n\r\n<p>TIỆN NGHI</p>\r\n\r\n<p><img alt=\"\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/THEMXE/Tucson/TienNghi/parallax5.jpg\" style=\"height:491px; width:1150px\" /></p>\r\n\r\n<p><strong>TRANG BỊ TIỆN NGHI CAO CẤP D&Agrave;NH CHO BẠN</strong></p>\r\n\r\n<p>Hyundai Tucson thế hệ ho&agrave;n to&agrave;n mới được trang bị những tiện nghi cao cấp h&agrave;ng đầu cho một chiếc SUV, đem đến cho bạn cảm gi&aacute;c hứng khởi nhất cho mỗi lần cầm l&aacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/THEMXE/Tucson/TienNghi/tiennghi1.jpg\" style=\"height:auto; width:301.328px\" /></p>\r\n\r\n<p><strong>Cảm biến l&ugrave;i hỗ trợ đỗ xe</strong>Hyundai Tucson được trang bị cảm biến l&ugrave;i gi&uacute;p người l&aacute;i dễ d&agrave;ng l&ugrave;i xe, gi&uacute;p cảnh b&aacute;o tr&aacute;nh va chạm xảy ra khi l&ugrave;i hoặc đỗ xe.</p>\r\n\r\n<p><img alt=\"exterior_front_sub1_on\" src=\"http://hyundai-thanhcong.vn/tempfiles/uploads/THEMXE/Tucson/TienNghi/tiennghi2.jpg\" style=\"height:auto; width:301.328px\" /></p>\r\n', '1', '2', '6', '28', 'Hàn Quốc', '925000000');
INSERT INTO `products` VALUES ('30', 'Ford Focus 2017', 'Ford Focus 2017.png', 'Ford Focus mới có thiết kế nhỏ gọn, ưa nhìn và nhiều tính năng lái tiên tiến.', '<p><img alt=\"ford-focus-2015 (49).jpg\" src=\"http://cdn2.autodaily.vn/du-lieu/2016/01/28/ford-focus-2015%20(49).jpg\" style=\"height:361px; width:645px\" /></p>\r\n\r\n<p><strong>Thiết kế</strong></p>\r\n\r\n<p>X&eacute;t về platform th&igrave; Focus mới l&agrave; thế hệ c&oacute; thiết kế đẹp nhất từ trước đến nay, đặc biệt l&agrave; d&ograve;ng hatchback 5 cửa. Với h&igrave;nh d&aacute;ng được nhấn mạnh ở sự bề thế, rộng r&atilde;i v&agrave; hơi hạ trọng t&acirc;m xuống một ch&uacute;t, kết hợp c&ugrave;ng nắp ca-p&ocirc; được đẩy căng gắn kết với mặt lưới tản nhiệt h&igrave;nh thang ngược đặc trưng của Ford, Focus mới tr&ocirc;ng mạnh mẽ v&agrave; cơ bắp hơn tr&ecirc;n đường.</p>\r\n\r\n<p><img alt=\"ford-focus-2015 (6).jpg\" src=\"http://cdn3.autodaily.vn/du-lieu/2016/01/28/ford-focus-2015%20(6).jpg\" style=\"height:361px; width:645px\" /></p>\r\n\r\n<p>X&eacute;t về platform th&igrave; Focus mới l&agrave; thế hệ c&oacute; thiết kế đẹp nhất từ trước đến nay, đặc biệt l&agrave; d&ograve;ng hatchback 5 cửa.</p>\r\n\r\n<p>To&agrave;n bộ cấu tr&uacute;c th&acirc;n xe thể hiện t&iacute;nh kh&iacute; động học r&otilde; r&agrave;ng. Khe hở đều tăm tắp v&agrave; viền m&eacute;p được ho&agrave;n thiện với chất lượng cao. C&aacute;c mảng chi tiết d&agrave;y dặn, chắn chắn v&agrave; đ&oacute;ng vai tr&ograve; lớn trong việc hạn chế tạp &acirc;m lọt v&agrave;o khoang xe.</p>\r\n\r\n<p><strong>Nội thất, tiện nghi</strong></p>\r\n\r\n<p>Với chiến lược &ldquo;One Ford&rdquo;, giờ đ&acirc;y kh&aacute;ch h&agrave;ng mua xe Ford kh&ocirc;ng chỉ đơn thuần sở hữu một chiếc xe thuần tu&yacute; m&agrave; c&ograve;n được hưởng lợi từ những c&ocirc;ng nghệ ti&ecirc;n tiến.</p>\r\n\r\n<p><img alt=\"ford-focus-2015 (22).jpg\" src=\"http://cdn2.autodaily.vn/du-lieu/2016/01/28/ford-focus-2015%20(22).jpg\" style=\"height:361px; width:645px\" /></p>\r\n\r\n<p>Đặc biệt, Focus mới được trang bị c&ocirc;ng nghệ kết nối ti&ecirc;n tiến của Ford, SYNC 2, gi&uacute;p tạo ra c&aacute;ch l&aacute;i xe th&ocirc;ng minh hơn v&agrave; an to&agrave;n hơn. T&agrave;i xế c&oacute; thể điều khiển nhiệt độ trong xe, hệ thống giải tr&iacute; v&agrave; hệ thống định vị v&agrave; kết nối với một điện thoại th&ocirc;ng minh bằng khẩu lệnh. Dĩ nhi&ecirc;n, h&atilde;ng xe Mỹ đ&ograve;i hỏi bạn phải ph&aacute;t &acirc;m tiếng Anh kh&aacute; chuẩn để hệ thống hiểu v&agrave; thực hiện mệnh lệnh.</p>\r\n\r\n<p><a href=\"http://danhgiaxe.autodaily.vn/danh-gia-xe/ford/ford/2015/511/danh-gia-chi-tiet-78963.html#\"><img alt=\"ford-focus-2015 (29).jpg\" src=\"http://cdn2.autodaily.vn/du-lieu/2016/01/28/ford-focus-2015%20(29).jpg\" style=\"height:120px; width:212px\" /> </a><a href=\"http://danhgiaxe.autodaily.vn/danh-gia-xe/ford/ford/2015/511/danh-gia-chi-tiet-78963.html#\"><img alt=\"ford-focus-2015 (34).jpg\" src=\"http://cdn2.autodaily.vn/du-lieu/2016/01/28/ford-focus-2015%20(34).jpg\" style=\"height:120px; width:212px\" /> </a><a href=\"http://danhgiaxe.autodaily.vn/danh-gia-xe/ford/ford/2015/511/danh-gia-chi-tiet-78963.html#\"><img alt=\"ford-focus-2015 (37).jpg\" src=\"http://cdn2.autodaily.vn/du-lieu/2016/01/28/ford-focus-2015%20(37).jpg\" style=\"height:120px; width:212px\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh cảm ứng 8 inch với độ ph&acirc;n giải cao, được đặt ở ch&iacute;nh giữa, dễ d&agrave;ng chạm tới v&agrave; điều khiển nhờ v&agrave;o c&aacute;c g&oacute;c c&acirc;u lệnh được định m&agrave;u kh&aacute;c nhau.Bảng điều khiển trung t&acirc;m tr&ecirc;n Ford Focus mới được giản tiện v&agrave; dễ d&ugrave;ng hơn. Việc thiết kế v&agrave; tổ chức lại c&aacute;c n&uacute;t bấm một c&aacute;ch gọn g&agrave;ng khiến người l&aacute;i c&oacute; thể dễ d&agrave;ng điều khiển mọi t&iacute;nh năng của xe.</p>\r\n\r\n<p>Tiện &iacute;ch của xe c&ograve;n được n&acirc;ng cao bởi hệ thống điều ho&agrave; tự động hai v&ugrave;ng độc lập ở khoang l&aacute;i ph&iacute;a trước, cảm biến gạt mưa tự động, đ&egrave;n pha tự động bật s&aacute;ng khi điều kiện s&aacute;ng kh&ocirc;ng tốt, chức năng khởi động bằng n&uacute;t bấm Start/Stop&hellip;</p>\r\n\r\n<p>Do bị giới hạn k&iacute;ch thước n&ecirc;n h&agrave;ng ghế sau của Focus kh&aacute; hạn chế, nhưng d&ugrave; sao vẫn đủ kh&ocirc;ng gian cho cả 3 người ngồi. So với c&aacute;c đối thủ như Toyota Altis, Honda Civic, Mazda3 th&igrave; Focus kh&ocirc;ng chiếm ưu thế về kh&ocirc;ng gian sử dụng, nhưng lại c&oacute; thế mạnh về việc &aacute;p dụng những c&ocirc;ng nghệ của c&aacute;c d&ograve;ng xe hạng sang. Đ&oacute; cũng l&agrave; thế mạnh của thế hệ Focus ho&agrave;n to&agrave;n mới.</p>\r\n\r\n<p><strong>Động cơ</strong></p>\r\n\r\n<p>Focus mới được trang bị động cơ EcoBoost 1.5L 4 xy-lanh sản sinh c&ocirc;ng suất 132 kW với m&ocirc; men xoắn 240 Nm. Theo c&ocirc;ng bố của Ford, động cơ n&agrave;y c&oacute; c&ocirc;ng suất cao hơn c&ocirc;ng suất của một động cơ 2.0L th&ocirc;ng thường m&agrave; lại tiết kiệm nhi&ecirc;n liệu hơn 6% v&agrave; &iacute;t kh&iacute; thải hơn.</p>\r\n\r\n<p>Phi&ecirc;n bản Focus 1.5L EcoBoost ch&uacute;ng t&ocirc;i đang trải nghiệm được trang bị hộp số tự động thủy lực ho&agrave;n to&agrave;n mới; phi&ecirc;n bản 1.6L c&ograve;n lại được trang bị hộp số tự động PowerShift c&oacute; chế độ chuyển số tay v&agrave; hệ thống l&aacute;i trợ lực điện (EPAS).</p>\r\n\r\n<p><strong>Vận h&agrave;nh</strong></p>\r\n\r\n<p>Tr&ecirc;n cung đường quen thuộc H&agrave; Nội &ndash; Đại lộ Thăng Long &ndash; Vườn Quốc gia Ba V&igrave;, lộ tr&igrave;nh tuy ngắn nhưng ch&uacute;ng t&ocirc;i c&oacute; cơ hội trải nghiệm kh&aacute; đầy đủ những t&iacute;nh năng vận h&agrave;nh v&agrave; sức mạnh của động cơ EcoBoost 1.5L khi l&aacute;i xe đi trong phố, cao tốc v&agrave; cả những cung đường đ&egrave;o dốc.</p>\r\n\r\n<p><img alt=\"ford-focus.JPG\" src=\"http://cdn1.autodaily.vn/du-lieu/2016/01/28/ford-focus.JPG\" style=\"height:361px; width:645px\" /></p>\r\n\r\n<p>&ldquo;Kho&aacute;i&rdquo; nhất l&agrave; thử những t&iacute;nh năng Hỗ trợ đỗ xe tự động. Bạn sẽ chẳng phải bận t&acirc;m khi đưa Focus v&agrave;o chỗ đỗ. Đ&acirc;y l&agrave; hệ thống vốn được x&acirc;y dựng từ t&iacute;nh năng Hỗ trợ đỗ xe song song, nay được tăng cường Hỗ trợ đỗ xe vu&ocirc;ng g&oacute;c. Khi được k&iacute;ch hoạt, hệ thống sẽ qu&eacute;t c&aacute;c chỗ đỗ dọc theo c&aacute;c xe kh&aacute;c. Khi t&igrave;m được chỗ đỗ ph&ugrave; hợp, hệ thống sẽ tự điều khiển v&ocirc;lăng để di chuyển được xe v&agrave;o chỗ đỗ, người l&aacute;i chỉ cần điều khiển việc chuyển số, ch&acirc;n phanh v&agrave; ch&acirc;n ga theo c&aacute;c hiệu lệnh tr&ecirc;n m&agrave;n h&igrave;nh.</p>\r\n\r\n<p>L&aacute;i Focus h&ograve;a m&igrave;nh v&agrave;o những con phố đ&ocirc;ng đ&uacute;c, với những chiếc xe m&aacute;y l&uacute;c n&agrave;o cũng trực tạt đầu xe, bạn cũng cảm thấy y&ecirc;n t&acirc;m hơn nhờ hệ thống cảnh b&aacute;o c&oacute; xe cắt ngang. T&iacute;nh năng n&agrave;y sử dụng ba cảnh b&aacute;o ri&ecirc;ng biệt cho người l&aacute;i khi hệ thống ph&aacute;t hiện ra c&oacute; xe tiến lại gần hoặc từ b&ecirc;n tr&aacute;i, hoặc từ b&ecirc;n phải, hoặc trong khoảng gần hơn 40m.</p>\r\n\r\n<p><img alt=\"ford-focus-2015 (52).jpg\" src=\"http://cdn2.autodaily.vn/du-lieu/2016/01/28/ford-focus-2015%20(52).jpg\" style=\"height:361px; width:645px\" /></p>\r\n\r\n<p>Trong điều kiện l&aacute;i ở đ&ocirc; thị với tốc độ dưới 55 km/h, hệ thống Hỗ trợ phanh chủ động được cải tiến c&oacute; thể gi&uacute;p giảm thiểu c&aacute;c va chạm khi ở tốc độ thấp. Sử dụng cảm biến gắn tr&ecirc;n k&iacute;nh l&aacute;i để ph&aacute;t hiện c&aacute;c xe đứng y&ecirc;n, hoặc di chuyển chậm hoặc phanh bất ngờ ở ph&iacute;a trước, hệ thống sẽ chủ động chuẩn bị lực phanh nếu xe của bạn tiến đến xe ph&iacute;a trước qu&aacute; nhanh. Nếu l&aacute;i xe kh&ocirc;ng c&oacute; phản ứng g&igrave;, hệ thống sẽ giảm v&ograve;ng tua m&aacute;y v&agrave; tự động phanh để giảm thiểu va chạm. Với những &ldquo;t&agrave;i non&rdquo; hay chị em phụ nữ thường xuy&ecirc;n mất tập trung, họ sẽ phải cảm ơn c&ocirc;ng nghệ th&ocirc;ng minh n&agrave;y của Ford.</p>\r\n\r\n<p>Đ&oacute; l&agrave; trong phố. C&ograve;n tr&ecirc;n đường cao tốc Thăng Long lại thấy được một Focus c&oacute; khả năng tăng tốc rất ấn tượng. Khả năng chuyển số của hộp số tự động 6 cấp linh hoạt v&agrave; c&oacute; 2 chế độ hoạt động l&agrave; D (ti&ecirc;u chuẩn) v&agrave; S (thể thao). Nếu như chế độ D mang lại sự &ecirc;m &aacute;i th&igrave; chế độ S mang tới cảm x&uacute;c khi tăng tốc do động cơ hoạt động ở v&ograve;ng tua cao hơn. V&iacute; dụ, tại tốc độ 100 km/h th&igrave; v&ograve;ng tua m&aacute;y ở chế đọ D chỉ l&agrave; 2.000 v&ograve;ng/ph&uacute;t c&ograve;n ở chế độ S l&agrave; 2.500 v&ograve;ng/ph&uacute;t. Thời gian chuyển số của chế độ S cũng nhanh hơn.</p>\r\n\r\n<p>Mặc d&ugrave; c&oacute; khả năng c&aacute;ch &acirc;m tốt nhờ h&agrave;ng loạt cải tiến về kh&iacute; động học, khung xe&hellip; nhưng khi sử dụng chế độ S, &acirc;m thanh của p&ocirc; xe v&agrave; động cơ vẫn đủ để tạo cảm gi&aacute;c phấn kh&iacute;ch cho những lần tăng tốc. Ngo&agrave;i ra, hộp số c&ograve;n c&oacute; chế độ sang số tay nằm ngay mặt b&ecirc;n của cần số. B&ecirc;n cạnh đ&oacute;, khi di chuyển ở tốc độ 100 km/h th&igrave; gi&oacute; v&agrave; tiếng lốp lọt v&agrave;o khoang xe l&agrave; rất &iacute;t. Hệ thống treo của Focus l&agrave; một điểm đ&aacute;ng ch&uacute; &yacute; khi khả năng dập tắt dao động nhanh của giảm chấn rất tốt, mang lại cảm gi&aacute;c &ecirc;m &aacute;i cho người ngồi trong khoang xe.</p>\r\n\r\n<p><img alt=\"ford-focus.JPG\" src=\"http://cdn3.autodaily.vn/du-lieu/2016/03/04/ford-focus.JPG\" style=\"height:361px; width:645px\" /></p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i chọn Vườn Quốc gia Ba V&igrave; l&agrave; điểm đến để trải nghiệm khả năng leo dốc v&agrave; &ocirc;m cua đường đ&egrave;o của Focus mới.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i chọn Vườn Quốc gia Ba V&igrave; l&agrave; điểm đến để trải nghiệm khả năng leo dốc v&agrave; &ocirc;m cua đường đ&egrave;o của Focus mới. Tại c&aacute;c kh&uacute;c cua kh&ocirc;ng qu&aacute; ngặt, xe c&oacute; thể b&aacute;m cua ở tốc độ 60km/h vẫn tốt, khi đ&oacute; giới hạn của sự trượt được cảnh b&aacute;o bởi tiếng ken k&eacute;t tr&ecirc;n đường, v&agrave; nếu như vượt ngưỡng đ&oacute; th&igrave; đ&atilde; c&oacute; hệ thống c&acirc;n bằng ESP can thiệp v&agrave; phanh triệt để nhằm bảo đảm t&iacute;nh an to&agrave;n.</p>\r\n\r\n<p>Những tay l&aacute;i &ldquo;thạo&rdquo; cũng c&oacute; thể chuyển sang d&ugrave;ng số tay bằng c&aacute;c n&uacute;t gạt +/- ngay tr&ecirc;n v&ocirc;lăng ở cung đường ngắn nhưng ngoằn ngo&egrave;o v&agrave; dốc cao như ở Ba V&igrave; n&agrave;y. Khi l&ecirc;n hay xuống dốc, h&atilde;y hạ thấp cấp số bằng c&aacute;ch bấm v&agrave;o n&uacute;t (-). Động cơ sẽ bứt mạnh khi l&ecirc;n v&agrave; gh&igrave; lại khi xuống tr&aacute;nh việc phải d&ugrave;ng qu&aacute; nhiều đến phanh.</p>\r\n\r\n<p>Hệ thống an to&agrave;n tr&ecirc;n xe cũng khiến người ngồi trong xe kh&ocirc;ng phải lo nghĩ nhiều. C&aacute;c t&iacute;nh năng an to&agrave;n thụ động bao gồm hệ thống t&uacute;i kh&iacute; d&agrave;nh cho h&agrave;ng ghế trước, t&uacute;i khi b&ecirc;n cho h&agrave;ng ghế sau v&agrave; t&uacute;i kh&iacute; kiểu m&agrave;nh b&ecirc;n h&ocirc;ng đặc biệt cho cả 2 h&agrave;ng ghế. Hệ thống an to&agrave;n chủ động gồm hệ thống Phanh chủ động, hệ thống hỗ trợ phanh khẩn cấp, hỗ trợ khởi h&agrave;nh ngang dốc, hệ thống phanh ABS đi k&egrave;m hệ thống c&acirc;n bằng điện tử.</p>\r\n', '1', '2', '4', '22', 'Hoa Kỳ', '799000000');

-- ----------------------------
-- Table structure for user
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `ID` int(11) NOT NULL AUTO_INCREMENT,
  `Username` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `Password` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `Name` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `Email` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `Phone` varchar(12) COLLATE utf8_unicode_ci DEFAULT NULL,
  `Address` varchar(200) COLLATE utf8_unicode_ci NOT NULL,
  `Sex` varchar(5) COLLATE utf8_unicode_ci DEFAULT NULL,
  `Level` int(11) NOT NULL,
  PRIMARY KEY (`ID`,`Username`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

