INSERT INTO `user` VALUES ('2', 'nakiet', 'f9e5d47633d0012cce9ba1386f83436e', 'Nguyễn Anh Kiệt', 'nakiet1911@gmail.com', '01674678354', 'Trảng Bom Đồng Nai', 'Nam', '0');
INSERT INTO `user` VALUES ('2', 'user', 'e10adc3949ba59abbe56e057f20f883e', 'Nguyễn Thành VIên', 'chatrieng19@gmail.com', '01663718996', 'HCM', 'Nam', '0');
